'use client'

// React Imports
import { useState, useEffect } from 'react'

// MUI Imports
import Card from '@mui/material/Card'
import CardHeader from '@mui/material/CardHeader'
import CardContent from '@mui/material/CardContent'
import Divider from '@mui/material/Divider'
import Button from '@mui/material/Button'
import Typography from '@mui/material/Typography'
import { styled } from '@mui/material/styles'
import type { BoxProps } from '@mui/material/Box'
import IconButton from '@mui/material/IconButton'
import List from '@mui/material/List'
import ListItem from '@mui/material/ListItem'

// Third-party Imports
import { useDropzone } from 'react-dropzone'
import { useForm } from 'react-hook-form'

// Component Imports
import CustomAvatar from '@core/components/mui/Avatar'
import AppReactDropzone from '@/libs/styles/AppReactDropzone'

type FileProp = {
    name: string
    type: string
    size: number
}

// Styled Dropzone Component
const Dropzone = styled(AppReactDropzone)<BoxProps>(({ theme }) => ({
    '& .dropzone': {
        minHeight: 'unset',
        padding: theme.spacing(12),
        [theme.breakpoints.down('sm')]: {
            paddingInline: theme.spacing(5)
        },
        '&+.MuiList-root .MuiListItem-root .file-name': {
            fontWeight: theme.typography.body1.fontWeight
        }
    }
}))

const HistoryPageHero = () => {
    // Local Form
    const { handleSubmit, setValue } = useForm()
    const [files, setFiles] = useState<File[]>([])

    // Load data from LocalStorage on mount
    useEffect(() => {
        // Only loading metadata if we were doing real persistence, 
        // for image-only sections without file restoration, we start blank mostly.
        // But for consistency we can reset.
    }, [])

    const { getRootProps, getInputProps } = useDropzone({
        maxFiles: 1,
        accept: {
            'image/*': ['.png', '.jpg', '.jpeg', '.gif', '.webp']
        },
        onDrop: (acceptedFiles: File[]) => {
            const file = acceptedFiles[0]

            if (file) {
                setFiles([Object.assign(file)])
                setValue('heroImage', file)
            }
        }
    })

    const renderFilePreview = (file: FileProp) => {
        if (file.type.startsWith('image')) {
            return <img width={38} height={38} alt={file.name} src={URL.createObjectURL(file as any)} />
        } else {
            return <i className='ri-file-text-line' />
        }
    }

    const handleRemoveFile = () => {
        setFiles([])
        setValue('heroImage', null)
    }

    const onSubmit = (data: any) => {
        // Save metadata
        localStorage.setItem('history_page_hero_data', JSON.stringify({ saved: true }))
        console.log('History Page Hero Saved:', files)
        alert('History Page Hero Save: Data saved to LocalStorage')
    }

    return (
        <Card>
            <form onSubmit={handleSubmit(onSubmit)}>
                <CardHeader
                    title='History Hero Section'
                    action={
                        <Button variant='contained' type='submit'>
                            Save Hero
                        </Button>
                    }
                />
                <Divider />
                <CardContent>
                    <div>
                        <Typography variant='caption' className='mb-2 block'>Hero Image (Image Only)</Typography>
                        <Dropzone>
                            <div {...getRootProps({ className: 'dropzone' })}>
                                <input {...getInputProps()} />
                                <div className='flex items-center flex-col gap-2 text-center'>
                                    <CustomAvatar variant='rounded' skin='light' color='secondary'>
                                        <i className='ri-upload-2-line' />
                                    </CustomAvatar>
                                    <Typography variant='h6'>Drag and Drop Your Image Here</Typography>
                                    <Typography color='text.disabled'>or</Typography>
                                    <Button variant='outlined' size='small'>
                                        Browse Image
                                    </Button>
                                </div>
                            </div>
                            {files.length > 0 && (
                                <List>
                                    <ListItem className='pis-4 plb-3'>
                                        <div className='file-details'>
                                            <div className='file-preview'>{renderFilePreview(files[0])}</div>
                                            <div>
                                                <Typography className='file-name font-medium' color='text.primary'>
                                                    {files[0].name}
                                                </Typography>
                                                <Typography className='file-size' variant='body2'>
                                                    {(files[0].size / 1024).toFixed(2)} kb
                                                </Typography>
                                            </div>
                                        </div>
                                        <IconButton onClick={handleRemoveFile}>
                                            <i className='ri-close-line text-xl' />
                                        </IconButton>
                                    </ListItem>
                                </List>
                            )}
                        </Dropzone>
                    </div>
                </CardContent>
            </form>
        </Card>
    )
}

export default HistoryPageHero

'use client'

// React Imports
import { useEffect, useState } from 'react'

// MUI Imports
import Card from '@mui/material/Card'
import CardHeader from '@mui/material/CardHeader'
import CardContent from '@mui/material/CardContent'
import Button from '@mui/material/Button'
import Typography from '@mui/material/Typography'
import { styled } from '@mui/material/styles'
import type { BoxProps } from '@mui/material/Box'
import IconButton from '@mui/material/IconButton'
import List from '@mui/material/List'
import ListItem from '@mui/material/ListItem'

// Third-party Imports
import { useForm, Controller } from 'react-hook-form'
import { useDropzone } from 'react-dropzone'

// Component Imports
import TextEditor from '@components/TextEditor'
import CustomAvatar from '@core/components/mui/Avatar'
import AppReactDropzone from '@/libs/styles/AppReactDropzone'

// Styled Dropzone
const Dropzone = styled(AppReactDropzone)<BoxProps>(({ theme }) => ({
    '& .dropzone': {
        minHeight: 'unset',
        padding: theme.spacing(6),
        border: `1px dashed ${theme.palette.divider}`,
        borderRadius: theme.shape.borderRadius,
        textAlign: 'center'
    }
}))

const OurHistorySection = () => {
    // Local Form
    const { control, handleSubmit, reset, setValue } = useForm({
        defaultValues: {
            description: '',
            image: null
        }
    })

    const [files, setFiles] = useState<File[]>([])

    // Load data
    useEffect(() => {
        const savedData = localStorage.getItem('our_history_section_data')

        if (savedData) {
            const parsed = JSON.parse(savedData)

            reset({ description: parsed.description || '', image: null })
        }
    }, [reset])

    const { getRootProps, getInputProps } = useDropzone({
        maxFiles: 1,
        accept: {
            'image/*': ['.png', '.jpg', '.jpeg', '.gif', '.webp']
        },
        onDrop: (acceptedFiles: File[]) => {
            const file = acceptedFiles[0]

            if (file) {
                setFiles([Object.assign(file)])
                setValue('image', file as any)
            }
        }
    })

    const onSubmit = (data: any) => {
        localStorage.setItem('our_history_section_data', JSON.stringify({ description: data.description }))
        console.log('Our History Section Saved:', data)
        alert('Our History Section Saved')
    }

    return (
        <Card>
            <form onSubmit={handleSubmit(onSubmit)}>
                <CardHeader
                    title='Our History Section'
                    action={
                        <Button variant='contained' type='submit'>
                            Save
                        </Button>
                    }
                />
                <CardContent>
                    <div className='flex flex-col gap-6'>
                        <Controller
                            name='description'
                            control={control}
                            render={({ field }) => (
                                <TextEditor
                                    value={field.value}
                                    onChange={field.onChange}
                                    label='Write about the history...'
                                />
                            )}
                        />
                        <div>
                            <Typography variant='caption' className='mb-2 block'>Supporting Image</Typography>
                            <Dropzone>
                                <div {...getRootProps({ className: 'dropzone' })}>
                                    <input {...getInputProps()} />
                                    {files.length > 0 ? (
                                        <div className='flex items-center justify-between'>
                                            <div className='flex items-center'>
                                                <img width={38} height={38} alt={files[0].name} src={URL.createObjectURL(files[0])} className='mr-2' />
                                                <Typography variant='body2'>{files[0].name}</Typography>
                                            </div>
                                            <IconButton onClick={() => { setFiles([]); setValue('image', null) }}>
                                                <i className='ri-close-line' />
                                            </IconButton>
                                        </div>
                                    ) : (
                                        <div className='flex flex-col items-center gap-2'>
                                            <i className='ri-upload-2-line text-xl' />
                                            <Typography variant='caption'>Upload Image</Typography>
                                        </div>
                                    )}
                                </div>
                            </Dropzone>
                        </div>
                    </div>
                </CardContent>
            </form>
        </Card>
    )
}

export default OurHistorySection

'use client'

// React Imports
import { useState, useEffect } from 'react'

// MUI Imports
import Button from '@mui/material/Button'
import Card from '@mui/material/Card'
import CardContent from '@mui/material/CardContent'
import CardHeader from '@mui/material/CardHeader'
import Grid from '@mui/material/Grid2'
import TextField from '@mui/material/TextField'
import Divider from '@mui/material/Divider'
import Typography from '@mui/material/Typography'
import InputAdornment from '@mui/material/InputAdornment'
import IconButton from '@mui/material/IconButton'
import Select from '@mui/material/Select'
import MenuItem from '@mui/material/MenuItem'
import Chip from '@mui/material/Chip'
import OutlinedInput from '@mui/material/OutlinedInput'
import InputLabel from '@mui/material/InputLabel'
import FormControl from '@mui/material/FormControl'
import FormHelperText from '@mui/material/FormHelperText'

// Third-party Imports
import { useForm, Controller, useWatch } from 'react-hook-form'

type HeroSectionData = {
    heroTitle: string
    heroSubtitle: string
    heroImageUrl: string
    ctaText: string
    ctaLink: string
}

type InfoSectionData = {
    infoHeading: string
    infoParagraph: string
}

type RelatedSectionType = 'blogs' | 'services' | 'parts'

type RelatedContentData = {
    sectionTypes: RelatedSectionType[]
    relatedBlogs: string[]
    relatedServices: string[]
    relatedParts: string[]
}

type FormValues = HeroSectionData & InfoSectionData & RelatedContentData

// Mock Data
const MOCK_BLOGS = [
    { id: '1', title: 'Top 10 Car Maintenance Tips' },
    { id: '2', title: 'Understanding Engine Oil' },
    { id: '3', title: 'Guide to Tire Safety' },
    { id: '4', title: 'Winter Driving Essentials' }
]

const MOCK_SERVICES = [
    { id: 's1', title: 'Oil Change Service' },
    { id: 's2', title: 'Tire Rotation' },
    { id: 's3', title: 'Brake Inspection' },
    { id: 's4', title: 'Engine Tuning' }
]

const MOCK_PARTS = [
    { id: 'p1', title: 'Synthetic Motor Oil 5W-30' },
    { id: 'p2', title: 'Air Filter Type A' },
    { id: 'p3', title: 'Ceramic Brake Pads' },
    { id: 'p4', title: 'All-Season Tires' }
]

const EditBlog = () => {
    const {
        control,
        handleSubmit,
        reset,

        setValue,
        formState: { errors }
    } = useForm<FormValues>({
        defaultValues: {
            heroTitle: 'Welcome to Our Blog',
            heroSubtitle: 'Discover the latest news, updates, and stories from our team.',
            heroImageUrl: 'hero-image.jpg',
            ctaText: 'Read Latest Posts',
            ctaLink: '/apps/blog/write',
            infoHeading: 'What We Write About',
            infoParagraph: 'We share insights on technology, lifestyle, and industry trends. Stay tuned for expert articles and in-depth guides.',
            sectionTypes: [],
            relatedBlogs: [],
            relatedServices: [],
            relatedParts: []
        }
    })

    // Watch section types to handle conditioning and side-effects
    const selectedSectionTypes = useWatch({ control, name: 'sectionTypes' }) || []

    // Load saved data from localStorage on mount
    useEffect(() => {
        const savedData = localStorage.getItem('blog-edit-data')

        if (savedData) {
            const parsedData = JSON.parse(savedData)


            // Merge saved data with default structure to ensure new fields exist
            reset({
                heroTitle: parsedData.heroTitle || 'Welcome to Our Blog',
                heroSubtitle: parsedData.heroSubtitle || 'Discover the latest news, updates, and stories from our team.',
                heroImageUrl: parsedData.heroImageUrl || 'hero-image.jpg',
                ctaText: parsedData.ctaText || 'Read Latest Posts',
                ctaLink: parsedData.ctaLink || '/apps/blog/write',
                infoHeading: parsedData.infoHeading || 'What We Write About',
                infoParagraph: parsedData.infoParagraph || 'We share insights on technology, lifestyle, and industry trends. Stay tuned for expert articles and in-depth guides.',
                sectionTypes: parsedData.sectionTypes || [],
                relatedBlogs: parsedData.relatedBlogs || [],
                relatedServices: parsedData.relatedServices || [],
                relatedParts: parsedData.relatedParts || []
            })
        }
    }, [reset])

    const onSubmit = (data: FormValues) => {
        console.log('Submitted Blog Page Data:', data)
        localStorage.setItem('blog-edit-data', JSON.stringify(data))
        alert('Blog Page Updated and Saved!')
    }

    const availableOptions = [
        { value: 'blogs', label: 'Related Blogs' },
        { value: 'services', label: 'Related Services' },
        { value: 'parts', label: 'Related Parts' }
    ]

    return (
        <Grid container spacing={6}>
            <Grid size={{ xs: 12 }}>
                <form onSubmit={handleSubmit(onSubmit)}>
                    <Grid container spacing={6}>
                        {/* Hero Section */}
                        <Grid size={{ xs: 12 }}>
                            <Card>
                                <CardHeader title='Hero Section Editing' />
                                <CardContent>
                                    <Grid container spacing={5}>
                                        <Grid size={{ xs: 12 }}>
                                            <Controller
                                                name='heroTitle'
                                                control={control}
                                                rules={{ required: true }}
                                                render={({ field }) => (
                                                    <TextField
                                                        {...field}
                                                        fullWidth
                                                        label='Title'
                                                        placeholder='Blog Title'
                                                        error={Boolean(errors.heroTitle)}
                                                        helperText={errors.heroTitle && 'Title is required'}
                                                    />
                                                )}
                                            />
                                        </Grid>
                                        <Grid size={{ xs: 12 }}>
                                            <Controller
                                                name='heroSubtitle'
                                                control={control}
                                                render={({ field }) => (
                                                    <TextField
                                                        {...field}
                                                        fullWidth
                                                        label='Subtitle'
                                                        placeholder='Blog Subtitle'
                                                    />
                                                )}
                                            />
                                        </Grid>
                                        <Grid size={{ xs: 12 }}>
                                            <Controller
                                                name='heroImageUrl'
                                                control={control}
                                                render={({ field }) => (
                                                    <div className='flex items-center gap-4'>
                                                        <TextField
                                                            {...field}
                                                            size='small'
                                                            fullWidth
                                                            placeholder='No file chosen'
                                                            variant='outlined'
                                                            label='Hero Image'
                                                            slotProps={{
                                                                input: {
                                                                    readOnly: true,
                                                                    endAdornment: field.value ? (
                                                                        <InputAdornment position='end'>
                                                                            <IconButton size='small' edge='end' onClick={() => field.onChange('')}>
                                                                                <i className='ri-close-line' />
                                                                            </IconButton>
                                                                        </InputAdornment>
                                                                    ) : null
                                                                }
                                                            }}
                                                        />
                                                        <Button component='label' variant='outlined' htmlFor='hero-image-upload' className='min-is-fit'>
                                                            Choose Image
                                                            <input
                                                                hidden
                                                                id='hero-image-upload'
                                                                type='file'
                                                                accept='image/*'
                                                                onChange={(event) => {
                                                                    const { files } = event.target

                                                                    if (files && files.length !== 0) {
                                                                        field.onChange(files[0].name)
                                                                    }
                                                                }}
                                                            />
                                                        </Button>
                                                    </div>
                                                )}
                                            />
                                        </Grid>
                                        <Grid size={{ xs: 12, sm: 6 }}>
                                            <Controller
                                                name='ctaText'
                                                control={control}
                                                render={({ field }) => (
                                                    <TextField
                                                        {...field}
                                                        fullWidth
                                                        label='CTA Button Text'
                                                        placeholder='Read More'
                                                    />
                                                )}
                                            />
                                        </Grid>
                                        <Grid size={{ xs: 12, sm: 6 }}>
                                            <Controller
                                                name='ctaLink'
                                                control={control}
                                                render={({ field }) => (
                                                    <TextField
                                                        {...field}
                                                        fullWidth
                                                        label='CTA Button Link'
                                                        placeholder='/articles/1'
                                                    />
                                                )}
                                            />
                                        </Grid>
                                    </Grid>
                                </CardContent>
                            </Card>
                        </Grid>

                        {/* Info Section */}
                        <Grid size={{ xs: 12 }}>
                            <Card>
                                <CardHeader title='Info Section Editing' />
                                <CardContent>
                                    <Grid container spacing={5}>
                                        <Grid size={{ xs: 12 }}>
                                            <Controller
                                                name='infoHeading'
                                                control={control}
                                                rules={{ required: true }}
                                                render={({ field }) => (
                                                    <TextField
                                                        {...field}
                                                        fullWidth
                                                        label='Heading'
                                                        placeholder='Section Heading'
                                                        error={Boolean(errors.infoHeading)}
                                                        helperText={errors.infoHeading && 'Heading is required'}
                                                    />
                                                )}
                                            />
                                        </Grid>
                                        <Grid size={{ xs: 12 }}>
                                            <Controller
                                                name='infoParagraph'
                                                control={control}
                                                rules={{ required: true }}
                                                render={({ field }) => (
                                                    <TextField
                                                        {...field}
                                                        fullWidth
                                                        multiline
                                                        rows={4}
                                                        label='Paragraph'
                                                        placeholder='Section Content...'
                                                        error={Boolean(errors.infoParagraph)}
                                                        helperText={errors.infoParagraph && 'Content is required'}
                                                    />
                                                )}
                                            />
                                        </Grid>
                                    </Grid>
                                </CardContent>
                            </Card>
                        </Grid>

                        {/* Related Content Configuration */}
                        <Grid size={{ xs: 12 }}>
                            <Card>
                                <CardHeader title='Related Content Configuration' subheader='Choose up to 2 sections to display' />
                                <CardContent>
                                    <Grid container spacing={5}>
                                        <Grid size={{ xs: 12 }}>
                                            <Controller
                                                name='sectionTypes'
                                                control={control}
                                                render={({ field }) => (
                                                    <FormControl fullWidth error={selectedSectionTypes.length > 2}>
                                                        <InputLabel id='section-types-label'>Select Related Sections (Max 2)</InputLabel>
                                                        <Select
                                                            {...field}
                                                            labelId='section-types-label'
                                                            multiple
                                                            input={<OutlinedInput label='Select Related Sections (Max 2)' />}
                                                            renderValue={(selected) => (
                                                                <div className='flex flex-wrap gap-2'>
                                                                    {(selected as string[]).map((value) => (
                                                                        <Chip
                                                                            key={value}
                                                                            label={availableOptions.find(opt => opt.value === value)?.label || value}
                                                                            size='small'
                                                                            onDelete={() => {
                                                                                const newValue = (selected as string[]).filter((item) => item !== value)

                                                                                field.onChange(newValue)
                                                                            }}
                                                                            onMouseDown={(event) => event.stopPropagation()}
                                                                        />
                                                                    ))}
                                                                </div>
                                                            )}
                                                            onChange={(e) => {
                                                                const value = e.target.value as string[]

                                                                if (value.length <= 2) {
                                                                    field.onChange(value)
                                                                }
                                                            }}
                                                        >
                                                            {availableOptions.map((option) => (
                                                                <MenuItem
                                                                    key={option.value}
                                                                    value={option.value}
                                                                    disabled={selectedSectionTypes.length >= 2 && !selectedSectionTypes.includes(option.value as RelatedSectionType)}
                                                                >
                                                                    {option.label}
                                                                </MenuItem>
                                                            ))}
                                                        </Select>
                                                        {selectedSectionTypes.length >= 2 && (
                                                            <FormHelperText>Maximum 2 sections selected</FormHelperText>
                                                        )}
                                                    </FormControl>
                                                )}
                                            />
                                        </Grid>

                                        {selectedSectionTypes.includes('blogs') && (
                                            <Grid size={{ xs: 12 }}>
                                                <Controller
                                                    name='relatedBlogs'
                                                    control={control}
                                                    render={({ field }) => (
                                                        <FormControl fullWidth>
                                                            <InputLabel id='related-blogs-select-label'>Select Related Blogs</InputLabel>
                                                            <Select
                                                                {...field}
                                                                labelId='related-blogs-select-label'
                                                                multiple
                                                                input={<OutlinedInput label='Select Related Blogs' />}
                                                                renderValue={(selected) => (
                                                                    <div className='flex flex-wrap gap-2'>
                                                                        {(selected as string[]).map((value) => (
                                                                            <Chip
                                                                                key={value}
                                                                                label={MOCK_BLOGS.find(b => b.id === value)?.title || value}
                                                                                size='small'
                                                                                onDelete={() => {
                                                                                    const newValue = (selected as string[]).filter((item) => item !== value)

                                                                                    field.onChange(newValue)
                                                                                }}
                                                                                onMouseDown={(event) => event.stopPropagation()}
                                                                            />
                                                                        ))}
                                                                    </div>
                                                                )}
                                                            >
                                                                {MOCK_BLOGS.map((item) => (
                                                                    <MenuItem key={item.id} value={item.id}>{item.title}</MenuItem>
                                                                ))}
                                                            </Select>
                                                        </FormControl>
                                                    )}
                                                />
                                            </Grid>
                                        )}

                                        {selectedSectionTypes.includes('services') && (
                                            <Grid size={{ xs: 12 }}>
                                                <Controller
                                                    name='relatedServices'
                                                    control={control}
                                                    render={({ field }) => (
                                                        <FormControl fullWidth>
                                                            <InputLabel id='related-services-select-label'>Select Related Services</InputLabel>
                                                            <Select
                                                                {...field}
                                                                labelId='related-services-select-label'
                                                                multiple
                                                                input={<OutlinedInput label='Select Related Services' />}
                                                                renderValue={(selected) => (
                                                                    <div className='flex flex-wrap gap-2'>
                                                                        {(selected as string[]).map((value) => (
                                                                            <Chip
                                                                                key={value}
                                                                                label={MOCK_SERVICES.find(s => s.id === value)?.title || value}
                                                                                size='small'
                                                                                onDelete={() => {
                                                                                    const newValue = (selected as string[]).filter((item) => item !== value)

                                                                                    field.onChange(newValue)
                                                                                }}
                                                                                onMouseDown={(event) => event.stopPropagation()}
                                                                            />
                                                                        ))}
                                                                    </div>
                                                                )}
                                                            >
                                                                {MOCK_SERVICES.map((item) => (
                                                                    <MenuItem key={item.id} value={item.id}>{item.title}</MenuItem>
                                                                ))}
                                                            </Select>
                                                        </FormControl>
                                                    )}
                                                />
                                            </Grid>
                                        )}

                                        {selectedSectionTypes.includes('parts') && (
                                            <Grid size={{ xs: 12 }}>
                                                <Controller
                                                    name='relatedParts'
                                                    control={control}
                                                    render={({ field }) => (
                                                        <FormControl fullWidth>
                                                            <InputLabel id='related-parts-select-label'>Select Related Parts</InputLabel>
                                                            <Select
                                                                {...field}
                                                                labelId='related-parts-select-label'
                                                                multiple
                                                                input={<OutlinedInput label='Select Related Parts' />}
                                                                renderValue={(selected) => (
                                                                    <div className='flex flex-wrap gap-2'>
                                                                        {(selected as string[]).map((value) => (
                                                                            <Chip
                                                                                key={value}
                                                                                label={MOCK_PARTS.find(p => p.id === value)?.title || value}
                                                                                size='small'
                                                                                onDelete={() => {
                                                                                    const newValue = (selected as string[]).filter((item) => item !== value)

                                                                                    field.onChange(newValue)
                                                                                }}
                                                                                onMouseDown={(event) => event.stopPropagation()}
                                                                            />
                                                                        ))}
                                                                    </div>
                                                                )}
                                                            >
                                                                {MOCK_PARTS.map((item) => (
                                                                    <MenuItem key={item.id} value={item.id}>{item.title}</MenuItem>
                                                                ))}
                                                            </Select>
                                                        </FormControl>
                                                    )}
                                                />
                                            </Grid>
                                        )}
                                    </Grid>
                                </CardContent>
                            </Card>
                        </Grid>

                        <Grid size={{ xs: 12 }} className='flex justify-end'>
                            <Button variant='contained' type='submit'>
                                Save Changes
                            </Button>
                        </Grid>
                    </Grid>
                </form>
            </Grid>
        </Grid>
    )
}

export default EditBlog

// React Imports
import { useState, useEffect } from 'react'

// MUI Imports
import Drawer from '@mui/material/Drawer'
import Button from '@mui/material/Button'
import TextField from '@mui/material/TextField'
import MenuItem from '@mui/material/MenuItem'
import Select from '@mui/material/Select'
import InputLabel from '@mui/material/InputLabel'
import FormControl from '@mui/material/FormControl'
import Typography from '@mui/material/Typography'
import IconButton from '@mui/material/IconButton'
import InputAdornment from '@mui/material/InputAdornment'
import Divider from '@mui/material/Divider'
import Box from '@mui/material/Box'
import Card from '@mui/material/Card'
import CardContent from '@mui/material/CardContent'
import Grid from '@mui/material/Grid2'
import Chip from '@mui/material/Chip'
import OutlinedInput from '@mui/material/OutlinedInput'

// Third-party Imports
import { useForm, useFieldArray, Controller } from 'react-hook-form'
import { useEditor, EditorContent } from '@tiptap/react'
import { StarterKit } from '@tiptap/starter-kit'
import { Underline } from '@tiptap/extension-underline'
import { Placeholder } from '@tiptap/extension-placeholder'
import { TextAlign } from '@tiptap/extension-text-align'

// Local Imports
import EditorToolbar from '../write/EditorToolbar'
import '@/libs/styles/tiptapEditor.css'

type Section = {
    title: string
    content: string
    imageUrl: string
}

type FormValues = {
    category: string
    blogTitle: string
    mainImage: string
    relatedBlogs: string[]
    sections: Section[]
}

type Category = {
    id: string
    title: string
}

type BlogPost = {
    id: string
    blogTitle: string
}

type Props = {
    open: boolean
    handleClose: () => void
    blogData: any
    onUpdate: () => void
}

const TiptapEditor = ({ value, onChange }: { value: string; onChange: (content: string) => void }) => {
    const editor = useEditor({
        extensions: [
            StarterKit,
            Placeholder.configure({
                placeholder: 'Write section content here...'
            }),
            TextAlign.configure({
                types: ['heading', 'paragraph']
            }),
            Underline
        ],
        content: value,
        onUpdate: ({ editor }) => {
            onChange(editor.getHTML())
        },
        immediatelyRender: false
    })

    return (
        <Card className='p-0 border shadow-none'>
            <CardContent className='p-0'>
                <EditorToolbar editor={editor} />
                <Divider className='mli-5' />
                <EditorContent editor={editor} className='bs-[135px] overflow-y-auto flex ' />
            </CardContent>
        </Card>
    )
}

const EditBlogDrawer = ({ open, handleClose, blogData, onUpdate }: Props) => {
    const [categories, setCategories] = useState<Category[]>([])
    const [allBlogs, setAllBlogs] = useState<BlogPost[]>([])

    const {
        control,
        handleSubmit,
        reset,
        setValue,
        formState: { errors }
    } = useForm<FormValues>({
        defaultValues: {
            category: '',
            blogTitle: '',
            mainImage: '',
            relatedBlogs: [],
            sections: [{ title: '', content: '', imageUrl: '' }]
        }
    })

    const { fields, append, remove } = useFieldArray({
        control,
        name: 'sections'
    })

    useEffect(() => {
        // Load Categories
        const savedCategories = localStorage.getItem('blog-categories')

        if (savedCategories) {
            setCategories(JSON.parse(savedCategories))
        }

        const savedPosts = JSON.parse(localStorage.getItem('blog-posts') || '[]')

        setAllBlogs(savedPosts)
    }, [])

    useEffect(() => {
        if (open && blogData) {
            reset({
                category: blogData.category || '',
                blogTitle: blogData.blogTitle || '',
                mainImage: blogData.mainImage || '',
                relatedBlogs: blogData.relatedBlogs || [],
                sections: blogData.sections || [{ title: '', content: '', imageUrl: '' }]
            })
        }
    }, [open, blogData, reset])

    const onSubmit = (data: FormValues) => {
        const savedPosts = JSON.parse(localStorage.getItem('blog-posts') || '[]')
        const timestamp = new Date().toISOString()

        const newPosts = savedPosts.map((post: any) =>
            post.id === blogData.id ? { ...post, ...data, updatedAt: timestamp } : post
        )

        localStorage.setItem('blog-posts', JSON.stringify(newPosts))
        handleClose()
        onUpdate()
    }

    return (
        <Drawer
            open={open}
            anchor='right'
            variant='temporary'
            onClose={handleClose}
            ModalProps={{ keepMounted: true }}
            sx={{ '& .MuiDrawer-paper': { width: { xs: 300, sm: 600 } } }}
        >
            <div className='flex items-center justify-between pli-5 plb-4'>
                <Typography variant='h5'>Edit Blog</Typography>
                <IconButton size='small' onClick={handleClose}>
                    <i className='ri-close-line text-2xl' />
                </IconButton>
            </div>
            <Divider />
            <div className='p-5 overflow-y-auto flex-1'>
                <form onSubmit={handleSubmit(onSubmit)} className='flex flex-col gap-5'>
                    <Controller
                        name='category'
                        control={control}
                        rules={{ required: true }}
                        render={({ field }) => (
                            <FormControl fullWidth error={Boolean(errors.category)}>
                                <InputLabel id='edit-category-select'>Category</InputLabel>
                                <Select
                                    {...field}
                                    labelId='edit-category-select'
                                    label='Category'
                                >
                                    {categories.map((cat) => (
                                        <MenuItem key={cat.id} value={cat.title}>
                                            {cat.title}
                                        </MenuItem>
                                    ))}
                                </Select>
                            </FormControl>
                        )}
                    />
                    <Controller
                        name='blogTitle'
                        control={control}
                        rules={{ required: true }}
                        render={({ field }) => (
                            <TextField
                                {...field}
                                fullWidth
                                label='Blog Title'
                                error={Boolean(errors.blogTitle)}
                                helperText={errors.blogTitle && 'Title is required'}
                            />
                        )}
                    />

                    <Controller
                        name='relatedBlogs'
                        control={control}
                        render={({ field }) => (
                            <FormControl fullWidth>
                                <InputLabel id='edit-related-blogs-label'>Related Blogs</InputLabel>
                                <Select
                                    {...field}
                                    labelId='edit-related-blogs-label'
                                    multiple
                                    input={<OutlinedInput label='Related Blogs' />}
                                    renderValue={(selected) => (
                                        <div className='flex flex-wrap gap-2'>
                                            {(selected as string[]).map((value) => {
                                                const blog = allBlogs.find((b: any) => b.id === value)


return <Chip key={value} label={blog?.blogTitle || value} size='small' />
                                            })}
                                        </div>
                                    )}
                                >
                                    {allBlogs.map((blog: any) => (
                                        (blogData && blog.id !== blogData.id) && (
                                            <MenuItem key={blog.id} value={blog.id}>
                                                {blog.blogTitle}
                                            </MenuItem>
                                        )
                                    ))}
                                </Select>
                            </FormControl>
                        )}
                    />

                    {/* Sections */}
                    <div className='flex items-center justify-between'>
                        <Typography variant='h6'>Sections</Typography>
                        <Button size='small' variant='outlined' onClick={() => append({ title: '', content: '', imageUrl: '' })}>
                            Add Section
                        </Button>
                    </div>

                    {fields.map((field, index) => (
                        <Card key={field.id} className='border'>
                            <CardContent className='flex flex-col gap-4'>
                                <div className='flex justify-between items-center'>
                                    <Typography variant='subtitle1'>Section {index + 1}</Typography>
                                    <IconButton onClick={() => remove(index)} color='error' size='small'>
                                        <i className='ri-delete-bin-line' />
                                    </IconButton>
                                </div>
                                <Controller
                                    name={`sections.${index}.title`}
                                    control={control}
                                    rules={{ required: true }}
                                    render={({ field }) => (
                                        <TextField
                                            {...field}
                                            fullWidth
                                            size='small'
                                            label='Section Title'
                                        />
                                    )}
                                />
                                <Controller
                                    name={`sections.${index}.content`}
                                    control={control}
                                    render={({ field }) => (
                                        <TiptapEditor value={field.value} onChange={field.onChange} />
                                    )}
                                />
                            </CardContent>
                        </Card>
                    ))}

                    <div className='flex items-center gap-4'>
                        <Button variant='contained' type='submit' fullWidth>
                            Update
                        </Button>
                        <Button variant='outlined' color='secondary' fullWidth onClick={handleClose}>
                            Cancel
                        </Button>
                    </div>
                </form>
            </div>
        </Drawer>
    )
}

export default EditBlogDrawer

'use client'

// MUI Imports
import Divider from '@mui/material/Divider'
import Grid from '@mui/material/Grid2'
import Card from '@mui/material/Card'
import CardHeader from '@mui/material/CardHeader'
import CardContent from '@mui/material/CardContent'
import TextField from '@mui/material/TextField'
import Typography from '@mui/material/Typography'
import InputLabel from '@mui/material/InputLabel'
import MenuItem from '@mui/material/MenuItem'
import FormControl from '@mui/material/FormControl'
import Select from '@mui/material/Select'
import Button from '@mui/material/Button'
import IconButton from '@mui/material/IconButton'

// Third-party Imports
import classnames from 'classnames'
import { useEditor, EditorContent } from '@tiptap/react'
import { StarterKit } from '@tiptap/starter-kit'
import { Underline } from '@tiptap/extension-underline'
import { Placeholder } from '@tiptap/extension-placeholder'
import { TextAlign } from '@tiptap/extension-text-align'
import type { Editor } from '@tiptap/core'

// Components Imports
import CustomIconButton from '@core/components/mui/IconButton'

// Style Imports
import '@/libs/styles/tiptapEditor.css'

const EditorToolbar = ({ editor }: { editor: Editor | null }) => {
  if (!editor) {
    return null
  }

  return (
    <div className='flex flex-wrap gap-x-3 gap-y-1 pbs-5 pbe-4 pli-5'>
      <CustomIconButton
        {...(editor.isActive('bold') && { color: 'primary' })}
        variant='outlined'
        size='small'
        onClick={() => editor.chain().focus().toggleBold().run()}
      >
        <i className={classnames('ri-bold', { 'text-textSecondary': !editor.isActive('bold') })} />
      </CustomIconButton>
      <CustomIconButton
        {...(editor.isActive('underline') && { color: 'primary' })}
        variant='outlined'
        size='small'
        onClick={() => editor.chain().focus().toggleUnderline().run()}
      >
        <i className={classnames('ri-underline', { 'text-textSecondary': !editor.isActive('underline') })} />
      </CustomIconButton>
      <CustomIconButton
        {...(editor.isActive('italic') && { color: 'primary' })}
        variant='outlined'
        size='small'
        onClick={() => editor.chain().focus().toggleItalic().run()}
      >
        <i className={classnames('ri-italic', { 'text-textSecondary': !editor.isActive('italic') })} />
      </CustomIconButton>
      <CustomIconButton
        {...(editor.isActive('strike') && { color: 'primary' })}
        variant='outlined'
        size='small'
        onClick={() => editor.chain().focus().toggleStrike().run()}
      >
        <i className={classnames('ri-strikethrough', { 'text-textSecondary': !editor.isActive('strike') })} />
      </CustomIconButton>
      <CustomIconButton
        {...(editor.isActive({ textAlign: 'left' }) && { color: 'primary' })}
        variant='outlined'
        size='small'
        onClick={() => editor.chain().focus().setTextAlign('left').run()}
      >
        <i className={classnames('ri-align-left', { 'text-textSecondary': !editor.isActive({ textAlign: 'left' }) })} />
      </CustomIconButton>
      <CustomIconButton
        {...(editor.isActive({ textAlign: 'center' }) && { color: 'primary' })}
        variant='outlined'
        size='small'
        onClick={() => editor.chain().focus().setTextAlign('center').run()}
      >
        <i
          className={classnames('ri-align-center', {
            'text-textSecondary': !editor.isActive({ textAlign: 'center' })
          })}
        />
      </CustomIconButton>
      <CustomIconButton
        {...(editor.isActive({ textAlign: 'right' }) && { color: 'primary' })}
        variant='outlined'
        size='small'
        onClick={() => editor.chain().focus().setTextAlign('right').run()}
      >
        <i
          className={classnames('ri-align-right', {
            'text-textSecondary': !editor.isActive({ textAlign: 'right' })
          })}
        />
      </CustomIconButton>
      <CustomIconButton
        {...(editor.isActive({ textAlign: 'justify' }) && { color: 'primary' })}
        variant='outlined'
        size='small'
        onClick={() => editor.chain().focus().setTextAlign('justify').run()}
      >
        <i
          className={classnames('ri-align-justify', {
            'text-textSecondary': !editor.isActive({ textAlign: 'justify' })
          })}
        />
      </CustomIconButton>
    </div>
  )
}

// React Imports
import { useEffect, useState } from 'react'

import type { CareerType } from '@/types/apps/ecommerceTypes'

import { useRouter } from 'next/navigation'

const ProductInformation = ({ careerData }: { careerData?: CareerType }) => {
  const router = useRouter()
  const [categories, setCategories] = useState<{ id: number; name: string }[]>([])
  const [jobTypes, setJobTypes] = useState<{ id: number; name: string }[]>([])

  const [formData, setFormData] = useState({
    jobTitle: '',
    location: '',
    salary: '',
    category: '',
    jobType: ''
  })

  // New state for PDF file
  const [pdfFile, setPdfFile] = useState<string | null>(null)
  const [pdfFileName, setPdfFileName] = useState<string>('')

  const loadOptions = () => {
    const savedCategories = localStorage.getItem('career-categories')
    const savedJobTypes = localStorage.getItem('career-job-types')

    if (savedCategories) {
      setCategories(JSON.parse(savedCategories))
    }

    if (savedJobTypes) {
      setJobTypes(JSON.parse(savedJobTypes))
    }
  }

  useEffect(() => {
    loadOptions()
  }, [])

  useEffect(() => {
    if (careerData) {
      setFormData({
        jobTitle: careerData.jobTitle,
        location: careerData.location,
        salary: careerData.salary,
        category: careerData.category,
        jobType: careerData.jobType
      })

      // If editing, we might want to load the PDF if it was saved (assuming careerData has it)
      // For now, we'll start with empty PDF on edit unless we update the type
    }
  }, [careerData])

  const editor = useEditor({
    extensions: [
      StarterKit,
      Placeholder.configure({
        placeholder: 'Write something here...'
      }),
      TextAlign.configure({
        types: ['heading', 'paragraph']
      }),
      Underline
    ],
    immediatelyRender: false,
    content: `
      <p>
        Keep your account secure with authentication step.
      </p>
    `
  })

  // Handle file upload
  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0]

    if (file) {
      if (file.type !== 'application/pdf') {
        alert('Please upload a PDF file.')

return
      }

      setPdfFileName(file.name)
      const reader = new FileReader()

      reader.onloadend = () => {
        setPdfFile(reader.result as string)
      }

      reader.readAsDataURL(file)
    }
  }

  // Handle Save
  const handleSave = () => {
    if (!formData.jobTitle) {
      alert('Job Title is required')

return
    }

    const newJob = {
      id: careerData?.id || Date.now().toString(), // Use existing ID if editing, else generate new
      ...formData,
      status: 'Active', // Default status
      pdfFile: pdfFile, // Save PDF base64 string
      pdfFileName: pdfFileName
    }

    // Get existing jobs
    const existingJobs = JSON.parse(localStorage.getItem('career-list') || '[]')

    let updatedJobs

    if (careerData?.id) {
      // Update existing
      updatedJobs = existingJobs.map((job: any) => job.id === careerData.id ? newJob : job)
    } else {
      // Add new
      updatedJobs = [...existingJobs, newJob]
    }

    localStorage.setItem('career-list', JSON.stringify(updatedJobs))
    alert('Job Saved Successfully!')
    router.push('/apps/career/list')
  }

  return (
    <Card>
      <CardHeader title='Job Opening Information' action={
        <Button variant='contained' onClick={handleSave}>
          Save Job
        </Button>
      } />
      <CardContent>
        <Grid container spacing={5} className='mbe-5'>
          <Grid size={{ xs: 12 }}>
            <TextField
              fullWidth
              label='Job Title'
              placeholder='Job Title'
              value={formData.jobTitle}
              onChange={e => setFormData({ ...formData, jobTitle: e.target.value })}
            />
          </Grid>
          <Grid size={{ xs: 12, sm: 6 }}>
            <TextField
              fullWidth
              label='Location'
              placeholder='Location'
              value={formData.location}
              onChange={e => setFormData({ ...formData, location: e.target.value })}
            />
          </Grid>
          <Grid size={{ xs: 12, sm: 6 }}>
            <TextField
              fullWidth
              label='Salary'
              placeholder='Salary'
              value={formData.salary}
              onChange={e => setFormData({ ...formData, salary: e.target.value })}
            />
          </Grid>
          <Grid size={{ xs: 12, sm: 6 }}>
            <FormControl fullWidth>
              <InputLabel id='category-select-label'>Category</InputLabel>
              <Select
                labelId='category-select-label'
                label='Category'
                value={formData.category}
                onChange={e => setFormData({ ...formData, category: e.target.value })}
                onOpen={loadOptions}
              >
                {categories.map((cat) => (
                  <MenuItem key={cat.id} value={cat.name}>{cat.name}</MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
          <Grid size={{ xs: 12, sm: 6 }}>
            <FormControl fullWidth>
              <InputLabel id='job-type-select-label'>Job Type</InputLabel>
              <Select
                labelId='job-type-select-label'
                label='Job Type'
                value={formData.jobType}
                onChange={e => setFormData({ ...formData, jobType: e.target.value })}
                onOpen={loadOptions}
              >
                {jobTypes.map((type) => (
                  <MenuItem key={type.id} value={type.name}>{type.name}</MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>

          {/* PDF Upload Section */}
          <Grid size={{ xs: 12 }}>
            <Typography className='mbe-2' variant='subtitle1'>Job Description (PDF)</Typography>
            <div className='flex items-center gap-4'>
              <Button component='label' variant='outlined' startIcon={<i className='ri-upload-2-line' />}>
                Upload PDF
                <input type='file' hidden accept='application/pdf' onChange={handleFileChange} />
              </Button>
              {pdfFileName && (
                <div className='flex items-center gap-2'>
                  <Typography variant='body2'>{pdfFileName}</Typography>
                  <IconButton size='small' color='error' onClick={() => { setPdfFile(null); setPdfFileName('') }}>
                    <i className='ri-close-line' />
                  </IconButton>
                </div>
              )}
            </div>
            {careerData && (careerData as any).pdfFile && !pdfFileName && (
              <Typography variant='caption' color='textSecondary' className='mt-1'>
                Existing PDF available. Upload new to replace.
              </Typography>
            )}
          </Grid>


        </Grid>
        <Typography className='mbe-1'>Description</Typography>
        <Card className='p-0 border shadow-none'>
          <CardContent className='p-0'>
            <EditorToolbar editor={editor} />
            <Divider className='mli-5' />
            <EditorContent editor={editor} className='bs-[135px] overflow-y-auto flex ' />
          </CardContent>
        </Card>
      </CardContent>
    </Card>
  )
}

export default ProductInformation

'use client'

// React Imports
import { useState, useEffect } from 'react'

// MUI Imports
import Card from '@mui/material/Card'
import CardHeader from '@mui/material/CardHeader'
import CardContent from '@mui/material/CardContent'
import Grid from '@mui/material/Grid2'
import Select from '@mui/material/Select'
import MenuItem from '@mui/material/MenuItem'
import Chip from '@mui/material/Chip'
import OutlinedInput from '@mui/material/OutlinedInput'
import InputLabel from '@mui/material/InputLabel'
import FormControl from '@mui/material/FormControl'
import FormHelperText from '@mui/material/FormHelperText'
import Button from '@mui/material/Button'

// Third-party Imports
import { useForm, Controller, useWatch } from 'react-hook-form'

type RelatedSectionType = 'blogs' | 'services' | 'parts' | 'projects'

type RelatedContentData = {
    sectionTypes: RelatedSectionType[]
    relatedBlogs: string[]
    relatedServices: string[]
    relatedParts: string[]
    relatedProjects: string[]
}

// Mock Data
const MOCK_BLOGS = [
    { id: '1', title: 'Top 10 Career Tips' },
    { id: '2', title: 'Resume Building Guide' },
    { id: '3', title: 'Interview Preparation' },
    { id: '4', title: 'Workplace Etiquette' }
]

const MOCK_SERVICES = [
    { id: 's1', title: 'Resume Review' },
    { id: 's2', title: 'Career Counseling' },
    { id: 's3', title: 'Mock Interview' },
    { id: 's4', title: 'LinkedIn Profile Optimization' }
]

const MOCK_PARTS = [
    { id: 'p1', title: 'Office Equipment' },
    { id: 'p2', title: 'Software Licenses' },
    { id: 'p3', title: 'Training Materials' },
    { id: 'p4', title: 'IT Support' }
]

const MOCK_PROJECTS = [
    { id: 'pr1', title: 'Internal Tool Development' },
    { id: 'pr2', title: 'Client Website Redesign' },
    { id: 'pr3', title: 'Mobile App Launch' },
    { id: 'pr4', title: 'Data Analytics Dashboard' }
]

const CareerRelated = ({ careerData, id }: { careerData?: any; id?: string }) => {
    const {
        control,
        handleSubmit,
        reset,
        watch,
        setValue,
        formState: { errors }
    } = useForm<RelatedContentData>({
        defaultValues: {
            sectionTypes: [],
            relatedBlogs: [],
            relatedServices: [],
            relatedParts: [],
            relatedProjects: []
        }
    })

    // Watch section types to handle conditioning and side-effects
    const selectedSectionTypes = useWatch({ control, name: 'sectionTypes' }) || []

    // Load saved data or props on mount
    useEffect(() => {
        const storageKey = `career-related-${id || 'new'}`
        const savedData = localStorage.getItem(storageKey)

        if (savedData) {
            reset(JSON.parse(savedData))
        } else if (careerData) {
            reset({
                sectionTypes: careerData.sectionTypes || [],
                relatedBlogs: careerData.relatedBlogs || [],
                relatedServices: careerData.relatedServices || [],
                relatedParts: careerData.relatedParts || [],
                relatedProjects: careerData.relatedProjects || []
            })
        }
    }, [careerData, reset, id])

    const onSubmit = (data: RelatedContentData) => {
        const storageKey = `career-related-${id || 'new'}`

        localStorage.setItem(storageKey, JSON.stringify(data))
        console.log('Submitted Career Related Content:', data)
        alert('Career Related Content Saved!')
    }

    const availableOptions = [
        { value: 'blogs', label: 'Related Blogs' },
        { value: 'services', label: 'Related Services' },
        { value: 'parts', label: 'Related Parts' },
        { value: 'projects', label: 'Related Projects' }
    ]

    return (
        <Card>
            <CardHeader title='Related Content Configuration' subheader='Choose up to 2 sections to display' />
            <CardContent>
                <form onSubmit={handleSubmit(onSubmit)}>
                    <Grid container spacing={5}>
                        <Grid size={{ xs: 12 }}>
                            <Controller
                                name='sectionTypes'
                                control={control}
                                render={({ field }) => (
                                    <FormControl fullWidth error={selectedSectionTypes.length > 2}>
                                        <InputLabel id='career-section-types-label'>Select Related Sections (Max 2)</InputLabel>
                                        <Select
                                            {...field}
                                            labelId='career-section-types-label'
                                            multiple
                                            input={<OutlinedInput label='Select Related Sections (Max 2)' />}
                                            renderValue={(selected) => (
                                                <div className='flex flex-wrap gap-2'>
                                                    {(selected as string[]).map((value) => (
                                                        <Chip
                                                            key={value}
                                                            label={availableOptions.find(opt => opt.value === value)?.label || value}
                                                            size='small'
                                                            onDelete={() => {
                                                                const newValue = (selected as string[]).filter((item) => item !== value)

                                                                field.onChange(newValue)
                                                            }}
                                                            onMouseDown={(event) => {
                                                                event.stopPropagation()
                                                            }}
                                                        />
                                                    ))}
                                                </div>
                                            )}
                                            onChange={(e) => {
                                                const value = e.target.value as string[]

                                                if (value.length <= 2) {
                                                    field.onChange(value)
                                                }
                                            }}
                                        >
                                            {availableOptions.map((option) => (
                                                <MenuItem
                                                    key={option.value}
                                                    value={option.value}
                                                    disabled={selectedSectionTypes.length >= 2 && !selectedSectionTypes.includes(option.value as RelatedSectionType)}
                                                >
                                                    {option.label}
                                                </MenuItem>
                                            ))}
                                        </Select>
