  Active: { title: 'Active', color: 'success' },
  Inactive: { title: 'Inactive', color: 'secondary' }
}

// Column Definitions
const columnHelper = createColumnHelper<CareerWithActionsType>()

const CareerListTable = ({ careerData }: { careerData?: CareerType[] }) => {
  // States
  const [rowSelection, setRowSelection] = useState({})
  const [data, setData] = useState<CareerType[]>(careerData || [])
  const [filteredData, setFilteredData] = useState<CareerType[]>(data)
  const [globalFilter, setGlobalFilter] = useState('')

  // Hooks
  const { lang: locale } = useParams()

  useEffect(() => {
    const localData = localStorage.getItem('career-list')

    if (localData) {
      try {
        const parsedLocalData = JSON.parse(localData)

        // Combine prop data and local data, avoiding duplicates if any logic exists (here simple concat or override)
        // Ideally, we might want to prioritize local data or just show local data if it's the source of truth for this demo
        // Let's prepend local data to show new additions at the top
        const combinedData = [...parsedLocalData, ...(careerData || [])]

        // Remove duplicates based on ID if necessary (assuming unique IDs)
        const uniqueData = Array.from(new Map(combinedData.map(item => [item.id, item])).values())

        setData(uniqueData)
        setFilteredData(uniqueData)
      } catch (e) {
        console.error("Failed to parse local career data", e)
      }
    } else {
      setData(careerData || [])
      setFilteredData(careerData || [])
    }
  }, [careerData])

  const columns = useMemo<ColumnDef<CareerWithActionsType, any>[]>(
    () => [

      columnHelper.display({
        id: 'serialNumber',
        header: '#',
        cell: props => <Typography>{props.row.index + 1}</Typography>
      }),
      columnHelper.accessor('jobTitle', {
        header: 'Job Name',
        cell: ({ row }) => (
          <div className='flex items-center gap-3'>
            <div className='flex flex-col'>
              <Typography className='font-medium' color='text.primary'>
                {row.original.jobTitle}
              </Typography>
            </div>
          </div>
        )
      }),
      columnHelper.accessor('location', {
        header: 'Location',
        cell: ({ row }) => <Typography>{row.original.location}</Typography>
      }),
      columnHelper.accessor('salary', {
        header: 'Salary',
        cell: ({ row }) => <Typography>{row.original.salary}</Typography>
      }),
      columnHelper.accessor('category', {
        header: 'Category',
        cell: ({ row }) => (
          <div className='flex items-center gap-3'>
            <CustomAvatar skin='light' color={careerCategoryObj[row.original.category]?.color} size={30}>
              <i className={classnames(careerCategoryObj[row.original.category]?.icon, 'text-lg')} />
            </CustomAvatar>
            <Typography color='text.primary'>{row.original.category}</Typography>
          </div>
        )
      }),
      columnHelper.accessor('jobType', {
        header: 'Job Type',
        cell: ({ row }) => <Typography>{row.original.jobType}</Typography>
      }),
      columnHelper.accessor('status', {
        header: 'Status',
        cell: ({ row }) => {
          const status = row.original.status


return (
            <div className='flex items-center gap-3'>
              <Chip
                label={careerStatusObj[status]?.title || status}
                variant='tonal'
                color={careerStatusObj[status]?.color || 'primary'}
                size='small'
              />
              <Switch checked={status === 'Active'} onChange={() => {
                // In a real app we would make an API call here.
                // For now, let's just update local state to reflect the toggle
                const updatedData = data?.map(item =>
                  item.id === row.original.id
                    ? { ...item, status: item.status === 'Active' ? 'Inactive' : 'Active' }
                    : item
                )

                setData(updatedData)

                // Also update local storage if it exists there
                const localData = localStorage.getItem('career-list')

                if (localData) {
                  const parsed = JSON.parse(localData)
                  const updatedLocal = parsed.map((item: any) => item.id === row.original.id ? { ...item, status: item.status === 'Active' ? 'Inactive' : 'Active' } : item)

                  localStorage.setItem('career-list', JSON.stringify(updatedLocal))
                }
              }} />
            </div>
          )
        }
      }),
      columnHelper.accessor('actions', {
        header: 'Actions',
        cell: ({ row }) => (
          <div className='flex items-center'>
            {row.original.pdfFile && (
              <IconButton size='small' color='primary' onClick={() => {
                // Create a link and click it to download
                const link = document.createElement('a');

                link.href = row.original.pdfFile || '';
                link.download = row.original.pdfFileName || 'job-description.pdf';
                document.body.appendChild(link);
                link.click();
                document.body.removeChild(link);
              }} title="Download PDF">
                <i className='ri-file-pdf-line text-[22px]' />
              </IconButton>
            )}
            <IconButton size='small' component={Link} href={getLocalizedUrl(`/apps/career/edit/${row.original.id}`, locale as Locale)}>
              <i className='ri-edit-box-line text-[22px] text-textSecondary' />
            </IconButton>
            <OptionMenu
              iconButtonProps={{ size: 'medium' }}
              iconClassName='text-textSecondary text-[22px]'
              options={[
                {
                  text: 'Delete',
                  icon: 'ri-delete-bin-7-line',
                  menuItemProps: {
                    onClick: () => {
                      const newData = data?.filter(career => career.id !== row.original.id)

                      setData(newData)

                      // Update local storage
                      const localData = localStorage.getItem('career-list')

                      if (localData) {
                        const parsed = JSON.parse(localData)
                        const updatedLocal = parsed.filter((item: any) => item.id !== row.original.id)

                        localStorage.setItem('career-list', JSON.stringify(updatedLocal))
                      }
                    }
                  }
                }
              ]}
            />
          </div>
        ),
        enableSorting: false
      })
    ],
    // eslint-disable-next-line react-hooks/exhaustive-deps
    [data, filteredData]
  )

  const table = useReactTable({
    data: filteredData as CareerType[],
    columns,
    filterFns: {
      fuzzy: fuzzyFilter
    },
    state: {
      rowSelection,
      globalFilter
    },
    initialState: {
      pagination: {
        pageSize: 10
      }
    },
    enableRowSelection: true,
    globalFilterFn: fuzzyFilter,
    onRowSelectionChange: setRowSelection,
    getCoreRowModel: getCoreRowModel(),
    onGlobalFilterChange: setGlobalFilter,
    getFilteredRowModel: getFilteredRowModel(),
    getSortedRowModel: getSortedRowModel(),
    getPaginationRowModel: getPaginationRowModel(),
    getFacetedRowModel: getFacetedRowModel(),
    getFacetedUniqueValues: getFacetedUniqueValues(),
    getFacetedMinMaxValues: getFacetedMinMaxValues()
  })

  return (
    <>
      <Card>
        <CardHeader title='Filters' />
        <TableFilters setData={setFilteredData} careerData={data} />
        <Divider />
        <div className='flex justify-between flex-col items-start sm:flex-row sm:items-center gap-y-4 p-5'>
          <DebouncedInput
            value={globalFilter ?? ''}
            onChange={value => setGlobalFilter(String(value))}
            placeholder='Search Job'
            className='max-sm:is-full'
          />
          <div className='flex items-center max-sm:flex-col gap-4 max-sm:is-full is-auto'>
            <Button
              color='secondary'
              variant='outlined'
              className='max-sm:is-full is-auto'
              startIcon={<i className='ri-upload-2-line' />}
            >
              Export
            </Button>
            <Button
              variant='contained'
              component={Link}
              href={getLocalizedUrl('/apps/career/add', locale as Locale)}
              startIcon={<i className='ri-add-line' />}
              className='max-sm:is-full is-auto'
            >
              Add Job
            </Button>
          </div>
        </div>
        <div className='overflow-x-auto'>
          <table className={tableStyles.table}>
            <thead>
              {table.getHeaderGroups().map(headerGroup => (
                <tr key={headerGroup.id}>
                  {headerGroup.headers.map(header => (
                    <th key={header.id}>
                      {header.isPlaceholder ? null : (
                        <>
                          <div
                            className={classnames({
                              'flex items-center': header.column.getIsSorted(),
                              'cursor-pointer select-none': header.column.getCanSort()
                            })}
                            onClick={header.column.getToggleSortingHandler()}
                          >
                            {flexRender(header.column.columnDef.header, header.getContext())}
                            {{
                              asc: <i className='ri-arrow-up-s-line text-xl' />,
                              desc: <i className='ri-arrow-down-s-line text-xl' />
                            }[header.column.getIsSorted() as 'asc' | 'desc'] ?? null}
                          </div>
                        </>
                      )}
                    </th>
                  ))}
                </tr>
              ))}
            </thead>
            {table.getFilteredRowModel().rows.length === 0 ? (
              <tbody>
                <tr>
                  <td colSpan={table.getVisibleFlatColumns().length} className='text-center'>
                    No data available
                  </td>
                </tr>
              </tbody>
            ) : (
              <tbody>
                {table
                  .getRowModel()
                  .rows.slice(0, table.getState().pagination.pageSize)
                  .map(row => {
                    return (
                      <tr key={row.id} className={classnames({ selected: row.getIsSelected() })}>
                        {row.getVisibleCells().map(cell => (
                          <td key={cell.id}>{flexRender(cell.column.columnDef.cell, cell.getContext())}</td>
                        ))}
                      </tr>
                    )
                  })}
              </tbody>
            )}
          </table>
        </div>
        <TablePagination
          rowsPerPageOptions={[10, 25, 50]}
          component='div'
          className='border-bs'
          count={table.getFilteredRowModel().rows.length}
          rowsPerPage={table.getState().pagination.pageSize}
          page={table.getState().pagination.pageIndex}
          onPageChange={(_, page) => {
            table.setPageIndex(page)
          }}
          onRowsPerPageChange={e => table.setPageSize(Number(e.target.value))}
        />
      </Card>
    </>
  )
}

export default CareerListTable

'use client'

// React Imports
import { useState, useEffect } from 'react'

// Next Imports
import { useRouter, useSearchParams } from 'next/navigation'

// MUI Imports
import Button from '@mui/material/Button'
import Card from '@mui/material/Card'
import CardContent from '@mui/material/CardContent'
import CardHeader from '@mui/material/CardHeader'
import Grid from '@mui/material/Grid2'
import TextField from '@mui/material/TextField'
import InputAdornment from '@mui/material/InputAdornment'
import IconButton from '@mui/material/IconButton'
import Divider from '@mui/material/Divider'
import Typography from '@mui/material/Typography'

// Third-party Imports
import { useForm, Controller } from 'react-hook-form'

// Local Imports
import type { RelatedContentData } from './CaseStudyRelated';
import CaseStudyRelated from './CaseStudyRelated'
import TextEditor from '@/components/TextEditor'

type FormValues = {
    heading: string
    slug: string
    heroImage: string
    description: string
    startSectionHeading: string
    startSectionShortDescription: string
    detailsImage: string
    detailsContent: string
    caseStudyDetail: string
    relatedContent: RelatedContentData
}

export type CaseStudyPost = {
    id: string
    updatedAt: string
} & FormValues

type Props = {
    isDrawer?: boolean
    handleClose?: () => void
    dataToEdit?: CaseStudyPost
    onSuccess?: () => void
}

const CaseStudyEditor = ({ isDrawer, handleClose, dataToEdit, onSuccess }: Props) => {
    const router = useRouter()
    const searchParams = useSearchParams()

    const editId = isDrawer ? dataToEdit?.id : searchParams?.get('id')

    const {
        control,
        handleSubmit,
        reset,
        setValue,
        watch,
        formState: { errors }
    } = useForm<FormValues>({
        defaultValues: {
            heading: '',
            slug: '',
            heroImage: '',
            description: '',
            startSectionHeading: '',
            startSectionShortDescription: '',
            detailsImage: '',
            detailsContent: '',
            caseStudyDetail: '',
            relatedContent: {
                sectionTypes: [],
                relatedBlogs: [],
                relatedServices: [],
                relatedParts: [],
                relatedProjects: []
            }
        }
    })

    const relatedContentValue = watch('relatedContent')
    const headingValue = watch('heading')

    // Auto-generate slug from heading if slug is empty
    useEffect(() => {
        if (headingValue && !watch('slug')) {
            const generatedSlug = headingValue
                .toLowerCase()
                .replace(/[^a-z0-9]+/g, '-')
                .replace(/(^-|-$)+/g, '')

            setValue('slug', generatedSlug)
        }
    }, [headingValue, setValue, watch])


    useEffect(() => {
        const savedPosts = JSON.parse(localStorage.getItem('casestudy-posts') || '[]')

        if (editId && (dataToEdit || !isDrawer)) {
            const postToEdit = dataToEdit || savedPosts.find((post: any) => post.id === editId)

            if (postToEdit) {
                reset({
                    heading: postToEdit.heading || '',
                    slug: postToEdit.slug || '',
                    heroImage: postToEdit.heroImage || '',
                    description: postToEdit.description || '',
                    startSectionHeading: postToEdit.startSectionHeading || '',
                    startSectionShortDescription: postToEdit.startSectionShortDescription || '',
                    detailsImage: postToEdit.detailsImage || '',
                    detailsContent: postToEdit.detailsContent || '',
                    caseStudyDetail: postToEdit.caseStudyDetail || '',
                    relatedContent: postToEdit.relatedContent || {
                        sectionTypes: [],
                        relatedBlogs: [],
                        relatedServices: [],
                        relatedParts: [],
                        relatedProjects: []
                    }
                })
            }
        }
    }, [editId, reset, dataToEdit, isDrawer])

    const onSubmit = (data: FormValues) => {
        const savedPosts = JSON.parse(localStorage.getItem('casestudy-posts') || '[]')
        const timestamp = new Date().toISOString()

        const finalData = {
            ...data
        }

        let newPostsList

        if (editId) {
            newPostsList = savedPosts.map((post: any) =>
                post.id === editId ? { ...post, ...finalData, updatedAt: timestamp } : post
            )
        } else {
            const newPost = {
                id: Date.now().toString(),
                ...finalData,
                updatedAt: timestamp
            }

            newPostsList = [...savedPosts, newPost]
        }

        localStorage.setItem('casestudy-posts', JSON.stringify(newPostsList))

        if (isDrawer) {
            if (onSuccess) onSuccess()
            if (handleClose) handleClose()
        } else {
            alert(editId ? 'Case Study Updated!' : 'Case Study Created!')

            if (!editId) {
                reset()
            } else {
                router.push('/apps/casestudy/list')
            }
        }
    }

    const handleRelatedContentSave = (data: RelatedContentData) => {
        setValue('relatedContent', data)
    }

    return (
        <form onSubmit={handleSubmit(onSubmit)}>
            <Grid container spacing={6}>
                <Grid size={{ xs: 12 }}>
                    <Card className={isDrawer ? 'shadow-none border-none' : ''}>
                        {!isDrawer && <CardHeader title='Case Study Editor' />}
                        <CardContent className={isDrawer ? 'p-0' : ''}>
                            <Grid container spacing={5}>
                                {/* Heading */}
                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='heading'
                                        control={control}
                                        rules={{ required: true }}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                label='Heading'
                                                placeholder='Case Study Title...'
                                                error={Boolean(errors.heading)}
                                                helperText={errors.heading && 'Heading is required'}
                                            />
                                        )}
                                    />
                                </Grid>

                                {/* Slug (Page Slug) */}
                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='slug'
                                        control={control}
                                        rules={{ required: true }}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                label='Page Slug'
                                                placeholder='case-study-title'
                                                error={Boolean(errors.slug)}
                                                helperText={errors.slug && 'Slug is required'}
                                            />
                                        )}
                                    />
                                </Grid>

                                {/* Hero Image */}
                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='heroImage'
                                        control={control}
                                        render={({ field }) => (
                                            <div className='flex items-center gap-4'>
                                                <TextField
                                                    {...field}
                                                    size='small'
                                                    fullWidth
                                                    placeholder='No file chosen'
                                                    variant='outlined'
                                                    label='Hero Image'
                                                    slotProps={{
                                                        input: {
                                                            endAdornment: field.value ? (
                                                                <InputAdornment position='end'>
                                                                    <IconButton size='small' edge='end' onClick={() => field.onChange('')}>
                                                                        <i className='ri-close-line' />
                                                                    </IconButton>
                                                                </InputAdornment>
                                                            ) : null
                                                        }
                                                    }}
                                                />
                                                <Button component='label' variant='outlined' htmlFor='hero-image-upload' className='min-is-fit'>
                                                    Choose Image
                                                    <input
                                                        hidden
                                                        id='hero-image-upload'
                                                        type='file'
                                                        accept='image/*'
                                                        onChange={(event) => {
                                                            const { files } = event.target

                                                            if (files && files.length !== 0) {
                                                                field.onChange(files[0].name)
                                                            }
                                                        }}
                                                    />
                                                </Button>
                                            </div>
                                        )}
                                    />
                                </Grid>

                                {/* Description */}
                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='description'
                                        control={control}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                multiline
                                                rows={3}
                                                label='Main Description'
                                                placeholder='General overview...'
                                            />
                                        )}
                                    />
                                </Grid>

                                <Grid size={{ xs: 12 }}>
                                    <Divider textAlign='left'>Start Section</Divider>
                                </Grid>

                                {/* Start Section Heading */}
                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='startSectionHeading'
                                        control={control}
                                        rules={{ required: true }}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                label='Start Section Heading'
                                                placeholder='Introduction Title...'
                                                error={Boolean(errors.startSectionHeading)}
                                                helperText={errors.startSectionHeading && 'Start Section Heading is required'}
                                            />
                                        )}
                                    />
                                </Grid>

                                {/* Start Section Short Description */}
                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='startSectionShortDescription'
                                        control={control}
                                        rules={{ required: true }}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                multiline
                                                rows={2}
                                                label='Start Section Short Description'
                                                placeholder='Brief intro...'
                                                error={Boolean(errors.startSectionShortDescription)}
                                                helperText={errors.startSectionShortDescription && 'Short Description is required'}
                                            />
                                        )}
                                    />
                                </Grid>

                                <Grid size={{ xs: 12 }}>
                                    <Divider textAlign='left'>Details Section</Divider>
                                </Grid>

                                {/* Details Image */}
                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='detailsImage'
                                        control={control}
                                        render={({ field }) => (
                                            <div className='flex items-center gap-4'>
                                                <TextField
                                                    {...field}
                                                    size='small'
                                                    fullWidth
                                                    placeholder='No file chosen'
                                                    variant='outlined'
                                                    label='Details Image'
                                                    slotProps={{
                                                        input: {
                                                            endAdornment: field.value ? (
                                                                <InputAdornment position='end'>
                                                                    <IconButton size='small' edge='end' onClick={() => field.onChange('')}>
                                                                        <i className='ri-close-line' />
                                                                    </IconButton>
                                                                </InputAdornment>
                                                            ) : null
                                                        }
                                                    }}
                                                />
                                                <Button component='label' variant='outlined' htmlFor='details-image-upload' className='min-is-fit'>
                                                    Choose Image
                                                    <input
                                                        hidden
                                                        id='details-image-upload'
                                                        type='file'
                                                        accept='image/*'
                                                        onChange={(event) => {
                                                            const { files } = event.target

                                                            if (files && files.length !== 0) {
                                                                field.onChange(files[0].name)
                                                            }
                                                        }}
                                                    />
                                                </Button>
                                            </div>
                                        )}
                                    />
                                </Grid>

                                {/* Details Content - Text Editor */}
                                <Grid size={{ xs: 12 }}>
                                    <Typography className='mbe-2'>Details Content</Typography>
                                    <Controller
                                        name='detailsContent'
                                        control={control}
                                        render={({ field }) => (
                                            <TextEditor
                                                value={field.value}
                                                onChange={field.onChange}
                                            />
                                        )}
                                    />
                                </Grid>


                                <Grid size={{ xs: 12 }}>
                                    <Divider textAlign='left'>Full Case Study Detail</Divider>
                                </Grid>

                                {/* Case Study Detail - Text Editor */}
                                <Grid size={{ xs: 12 }}>
                                    <Typography className='mbe-2'>Full Case Study Detail</Typography>
                                    <Controller
                                        name='caseStudyDetail'
                                        control={control}
                                        render={({ field }) => (
                                            <TextEditor
                                                value={field.value}
                                                onChange={field.onChange}
                                            />
                                        )}
                                    />
                                </Grid>

                                <Grid size={{ xs: 12 }}>
                                    <Divider textAlign='left'>Related Content</Divider>
                                </Grid>

                                {/* Related Content */}
                                <Grid size={{ xs: 12 }}>
                                    <CaseStudyRelated
                                        caseStudyData={relatedContentValue}
                                        onSave={handleRelatedContentSave}
                                    />
                                </Grid>

                            </Grid>
                        </CardContent>
                    </Card>
                </Grid>

                <Grid size={{ xs: 12 }} className='flex justify-end pbe-10 gap-4'>
                    {isDrawer && handleClose && (
                        <Button variant='outlined' color='secondary' onClick={handleClose}>
                            Cancel
                        </Button>
                    )}
                    <Button variant='contained' size='large' type='submit'>
                        {editId ? 'Update Case Study' : 'Create Case Study'}
                    </Button>
                </Grid>
            </Grid>
        </form>
    )
}

export default CaseStudyEditor

'use client'

// React Imports
import { useState, useEffect } from 'react'

// MUI Imports
import Button from '@mui/material/Button'
import Card from '@mui/material/Card'
import CardContent from '@mui/material/CardContent'
import CardHeader from '@mui/material/CardHeader'
import Grid from '@mui/material/Grid2'
import TextField from '@mui/material/TextField'
import InputAdornment from '@mui/material/InputAdornment'
import IconButton from '@mui/material/IconButton'
import Divider from '@mui/material/Divider'
import MenuItem from '@mui/material/MenuItem'

// Third-party Imports
import { useForm, Controller } from 'react-hook-form'

// Local Imports
import type { RelatedContentData } from './CaseStudyRelated';
import CaseStudyRelated from './CaseStudyRelated'

type LandingFormValues = {
    heading: string
    subheading: string
    ctaButtonText: string
    ctaButtonLink: string
    heroImage: string
    sectionHeading: string
    sectionDescription: string
    relatedContent: RelatedContentData
}

type Props = {
    handleClose?: () => void
}

const INTERNAL_PAGES = [
    { label: 'Home', value: '/dashboards/crm' }, // Adjust based on actual home route
    { label: 'Case Studies', value: '/apps/casestudy/list' },
    { label: 'Academy', value: '/apps/academy/dashboard' },
    { label: 'Jobs', value: '/apps/career/dashboard' },
    { label: 'Events', value: '/apps/events/list' },
    { label: 'News', value: '/apps/news/list' },
    { label: 'Blog', value: '/apps/blog/dashboard' },
    { label: 'Contact Us', value: '/pages/contact' }, // Assuming this exists or similar
    { label: 'About Us', value: '/pages/about' }, // Assuming this exists
    { label: 'FAQ', value: '/pages/faq' },
    { label: 'Pricing', value: '/pages/pricing' }
]

const CaseStudyLandingSettings = ({ handleClose }: Props) => {
    const {
        control,
        handleSubmit,
        reset,
        setValue,
        watch,
        formState: { errors }
    } = useForm<LandingFormValues>({
        defaultValues: {
            heading: '',
            subheading: '',
            ctaButtonText: 'Learn More',
            ctaButtonLink: '',
            heroImage: '',
            sectionHeading: '',
            sectionDescription: '',
            relatedContent: {
                sectionTypes: [],
                relatedBlogs: [],
                relatedServices: [],
                relatedParts: [],
                relatedProjects: []
            }
        }
    })

    const relatedContentValue = watch('relatedContent')

    useEffect(() => {
        const savedSettings = localStorage.getItem('casestudy-landing')

        if (savedSettings) {
            reset(JSON.parse(savedSettings))
        }
    }, [reset])

    const onSubmit = (data: LandingFormValues) => {
        localStorage.setItem('casestudy-landing', JSON.stringify(data))
        alert('Landing Page Settings Saved!')
        if (handleClose) handleClose()
    }

    const handleRelatedContentSave = (data: RelatedContentData) => {
        setValue('relatedContent', data)
    }

    return (
        <form onSubmit={handleSubmit(onSubmit)}>
            <Grid container spacing={6}>
                <Grid size={{ xs: 12 }}>
                    <Card className='shadow-none border-none'>
                        <CardHeader title='Landing Page Configuration' />
                        <CardContent className='p-0'>
                            <Grid container spacing={5}>
                                {/* Heading */}
                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='heading'
                                        control={control}
                                        rules={{ required: true }}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                label='Page Heading'
                                                placeholder='Main Page Title...'
                                                error={Boolean(errors.heading)}
                                                helperText={errors.heading && 'Heading is required'}
                                            />
                                        )}
                                    />
                                </Grid>

                                {/* Subheading */}
                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='subheading'
                                        control={control}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                label='Subheading'
                                                placeholder='Subtitle...'
                                            />
                                        )}
                                    />
                                </Grid>

                                {/* CTA Button Text */}
                                <Grid size={{ xs: 12, sm: 6 }}>
                                    <Controller
                                        name='ctaButtonText'
                                        control={control}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                label='CTA Button Text'
                                                placeholder='Learn More'
                                            />
                                        )}
                                    />
                                </Grid>

                                {/* CTA Button Link */}
                                <Grid size={{ xs: 12, sm: 6 }}>
                                    <Controller
                                        name='ctaButtonLink'
                                        control={control}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                select
                                                fullWidth
                                                label='CTA Button Link'
                                                defaultValue=''
                                            >
                                                <MenuItem value=''>
                                                    <em>None</em>
                                                </MenuItem>
                                                {INTERNAL_PAGES.map((page) => (
                                                    <MenuItem key={page.value} value={page.value}>
                                                        {page.label}
                                                    </MenuItem>
                                                ))}
                                            </TextField>
                                        )}
                                    />
                                </Grid>

                                {/* Hero Image */}
                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='heroImage'
                                        control={control}
                                        render={({ field }) => (
                                            <div className='flex items-center gap-4'>
                                                <TextField
                                                    {...field}
                                                    size='small'
                                                    fullWidth
                                                    placeholder='No file chosen'
                                                    variant='outlined'
                                                    label='Hero Image'
                                                    slotProps={{
                                                        input: {
                                                            endAdornment: field.value ? (
                                                                <InputAdornment position='end'>
                                                                    <IconButton size='small' edge='end' onClick={() => field.onChange('')}>
                                                                        <i className='ri-close-line' />
                                                                    </IconButton>
                                                                </InputAdornment>
                                                            ) : null
                                                        }
                                                    }}
                                                />
                                                <Button component='label' variant='outlined' htmlFor='landing-hero-image-upload' className='min-is-fit'>
                                                    Choose Image
                                                    <input
                                                        hidden
                                                        id='landing-hero-image-upload'
                                                        type='file'
                                                        accept='image/*'
                                                        onChange={(event) => {
                                                            const { files } = event.target

                                                            if (files && files.length !== 0) {
                                                                field.onChange(files[0].name)
                                                            }
                                                        }}
                                                    />
                                                </Button>
                                            </div>
                                        )}
                                    />
                                </Grid>

                                <Grid size={{ xs: 12 }}>
                                    <Divider textAlign='left'>Content Section</Divider>
                                </Grid>

                                {/* Fixed Section Heading */}
                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='sectionHeading'
                                        control={control}
                                        rules={{ required: true }}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                label='Section Heading'
                                                placeholder='Section Title...'
                                                error={Boolean(errors.sectionHeading)}
                                                helperText={errors.sectionHeading && 'Section Heading is required'}
                                            />
                                        )}
                                    />
                                </Grid>

                                {/* Fixed Section Description */}
                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='sectionDescription'
                                        control={control}
                                        rules={{ required: true }}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                multiline
                                                rows={4}
                                                label='Section Description'
                                                placeholder='Content paragraph...'
                                                error={Boolean(errors.sectionDescription)}
                                                helperText={errors.sectionDescription && 'Section Description is required'}
                                            />
                                        )}
                                    />
                                </Grid>

                                <Grid size={{ xs: 12 }}>
                                    <Divider textAlign='left'>Related Content</Divider>
                                </Grid>

                                {/* Related Content */}
                                <Grid size={{ xs: 12 }}>
                                    <CaseStudyRelated
                                        caseStudyData={relatedContentValue}
                                        onSave={handleRelatedContentSave}
                                    />
                                </Grid>

                            </Grid>
                        </CardContent>
                    </Card>
                </Grid>

                <Grid size={{ xs: 12 }} className='flex justify-end pbe-10 gap-4'>
                    {handleClose && (
                        <Button variant='outlined' color='secondary' onClick={handleClose}>
                            Cancel
                        </Button>
                    )}
                    <Button variant='contained' size='large' type='submit'>
                        Save Settings
                    </Button>
                </Grid>
            </Grid>
        </form>
    )
}

