'use client'

// React Imports
import { useState, useEffect } from 'react'

// MUI Imports
import Card from '@mui/material/Card'
import CardHeader from '@mui/material/CardHeader'
import CardContent from '@mui/material/CardContent'
import Grid from '@mui/material/Grid2'
import Select from '@mui/material/Select'
import MenuItem from '@mui/material/MenuItem'
import Chip from '@mui/material/Chip'
import OutlinedInput from '@mui/material/OutlinedInput'
import InputLabel from '@mui/material/InputLabel'
import FormControl from '@mui/material/FormControl'
import FormHelperText from '@mui/material/FormHelperText'
import Button from '@mui/material/Button'

// Third-party Imports
import { useForm, Controller, useWatch } from 'react-hook-form'

type RelatedSectionType = 'blogs' | 'services' | 'parts' | 'projects'

export type RelatedContentData = {
    sectionTypes: RelatedSectionType[]
    relatedBlogs: string[]
    relatedServices: string[]
    relatedParts: string[]
    relatedProjects: string[]
}

// Mock Data
const MOCK_BLOGS = [
    { id: '1', title: 'Top 10 Career Tips' },
    { id: '2', title: 'Resume Building Guide' },
    { id: '3', title: 'Interview Preparation' },
    { id: '4', title: 'Workplace Etiquette' }
]

const MOCK_SERVICES = [
    { id: 's1', title: 'Resume Review' },
    { id: 's2', title: 'Career Counseling' },
    { id: 's3', title: 'Mock Interview' },
    { id: 's4', title: 'LinkedIn Profile Optimization' }
]

const MOCK_PARTS = [
    { id: 'p1', title: 'Office Equipment' },
    { id: 'p2', title: 'Software Licenses' },
    { id: 'p3', title: 'Training Materials' },
    { id: 'p4', title: 'IT Support' }
]

const MOCK_PROJECTS = [
    { id: 'pr1', title: 'Internal Tool Development' },
    { id: 'pr2', title: 'Client Website Redesign' },
    { id: 'pr3', title: 'Mobile App Launch' },
    { id: 'pr4', title: 'Data Analytics Dashboard' }
]

const CaseStudyRelated = ({ caseStudyData, id, onSave }: { caseStudyData?: any; id?: string, onSave?: (data: RelatedContentData) => void }) => {
    const {
        control,
        handleSubmit,
        reset,
        useWatch: useWatchForm
    } = useForm<RelatedContentData>({
        defaultValues: {
            sectionTypes: [],
            relatedBlogs: [],
            relatedServices: [],
            relatedParts: [],
            relatedProjects: []
        }
    })

    // Watch section types to handle conditioning and side-effects
    const selectedSectionTypes = useWatch({ control, name: 'sectionTypes' }) || []

    // Load saved data or props on mount
    useEffect(() => {
        // If data is passed via props, use it. Otherwise try local storage if id exists.
        if (caseStudyData) {
            reset({
                sectionTypes: caseStudyData.sectionTypes || [],
                relatedBlogs: caseStudyData.relatedBlogs || [],
                relatedServices: caseStudyData.relatedServices || [],
                relatedParts: caseStudyData.relatedParts || [],
                relatedProjects: caseStudyData.relatedProjects || []
            })
        } else if (id) {
            const storageKey = `casestudy-related-${id}`
            const savedData = localStorage.getItem(storageKey)

            if (savedData) {
                reset(JSON.parse(savedData))
            }
        }
    }, [caseStudyData, reset, id])

    const onSubmit = (data: RelatedContentData) => {
        if (id) {
            const storageKey = `casestudy-related-${id}`

            localStorage.setItem(storageKey, JSON.stringify(data))
        }

        if (onSave) {
            onSave(data)
        }

        console.log('Submitted Case Study Related Content:', data)
        alert('Case Study Related Content Saved!')
    }

    const availableOptions = [
        { value: 'blogs', label: 'Related Blogs' },
        { value: 'services', label: 'Related Services' },
        { value: 'parts', label: 'Related Parts' },
        { value: 'projects', label: 'Related Projects' }
    ]

    return (
        <Card>
            <CardHeader title='Related Content Configuration' subheader='Choose up to 2 sections to display' />
            <CardContent>
                <form onSubmit={handleSubmit(onSubmit)}>
                    <Grid container spacing={5}>
                        <Grid size={{ xs: 12 }}>
                            <Controller
                                name='sectionTypes'
                                control={control}
                                render={({ field }) => (
                                    <FormControl fullWidth error={selectedSectionTypes.length > 2}>
                                        <InputLabel id='casestudy-section-types-label'>Select Related Sections (Max 2)</InputLabel>
                                        <Select
                                            {...field}
                                            labelId='casestudy-section-types-label'
                                            multiple
                                            input={<OutlinedInput label='Select Related Sections (Max 2)' />}
                                            renderValue={(selected) => (
                                                <div className='flex flex-wrap gap-2'>
                                                    {(selected as string[]).map((value) => (
                                                        <Chip
                                                            key={value}
                                                            label={availableOptions.find(opt => opt.value === value)?.label || value}
                                                            size='small'
                                                            onDelete={() => {
                                                                const newValue = (selected as string[]).filter((item) => item !== value)

                                                                field.onChange(newValue)
                                                            }}
                                                            onMouseDown={(event) => {
                                                                event.stopPropagation()
                                                            }}
                                                        />
                                                    ))}
                                                </div>
                                            )}
                                            onChange={(e) => {
                                                const value = e.target.value as string[]

                                                if (value.length <= 2) {
                                                    field.onChange(value)
                                                }
                                            }}
                                        >
                                            {availableOptions.map((option) => (
                                                <MenuItem
                                                    key={option.value}
                                                    value={option.value}
                                                    disabled={selectedSectionTypes.length >= 2 && !selectedSectionTypes.includes(option.value as RelatedSectionType)}
                                                >
                                                    {option.label}
                                                </MenuItem>
                                            ))}
                                        </Select>
                                        {selectedSectionTypes.length >= 2 && (
                                            <FormHelperText>Maximum 2 sections selected</FormHelperText>
                                        )}
                                    </FormControl>
                                )}
                            />
                        </Grid>

                        {selectedSectionTypes.includes('blogs') && (
                            <Grid size={{ xs: 12 }}>
                                <Controller
                                    name='relatedBlogs'
                                    control={control}
                                    render={({ field }) => (
                                        <FormControl fullWidth>
                                            <InputLabel id='casestudy-related-blogs-label'>Select Related Blogs</InputLabel>
                                            <Select
                                                {...field}
                                                labelId='casestudy-related-blogs-label'
                                                multiple
                                                input={<OutlinedInput label='Select Related Blogs' />}
                                                renderValue={(selected) => (
                                                    <div className='flex flex-wrap gap-2'>
                                                        {(selected as string[]).map((value) => (
                                                            <Chip
                                                                key={value}
                                                                label={MOCK_BLOGS.find(b => b.id === value)?.title || value}
                                                                size='small'
                                                                onDelete={() => {
                                                                    const newValue = (selected as string[]).filter((item) => item !== value)

                                                                    field.onChange(newValue)
                                                                }}
                                                                onMouseDown={(event) => event.stopPropagation()}
                                                            />
                                                        ))}
                                                    </div>
                                                )}
                                            >
                                                {MOCK_BLOGS.map((item) => (
                                                    <MenuItem key={item.id} value={item.id}>{item.title}</MenuItem>
                                                ))}
                                            </Select>
                                        </FormControl>
                                    )}
                                />
                            </Grid>
                        )}

                        {selectedSectionTypes.includes('services') && (
                            <Grid size={{ xs: 12 }}>
                                <Controller
                                    name='relatedServices'
                                    control={control}
                                    render={({ field }) => (
                                        <FormControl fullWidth>
                                            <InputLabel id='casestudy-related-services-label'>Select Related Services</InputLabel>
                                            <Select
                                                {...field}
                                                labelId='casestudy-related-services-label'
                                                multiple
                                                input={<OutlinedInput label='Select Related Services' />}
                                                renderValue={(selected) => (
                                                    <div className='flex flex-wrap gap-2'>
                                                        {(selected as string[]).map((value) => (
                                                            <Chip
                                                                key={value}
                                                                label={MOCK_SERVICES.find(s => s.id === value)?.title || value}
                                                                size='small'
                                                                onDelete={() => {
                                                                    const newValue = (selected as string[]).filter((item) => item !== value)

                                                                    field.onChange(newValue)
                                                                }}
                                                                onMouseDown={(event) => event.stopPropagation()}
                                                            />
                                                        ))}
                                                    </div>
                                                )}
                                            >
                                                {MOCK_SERVICES.map((item) => (
                                                    <MenuItem key={item.id} value={item.id}>{item.title}</MenuItem>
                                                ))}
                                            </Select>
                                        </FormControl>
                                    )}
                                />
                            </Grid>
                        )}

                        {selectedSectionTypes.includes('parts') && (
                            <Grid size={{ xs: 12 }}>
                                <Controller
                                    name='relatedParts'
                                    control={control}
                                    render={({ field }) => (
                                        <FormControl fullWidth>
                                            <InputLabel id='casestudy-related-parts-label'>Select Related Parts</InputLabel>
                                            <Select
                                                {...field}
                                                labelId='casestudy-related-parts-label'
                                                multiple
                                                input={<OutlinedInput label='Select Related Parts' />}
                                                renderValue={(selected) => (
                                                    <div className='flex flex-wrap gap-2'>
                                                        {(selected as string[]).map((value) => (
                                                            <Chip
                                                                key={value}
                                                                label={MOCK_PARTS.find(p => p.id === value)?.title || value}
                                                                size='small'
                                                                onDelete={() => {
                                                                    const newValue = (selected as string[]).filter((item) => item !== value)

                                                                    field.onChange(newValue)
                                                                }}
                                                                onMouseDown={(event) => event.stopPropagation()}
                                                            />
                                                        ))}
                                                    </div>
                                                )}
                                            >
                                                {MOCK_PARTS.map((item) => (
                                                    <MenuItem key={item.id} value={item.id}>{item.title}</MenuItem>
                                                ))}
                                            </Select>
                                        </FormControl>
                                    )}
                                />
                            </Grid>
                        )}

                        {selectedSectionTypes.includes('projects') && (
                            <Grid size={{ xs: 12 }}>
                                <Controller
                                    name='relatedProjects'
                                    control={control}
                                    render={({ field }) => (
                                        <FormControl fullWidth>
                                            <InputLabel id='casestudy-related-projects-label'>Select Related Projects</InputLabel>
                                            <Select
                                                {...field}
                                                labelId='casestudy-related-projects-label'
                                                multiple
                                                input={<OutlinedInput label='Select Related Projects' />}
                                                renderValue={(selected) => (
                                                    <div className='flex flex-wrap gap-2'>
                                                        {(selected as string[]).map((value) => (
                                                            <Chip
                                                                key={value}
                                                                label={MOCK_PROJECTS.find(p => p.id === value)?.title || value}
                                                                size='small'
                                                                onDelete={() => {
                                                                    const newValue = (selected as string[]).filter((item) => item !== value)

                                                                    field.onChange(newValue)
                                                                }}
                                                                onMouseDown={(event) => event.stopPropagation()}
                                                            />
                                                        ))}
                                                    </div>
                                                )}
                                            >
                                                {MOCK_PROJECTS.map((item) => (
                                                    <MenuItem key={item.id} value={item.id}>{item.title}</MenuItem>
                                                ))}
                                            </Select>
                                        </FormControl>
                                    )}
                                />
                            </Grid>
                        )}
                        <Grid size={{ xs: 12 }} className='flex justify-end'>
                            <Button variant='contained' type='submit'>
                                Save Related Content
                            </Button>
                        </Grid>
                    </Grid>
                </form>
            </CardContent>
        </Card>
    )
}

export default CaseStudyRelated

// React Imports
import { useState } from 'react'
import type { ChangeEvent, ReactNode } from 'react'

// MUI Import
import Drawer from '@mui/material/Drawer'
import Typography from '@mui/material/Typography'
import IconButton from '@mui/material/IconButton'
import TextField from '@mui/material/TextField'
import Radio from '@mui/material/Radio'
import RadioGroup from '@mui/material/RadioGroup'
import FormControlLabel from '@mui/material/FormControlLabel'
import FormLabel from '@mui/material/FormLabel'
import List from '@mui/material/List'
import ListItem from '@mui/material/ListItem'
import ListItemButton from '@mui/material/ListItemButton'
import ListItemText from '@mui/material/ListItemText'
import ListItemIcon from '@mui/material/ListItemIcon'
import Switch from '@mui/material/Switch'
import Backdrop from '@mui/material/Backdrop'
import Button from '@mui/material/Button'

// Third Party Imports
import PerfectScrollbar from 'react-perfect-scrollbar'

// Type Imports
import type { AppDispatch } from '@/redux-store'
import type { ProfileUserType, StatusType } from '@/types/apps/chatTypes'

// Slice Imports
import { setUserStatus } from '@/redux-store/slices/chat'

// Component Imports
import AvatarWithBadge from './AvatarWithBadge'
import { statusObj } from '@views/apps/chat/SidebarLeft'

type Props = {
  userSidebar: boolean
  setUserSidebar: (open: boolean) => void
  profileUserData: ProfileUserType
  dispatch: AppDispatch
  isBelowLgScreen: boolean
  isBelowSmScreen: boolean
}

const ScrollWrapper = ({ children, isBelowLgScreen }: { children: ReactNode; isBelowLgScreen: boolean }) => {
  if (isBelowLgScreen) {
    return <div className='bs-full overflow-x-hidden overflow-y-auto'>{children}</div>
  } else {
    return <PerfectScrollbar options={{ wheelPropagation: false }}>{children}</PerfectScrollbar>
  }
}

const UserProfileLeft = (props: Props) => {
  // Props
  const { userSidebar, setUserSidebar, profileUserData, dispatch, isBelowLgScreen, isBelowSmScreen } = props

  // States
  const [twoStepVerification, setTwoStepVerification] = useState<boolean>(true)
  const [notification, setNotification] = useState<boolean>(false)

  const handleTwoStepVerification = () => {
    setTwoStepVerification(!twoStepVerification)
  }

  const handleNotification = () => {
    setNotification(!notification)
  }

  const handleUserStatus = (e: ChangeEvent<HTMLInputElement>) => {
    dispatch(setUserStatus({ status: e.target.value as StatusType }))
  }

  return profileUserData ? (
    <>
      <Drawer
        open={userSidebar}
        anchor='left'
        variant='persistent'
        ModalProps={{ keepMounted: true }}
        onClose={() => setUserSidebar(false)}
        sx={{
          zIndex: 13,
          '& .MuiDrawer-paper': { width: isBelowSmScreen ? '100%' : '370px', position: 'absolute', border: 0 }
        }}
      >
        <IconButton className='absolute block-start-4 inline-end-4' onClick={() => setUserSidebar(false)}>
          <i className='ri-close-line' />
        </IconButton>
        <div className='flex flex-col justify-center items-center gap-4 mbs-6 pli-5 pbs-5 pbe-1'>
          <AvatarWithBadge
            alt={profileUserData.fullName}
            src={profileUserData.avatar}
            badgeColor={statusObj[profileUserData.status]}
            className='bs-[84px] is-[84px]'
            badgeSize={12}
          />
          <div className='text-center'>
            <Typography variant='h5'>{profileUserData.fullName}</Typography>

          </div>
        </div>
        <ScrollWrapper isBelowLgScreen={isBelowLgScreen}>
          <div className='flex flex-col gap-6 p-5'>
            <List className='plb-0'>
              <ListItem className='p-2 gap-2'>
                <ListItemIcon>
                  <i className='ri-mail-line' />
                </ListItemIcon>
                <ListItemText primary={`${profileUserData.fullName.toLowerCase().replace(/\s/g, '_')}@email.com`} />
              </ListItem>
            </List>
            <div className='flex flex-col gap-1'>
              <FormLabel id='status-radio-buttons-group-label' className='uppercase text-textDisabled'>
                Status
              </FormLabel>
              <RadioGroup
                value={profileUserData.status}
                name='radio-buttons-group'
                onChange={handleUserStatus}
                aria-labelledby='status-radio-buttons-group-label'
              >
                <FormControlLabel value='online' control={<Radio color='success' />} label='Online' />
                <FormControlLabel value='away' control={<Radio color='warning' />} label='Away' />
                <FormControlLabel value='busy' control={<Radio color='error' />} label='Do not disturb' />
                <FormControlLabel value='offline' control={<Radio color='secondary' />} label='Offline' />
              </RadioGroup>
            </div>

          </div>
        </ScrollWrapper>
      </Drawer>
      <Backdrop open={userSidebar} onClick={() => setUserSidebar(false)} className='absolute z-[12]' />
    </>
  ) : null
}

