    return new Promise((resolve, reject) => {
        const reader = new FileReader()

        reader.readAsDataURL(file)
        reader.onload = () => resolve(reader.result as string)
        reader.onerror = (error) => reject(error)
    })
}

type FormValues = {
    category: string
    heading: string
    shortDescription: string
    longDetail: string
    location: string
    eventDate: Date | null
    mainImage: string
    gallery: string[]
    status: 'published' | 'scheduled' | 'draft'
    scheduledDate: Date | null
}

type Category = {
    id: string
    title: string
}

type EventPost = {
    id: string
    heading: string
    status: string
    eventDate: string | null

    /* ... other fields ... */
}

type Props = {
    isDrawer?: boolean
    handleClose?: () => void
    dataToEdit?: EventPost
    onSuccess?: () => void
}

const TiptapEditor = ({ value, onChange, placeholder }: { value: string; onChange: (content: string) => void; placeholder?: string }) => {
    const editor = useEditor({
        extensions: [
            StarterKit,
            Placeholder.configure({
                placeholder: placeholder || 'Write content here...'
            }),
            TextAlign.configure({
                types: ['heading', 'paragraph']
            }),
            Underline
        ],
        content: value,
        onUpdate: ({ editor }) => {
            onChange(editor.getHTML())
        },
        immediatelyRender: false
    })

    return (
        <Card className='p-0 border shadow-none'>
            <CardContent className='p-0'>
                <EditorToolbar editor={editor} />
                <Divider className='mli-5' />
                <EditorContent editor={editor} className='bs-[135px] overflow-y-auto flex ' />
            </CardContent>
        </Card>
    )
}

const EventsEditor = ({ isDrawer, handleClose, dataToEdit, onSuccess }: Props) => {
    const [categories, setCategories] = useState<Category[]>([])
    const [imageModalOpen, setImageModalOpen] = useState(false)
    const [selectedImage, setSelectedImage] = useState('')
    const router = useRouter()
    const searchParams = useSearchParams()

    // Use dataToEdit id if in drawer, else URL param
    const editId = isDrawer ? dataToEdit?.id : searchParams?.get('id')

    const {
        control,
        handleSubmit,
        reset,
        setValue,
        watch,
        formState: { errors }
    } = useForm<FormValues>({
        defaultValues: {
            category: '',
            heading: '',
            shortDescription: '',
            longDetail: '',
            location: '',
            eventDate: new Date(),
            mainImage: '',
            gallery: [],
            gallery: [],
            status: 'draft',
            scheduledDate: new Date()
        }
    })

    const statusValue = watch('status')

    useEffect(() => {
        // Load Categories
        const savedCategories = localStorage.getItem('events-categories')

        if (savedCategories) {
            setCategories(JSON.parse(savedCategories))
        } else {
            // Default categories if none exist
            const defaultCategories = [{ id: '1', title: 'Music' }, { id: '2', title: 'Tech' }, { id: '3', title: 'Workshop' }]

            setCategories(defaultCategories)
            localStorage.setItem('events-categories', JSON.stringify(defaultCategories))
        }

        const savedEvents = JSON.parse(localStorage.getItem('events-posts') || '[]')

        if (editId && (dataToEdit || !isDrawer)) {
            const postToEdit = dataToEdit || savedEvents.find((post: any) => post.id === editId)

            if (postToEdit) {
                reset({
                    category: postToEdit.category || '',
                    heading: postToEdit.heading || '',
                    shortDescription: postToEdit.shortDescription || '',
                    longDetail: postToEdit.longDetail || '',
                    location: postToEdit.location || '',
                    eventDate: postToEdit.eventDate ? new Date(postToEdit.eventDate) : new Date(),
                    mainImage: postToEdit.mainImage || '',
                    gallery: postToEdit.gallery || [],
                    gallery: postToEdit.gallery || [],
                    status: postToEdit.status || 'draft',
                    scheduledDate: postToEdit.scheduledDate ? new Date(postToEdit.scheduledDate) : new Date()
                })
            }
        }
    }, [editId, reset, dataToEdit, isDrawer])

    const onSubmit = (data: FormValues) => {
        const savedEvents = JSON.parse(localStorage.getItem('events-posts') || '[]')
        const timestamp = new Date().toISOString()

        const finalData = {
            ...data,
            ...data,
            eventDate: data.eventDate ? data.eventDate.toISOString() : null,
            scheduledDate: data.scheduledDate ? data.scheduledDate.toISOString() : null
        }

        let newEventsList

        if (editId) {
            newEventsList = savedEvents.map((post: any) =>
                post.id === editId ? { ...post, ...finalData, updatedAt: timestamp } : post
            )
        } else {
            const newPost = {
                id: Date.now().toString(),
                ...finalData,
                publishedAt: timestamp,
                updatedAt: timestamp
            }

            newEventsList = [...savedEvents, newPost]
        }

        localStorage.setItem('events-posts', JSON.stringify(newEventsList))

        if (isDrawer) {
            if (onSuccess) onSuccess()
            if (handleClose) handleClose()
        } else {
            alert(editId ? 'Event Updated!' : 'Event Published!')

            if (!editId) {
                reset()
            } else {
                router.push('/apps/events/list')
            }
        }
    }

    return (
        <form onSubmit={handleSubmit(onSubmit)}>
            <Grid container spacing={6}>
                <Grid size={{ xs: 12 }}>
                    <Card className={isDrawer ? 'shadow-none border-none' : ''}>
                        {!isDrawer && <CardHeader title='Event Creating and Editing' />}
                        <CardContent className={isDrawer ? 'p-0' : ''}>
                            <Grid container spacing={5}>
                                {/* Category */}
                                <Grid size={{ xs: 12, sm: 6 }}>
                                    <Controller
                                        name='category'
                                        control={control}
                                        rules={{ required: true }}
                                        render={({ field }) => (
                                            <FormControl fullWidth error={Boolean(errors.category)}>
                                                <InputLabel id='category-select'>Category</InputLabel>
                                                <Select
                                                    {...field}
                                                    labelId='category-select'
                                                    label='Category'
                                                >
                                                    <MenuItem value='' disabled>Select a Category</MenuItem>
                                                    {categories.map((cat) => (
                                                        <MenuItem key={cat.id} value={cat.title}>
                                                            {cat.title}
                                                        </MenuItem>
                                                    ))}
                                                </Select>
                                                {errors.category && <Typography color='error' variant='caption'>Category is required</Typography>}
                                            </FormControl>
                                        )}
                                    />
                                </Grid>

                                {/* Heading */}
                                <Grid size={{ xs: 12, sm: 6 }}>
                                    <Controller
                                        name='heading'
                                        control={control}
                                        rules={{ required: true }}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                label='Event Heading'
                                                placeholder='Event Name...'
                                                error={Boolean(errors.heading)}
                                                helperText={errors.heading && 'Heading is required'}
                                            />
                                        )}
                                    />
                                </Grid>

                                {/* Location */}
                                <Grid size={{ xs: 12, sm: 6 }}>
                                    <Controller
                                        name='location'
                                        control={control}
                                        rules={{ required: true }}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                label='Event Location'
                                                placeholder='New York, USA'
                                                error={Boolean(errors.location)}
                                                helperText={errors.location && 'Location is required'}
                                            />
                                        )}
                                    />
                                </Grid>

                                {/* Event Date */}
                                <Grid size={{ xs: 12, sm: 6 }}>
                                    <Controller
                                        name="eventDate"
                                        control={control}
                                        render={({ field }) => (
                                            <div className='flex flex-col gap-2'>
                                                <DatePicker
                                                    selected={field.value}
                                                    onChange={(date) => field.onChange(date)}
                                                    showTimeSelect
                                                    dateFormat="Pp"
                                                    className='w-full border rounded p-2'
                                                    customInput={<TextField fullWidth label="Event Date" />}
                                                />
                                            </div>
                                        )}
                                    />
                                </Grid>

                                {/* Main Image */}
                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='mainImage'
                                        control={control}
                                        render={({ field }) => {
                                            const isValidImage = field.value && field.value.startsWith('data:image/')


return (
                                                <div className='flex flex-col gap-4'>
                                                    <div className='flex items-center gap-4'>
                                                        {isValidImage && <Typography variant='body2' className='font-medium'>Main Image</Typography>}
                                                        <Button component='label' variant='outlined' htmlFor='main-image-upload'>
                                                            Choose Image
                                                            <input
                                                                hidden
                                                                id='main-image-upload'
                                                                type='file'
                                                                accept='image/*'
                                                                onChange={async (event) => {
                                                                    const { files } = event.target

                                                                    if (files && files.length !== 0) {
                                                                        const base64 = await fileToBase64(files[0])

                                                                        field.onChange(base64)
                                                                    }
                                                                }}
                                                            />
                                                        </Button>
                                                        {isValidImage && (
                                                            <IconButton size='small' color='error' onClick={() => field.onChange('')}>
                                                                <i className='ri-delete-bin-line' />
                                                            </IconButton>
                                                        )}
                                                    </div>
                                                    {isValidImage && (
                                                        <div
                                                            className='border rounded p-2 cursor-pointer hover:opacity-80 transition-opacity'
                                                            onClick={() => {
                                                                setSelectedImage(field.value)
                                                                setImageModalOpen(true)
                                                            }}
                                                        >
                                                            <img
                                                                src={field.value}
                                                                alt='Main Image Preview'
                                                                className='w-32 h-32 object-cover rounded'
                                                            />
                                                        </div>
                                                    )}
                                                </div>
                                            )
                                        }}
                                    />
                                </Grid>

                                {/* Gallery (Multiple Images) */}
                                <Grid size={{ xs: 12 }}>
                                    <Typography className='mbe-2'>Event Gallery</Typography>
                                    <Controller
                                        name='gallery'
                                        control={control}
                                        render={({ field }) => (
                                            <div className='flex flex-col gap-4'>
                                                <Button component='label' variant='outlined' htmlFor='gallery-upload' className='w-fit'>
                                                    Upload Gallery Images
                                                    <input
                                                        hidden
                                                        id='gallery-upload'
                                                        type='file'
                                                        accept='image/*'
                                                        multiple
                                                        onChange={(event) => {
                                                            const { files } = event.target

                                                            if (files && files.length !== 0) {
                                                                const fileNames = Array.from(files).map(f => f.name)

                                                                field.onChange([...field.value, ...fileNames])
                                                            }
                                                        }}
                                                    />
                                                </Button>
                                                <div className='flex flex-wrap gap-2'>
                                                    {field.value.map((img, index) => (
                                                        <Chip
                                                            key={index}
                                                            label={img}
                                                            onDelete={() => {
                                                                const newGallery = field.value.filter((_, i) => i !== index)

                                                                field.onChange(newGallery)
                                                            }}
                                                        />
                                                    ))}
                                                </div>
                                            </div>
                                        )}
                                    />
                                </Grid>

                                {/* Short Description */}
                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='shortDescription'
                                        control={control}
                                        rules={{ required: true }}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                multiline
                                                rows={3}
                                                label='Short Description'
                                                placeholder='Brief summary of the event...'
                                                error={Boolean(errors.shortDescription)}
                                                helperText={errors.shortDescription && 'Short Description is required'}
                                            />
                                        )}
                                    />
                                </Grid>

                                {/* Long Detail */}
                                <Grid size={{ xs: 12 }}>
                                    <Typography className='mbe-2'>Long Detail</Typography>
                                    <Controller
                                        name='longDetail'
                                        control={control}
                                        render={({ field }) => (
                                            <TiptapEditor value={field.value} onChange={field.onChange} placeholder="Full event details..." />
                                        )}
                                    />
                                </Grid>

                                {/* Publishing Status */}
                                <Grid size={{ xs: 12, sm: 12 }}>
                                    <FormControl>
                                        <FormLabel id="status-group-label" className='mbe-2'>Publishing Status</FormLabel>
                                        <Controller
                                            name="status"
                                            control={control}
                                            render={({ field }) => (
                                                <RadioGroup
                                                    row
                                                    aria-labelledby="status-group-label"
                                                    {...field}
                                                >
                                                    <FormControlLabel value="draft" control={<Radio />} label="Draft" />
                                                    <FormControlLabel value="published" control={<Radio />} label="Publish Now" />
                                                    <FormControlLabel value="scheduled" control={<Radio />} label="Schedule" />
                                                </RadioGroup>
                                            )}
                                        />
                                    </FormControl>
                                </Grid>

                                {statusValue === 'scheduled' && (
                                    <Grid size={{ xs: 12, sm: 6 }}>
                                        <Controller
                                            name="scheduledDate"
                                            control={control}
                                            render={({ field }) => (
                                                <div className='flex flex-col gap-2'>
                                                    <InputLabel>Scheduled Date & Time</InputLabel>
                                                    <DatePicker
                                                        selected={field.value}
                                                        onChange={(date) => field.onChange(date)}
                                                        showTimeSelect
                                                        dateFormat="Pp"
                                                        className='w-full border rounded p-2'
                                                        customInput={<TextField fullWidth size='small' />}
                                                    />
                                                </div>
                                            )}
                                        />
                                    </Grid>
                                )}
                            </Grid>
                        </CardContent>
                    </Card>
                </Grid>

                <Grid size={{ xs: 12 }} className='flex justify-end pbe-10 gap-4'>
                    {isDrawer && handleClose && (
                        <Button variant='outlined' color='secondary' onClick={handleClose}>
                            Cancel
                        </Button>
                    )}
                    <Button variant='contained' size='large' type='submit'>
                        {editId ? 'Update Event' : 'Publish Event'}
                    </Button>
                </Grid>
            </Grid>

            {/* Image Preview Modal */}
            <Dialog
                open={imageModalOpen}
                onClose={() => setImageModalOpen(false)}
                maxWidth="lg"
                fullWidth
            >
                <DialogContent className='relative p-0'>
                    <IconButton
                        className='absolute top-2 right-2 z-10 bg-white hover:bg-gray-100'
                        onClick={() => setImageModalOpen(false)}
                        size='small'
                    >
                        <i className='ri-close-line text-xl' />
                    </IconButton>
                    <img
                        src={selectedImage}
                        alt='Full Size Preview'
                        className='w-full h-auto'
                    />
                </DialogContent>
            </Dialog>
        </form>
    )
}

export default EventsEditor

'use client'

// MUI Imports
import Grid from '@mui/material/Grid2'
import Card from '@mui/material/Card'
import CardContent from '@mui/material/CardContent'
import Divider from '@mui/material/Divider'
import Typography from '@mui/material/Typography'
import useMediaQuery from '@mui/material/useMediaQuery'
import type { Theme } from '@mui/material/styles'

// Third-party Imports
import classnames from 'classnames'

// Component Imports
import CustomAvatar from '@core/components/mui/Avatar'

type DataType = {
    title: string
    value: string
    icon: string
}

// Vars
const data: DataType[] = [
    {
        title: 'Total FAQs',
        value: '5',
        icon: 'ri-question-answer-line',
    }
]

const FaqCard = () => {
    // Hooks
    const isBelowMdScreen = useMediaQuery((theme: Theme) => theme.breakpoints.down('md'))
    const isSmallScreen = useMediaQuery((theme: Theme) => theme.breakpoints.down('sm'))

    return (
        <Card>
            <CardContent>
                <Grid container spacing={6}>
                    {data.map((item, index) => (
                        <Grid
                            size={{ xs: 12, sm: 6, md: 3 }}
                            key={index}
                        >
                            <div className='flex flex-col gap-1'>
                                <div className='flex justify-between'>
                                    <div className='flex flex-col gap-1'>
                                        <Typography>{item.title}</Typography>
                                        <Typography variant='h4'>{item.value}</Typography>
                                    </div>
                                    <CustomAvatar variant='rounded' size={44}>
                                        <i className={classnames(item.icon, 'text-[28px]')} />
                                    </CustomAvatar>
                                </div>
                            </div>
                        </Grid>
                    ))}
                </Grid>
            </CardContent>
        </Card>
    )
}

export default FaqCard

'use client'

// React Imports
import { useEffect, useMemo, useState } from 'react'

// MUI Imports
import Card from '@mui/material/Card'
import CardHeader from '@mui/material/CardHeader'
import Button from '@mui/material/Button'
import Divider from '@mui/material/Divider'
import IconButton from '@mui/material/IconButton'
import TablePagination from '@mui/material/TablePagination'
import Typography from '@mui/material/Typography'
import Dialog from '@mui/material/Dialog'
import DialogTitle from '@mui/material/DialogTitle'
import DialogContent from '@mui/material/DialogContent'
import DialogContentText from '@mui/material/DialogContentText'
import DialogActions from '@mui/material/DialogActions'

// Third-party Imports
import classnames from 'classnames'
import {
    createColumnHelper,
    flexRender,
    getCoreRowModel,
    useReactTable,
    getFilteredRowModel,
    getPaginationRowModel,
    getSortedRowModel
} from '@tanstack/react-table'
import type { ColumnDef } from '@tanstack/react-table'

// Type Imports
import type { FaqType } from '@/types/apps/ecommerceTypes'

// Component Imports
import FaqDrawer from './FaqDrawer'

// Style Imports
import tableStyles from '@core/styles/table.module.css'

// Column Definitions
const columnHelper = createColumnHelper<FaqType>()

const FaqListTable = ({ faqData }: { faqData?: FaqType[] }) => {
    // States
    const [data, setData] = useState(faqData || [])
    const [drawerOpen, setDrawerOpen] = useState(false)
    const [editData, setEditData] = useState<FaqType | null>(null)
    const [viewData, setViewData] = useState<FaqType | null>(null)
    const [detailsOpen, setDetailsOpen] = useState(false)

    const handleEdit = (faq: FaqType) => {
        setEditData(faq)
        setDrawerOpen(true)
    }

    const handleAdd = () => {
        setEditData(null)
        setDrawerOpen(true)
    }

    const handleView = (faq: FaqType) => {
        setViewData(faq)
        setDetailsOpen(true)
    }

    const handleDelete = (id: number) => {
        setData(data.filter(item => item.id !== id))
    }

    const columns = useMemo<ColumnDef<FaqType, any>[]>(
        () => [
            {
                id: 'serialNumber',
                header: '#',
                cell: ({ row }) => <Typography>{row.index + 1}</Typography>
            },
            columnHelper.accessor('title', {
                header: 'Title',
                cell: ({ row }) => <Typography className='font-medium' color='text.primary'>{row.original.title}</Typography>
            }),
            columnHelper.accessor('description', {
                header: 'Description',
                cell: ({ row }) => {
                    const plainText = row.original.description.replace(/<[^>]+>/g, '')

                    return (
                        <Typography>
                            {plainText.length > 50
                                ? `${plainText.substring(0, 50)}...`
                                : plainText}
                        </Typography>
                    )
                }
            }),
            columnHelper.display({
                id: 'actions',
                header: 'Actions',
                cell: ({ row }) => (
                    <div className='flex items-center'>
                        <IconButton size='small' onClick={() => handleView(row.original)} title="View Complete">
                            <i className='ri-eye-line text-[22px] text-textSecondary' />
                        </IconButton>
                        <IconButton size='small' onClick={() => handleEdit(row.original)} title="Edit">
                            <i className='ri-edit-box-line text-[22px] text-textSecondary' />
                        </IconButton>
                        <IconButton size='small' onClick={() => handleDelete(row.original.id)} title="Delete">
                            <i className='ri-delete-bin-line text-[22px] text-error' />
                        </IconButton>
                    </div>
                )
            })
        ],
        // eslint-disable-next-line react-hooks/exhaustive-deps
        [data]
    )

    const table = useReactTable({
        data,
        columns,
        getCoreRowModel: getCoreRowModel(),
        getSortedRowModel: getSortedRowModel(),
        getPaginationRowModel: getPaginationRowModel(),
        initialState: {
            pagination: {
                pageSize: 10
            }
        }
    })

    return (
        <>
            <Card>
                <CardHeader
                    title='FAQ List'
                    action={
                        <Button
                            variant='contained'
                            startIcon={<i className='ri-add-line' />}
                            onClick={handleAdd}
                        >
                            Add FAQ
                        </Button>
                    }
                />
                <div className='overflow-x-auto'>
                    <table className={tableStyles.table}>
                        <thead>
                            {table.getHeaderGroups().map(headerGroup => (
                                <tr key={headerGroup.id}>
                                    {headerGroup.headers.map(header => (
                                        <th key={header.id}>
                                            {header.isPlaceholder ? null : (
                                                <div
                                                    className={classnames({
                                                        'flex items-center': header.column.getIsSorted(),
                                                        'cursor-pointer select-none': header.column.getCanSort()
                                                    })}
                                                    onClick={header.column.getToggleSortingHandler()}
                                                >
                                                    {flexRender(header.column.columnDef.header, header.getContext())}
                                                    {{
                                                        asc: <i className='ri-arrow-up-s-line text-xl' />,
                                                        desc: <i className='ri-arrow-down-s-line text-xl' />
                                                    }[header.column.getIsSorted() as 'asc' | 'desc'] ?? null}
                                                </div>
                                            )}
                                        </th>
                                    ))}
                                </tr>
                            ))}
                        </thead>
                        {table.getRowModel().rows.length === 0 ? (
                            <tbody>
                                <tr>
                                    <td colSpan={table.getVisibleFlatColumns().length} className='text-center'>
                                        No data available
                                    </td>
                                </tr>
                            </tbody>
                        ) : (
                            <tbody>
                                {table
                                    .getRowModel()
                                    .rows.slice(0, table.getState().pagination.pageSize)
                                    .map(row => {
                                        return (
                                            <tr key={row.id}>
                                                {row.getVisibleCells().map(cell => (
                                                    <td key={cell.id}>{flexRender(cell.column.columnDef.cell, cell.getContext())}</td>
                                                ))}
                                            </tr>
                                        )
                                    })}
                            </tbody>
                        )}
                    </table>
                </div>
                <TablePagination
                    rowsPerPageOptions={[10, 25, 50]}
                    component='div'
                    className='border-bs'
                    count={table.getFilteredRowModel().rows.length}
                    rowsPerPage={table.getState().pagination.pageSize}
                    page={table.getState().pagination.pageIndex}
                    onPageChange={(_, page) => {
                        table.setPageIndex(page)
                    }}
                    onRowsPerPageChange={e => table.setPageSize(Number(e.target.value))}
                />
            </Card>

            <FaqDrawer
                open={drawerOpen}
                handleClose={() => setDrawerOpen(false)}
                faqData={editData}
                setData={setData}
                data={data}
            />

            <Dialog
                open={detailsOpen}
                onClose={() => setDetailsOpen(false)}
                aria-labelledby="alert-dialog-title"
                aria-describedby="alert-dialog-description"
            >
                <DialogTitle id="alert-dialog-title">
                    {viewData?.title}
                </DialogTitle>
                <DialogContent>
                    <div
                        id="alert-dialog-description"
                        dangerouslySetInnerHTML={{ __html: viewData?.description || '' }}
                    />
                </DialogContent>
                <DialogActions>
                    <Button onClick={() => setDetailsOpen(false)} autoFocus>
                        Close
                    </Button>
                </DialogActions>
            </Dialog>
        </>
    )
}

export default FaqListTable

'use client'

// React Imports
import { useState, useEffect } from 'react'

// MUI Imports
import Card from '@mui/material/Card'
import CardContent from '@mui/material/CardContent'
import CardHeader from '@mui/material/CardHeader'
import Grid from '@mui/material/Grid2'
import Button from '@mui/material/Button'
import IconButton from '@mui/material/IconButton'
import Typography from '@mui/material/Typography'
import Dialog from '@mui/material/Dialog'
import DialogContent from '@mui/material/DialogContent'

// Third-party Imports
import { useForm, Controller } from 'react-hook-form'

type FormData = {
    galleryImages: string[]
}

const fileToBase64 = (file: File): Promise<string> => {
    return new Promise((resolve, reject) => {
        const reader = new FileReader()

        reader.readAsDataURL(file)
        reader.onload = () => resolve(reader.result as string)
        reader.onerror = (error) => reject(error)
    })
}

const GalleryLandingSettings = () => {
    // Modal state for image preview
    const [imageModalOpen, setImageModalOpen] = useState(false)
    const [selectedImage, setSelectedImage] = useState('')

    // Hooks
    const {
        control,
        handleSubmit,
        reset,
        setValue
    } = useForm<FormData>({
        defaultValues: {
            galleryImages: []
        }
    })

    // Load saved data
    useEffect(() => {
        const savedData = localStorage.getItem('gallery-landing-settings')

        if (savedData) {
            const parsedData = JSON.parse(savedData)

            reset(parsedData)
        }
    }, [reset])

    const onSubmit = (data: FormData) => {
        localStorage.setItem('gallery-landing-settings', JSON.stringify(data))
        alert('Gallery settings saved successfully!')
    }

    return (
        <form onSubmit={handleSubmit(onSubmit)}>
            <Grid container spacing={6}>
                <Grid size={{ xs: 12 }}>
                    <Card>
                        <CardHeader title='Gallery Images' />
                        <CardContent>
                            <Grid container spacing={5}>
                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='galleryImages'
                                        control={control}
                                        render={({ field }) => (
                                            <div className='flex flex-col gap-4'>
                                                <div className='flex items-center gap-4'>
                                                    <Button component='label' variant='outlined' htmlFor='gallery-images-upload'>
                                                        Upload Images
                                                        <input
                                                            hidden
                                                            id='gallery-images-upload'
                                                            type='file'
                                                            accept='image/*'
                                                            multiple
                                                            onChange={async (event) => {
                                                                const { files } = event.target

                                                                if (files && files.length !== 0) {
                                                                    const base64Promises = Array.from(files).map(file => fileToBase64(file))
                                                                    const base64Images = await Promise.all(base64Promises)

                                                                    field.onChange([...field.value, ...base64Images])
                                                                }
                                                            }}
                                                        />
                                                    </Button>
                                                    {field.value.length > 0 && (
                                                        <Typography variant='body2' color='text.secondary'>
                                                            {field.value.length} image{field.value.length > 1 ? 's' : ''} uploaded
                                                        </Typography>
                                                    )}
                                                </div>

                                                {/* Image Grid */}
                                                {field.value.length > 0 && (
                                                    <div className='grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-6 gap-4'>
                                                        {field.value.map((img, index) => (
                                                            <div key={index} className='relative group'>
                                                                <div
                                                                    className='border rounded p-2 cursor-pointer hover:opacity-80 transition-opacity'
                                                                    onClick={() => {
                                                                        setSelectedImage(img)
                                                                        setImageModalOpen(true)
                                                                    }}
                                                                >
                                                                    <img
                                                                        src={img}
                                                                        alt={`Gallery ${index + 1}`}
                                                                        className='w-32 h-32 object-cover rounded'
                                                                    />
                                                                </div>
                                                                <IconButton
                                                                    size='small'
                                                                    color='error'
                                                                    className='absolute top-0 right-0 bg-white hover:bg-gray-100'
                                                                    onClick={() => {
                                                                        const newImages = field.value.filter((_, i) => i !== index)

                                                                        field.onChange(newImages)
                                                                    }}
                                                                >
                                                                    <i className='ri-delete-bin-line' />
                                                                </IconButton>
                                                            </div>
                                                        ))}
                                                    </div>
                                                )}

                                                {field.value.length === 0 && (
                                                    <Typography variant='body2' color='text.secondary' className='text-center py-8'>
                                                        No images uploaded yet. Click "Upload Images" to add images to the gallery.
                                                    </Typography>
                                                )}
                                            </div>
                                        )}
                                    />
                                </Grid>
                            </Grid>
                        </CardContent>
                    </Card>
                </Grid>

                <Grid size={{ xs: 12 }} className='flex justify-end'>
                    <Button variant='contained' type='submit' size='large'>
                        Save Changes
                    </Button>
                </Grid>
            </Grid>

            {/* Image Preview Modal */}
            <Dialog
                open={imageModalOpen}
                onClose={() => setImageModalOpen(false)}
                maxWidth="lg"
                fullWidth
            >
                <DialogContent className='relative p-0'>
                    <IconButton
                        className='absolute top-2 right-2 z-10 bg-white hover:bg-gray-100'
                        onClick={() => setImageModalOpen(false)}
                        size='small'
                    >
                        <i className='ri-close-line text-xl' />
                    </IconButton>
                    <img
                        src={selectedImage}
                        alt='Full Size Preview'
                        className='w-full h-auto'
                    />
                </DialogContent>
            </Dialog>
        </form>
    )
}

export default GalleryLandingSettings

'use client'

// React Imports
import { useState, useEffect } from 'react'

// MUI Imports
import Card from '@mui/material/Card'
import CardHeader from '@mui/material/CardHeader'
import CardContent from '@mui/material/CardContent'
import Divider from '@mui/material/Divider'
import TextField from '@mui/material/TextField'
import Button from '@mui/material/Button'
import Typography from '@mui/material/Typography'
import { styled } from '@mui/material/styles'
import type { BoxProps } from '@mui/material/Box'
import IconButton from '@mui/material/IconButton'
import List from '@mui/material/List'
import ListItem from '@mui/material/ListItem'
import Switch from '@mui/material/Switch'
import FormControlLabel from '@mui/material/FormControlLabel'

// Third-party Imports
import { useDropzone } from 'react-dropzone'
import { Controller, useForm } from 'react-hook-form'

// Component Imports
import CustomAvatar from '@core/components/mui/Avatar'
import AppReactDropzone from '@/libs/styles/AppReactDropzone'

type FileProp = {
    name: string
    type: string
    size: number
}

// Styled Dropzone Component
const Dropzone = styled(AppReactDropzone)<BoxProps>(({ theme }) => ({
    '& .dropzone': {
        minHeight: 'unset',
        padding: theme.spacing(12),
        [theme.breakpoints.down('sm')]: {
            paddingInline: theme.spacing(5)
        },
        '&+.MuiList-root .MuiListItem-root .file-name': {
            fontWeight: theme.typography.body1.fontWeight
        }
    }
}))

const HomeHeroSection = () => {
    // Local Form
    const { control, handleSubmit, setValue, reset, watch } = useForm({
        defaultValues: {
            isVisible: true,
            title: '',
            subtitle: '',
            btnText: '',
            image: null
        }
    })

    const [files, setFiles] = useState<File[]>([])
    const isVisible = watch('isVisible')

    // Load data
    useEffect(() => {
        const savedData = localStorage.getItem('home_hero_data')

        if (savedData) {
            const parsed = JSON.parse(savedData)

            reset({
                isVisible: parsed.isVisible !== undefined ? parsed.isVisible : true,
                title: parsed.title || '',
                subtitle: parsed.subtitle || '',
                btnText: parsed.btnText || '',
                image: null
            })
        }
    }, [reset])

    const { getRootProps, getInputProps } = useDropzone({
        maxFiles: 1,
        accept: {
            'image/*': ['.png', '.jpg', '.jpeg', '.gif', '.webp']
        },
        onDrop: (acceptedFiles: File[]) => {
            const file = acceptedFiles[0]

            if (file) {
                setFiles([Object.assign(file)])
                setValue('image', file as any)
            }
        }
    })

    const renderFilePreview = (file: FileProp) => {
        if (file.type.startsWith('image')) {
            return <img width={38} height={38} alt={file.name} src={URL.createObjectURL(file as any)} />
        } else {
            return <i className='ri-file-text-line' />
        }
    }

    const handleRemoveFile = () => {
        setFiles([])
        setValue('image', null)
    }

    const onSubmit = (data: any) => {
        const dataToSave = {
            isVisible: data.isVisible,
            title: data.title,
            subtitle: data.subtitle,
            btnText: data.btnText
        }

        localStorage.setItem('home_hero_data', JSON.stringify(dataToSave))

        console.log('Home Hero Saved:', data)
        alert('Home Hero Saved')
    }

    return (
        <Card>
            <form onSubmit={handleSubmit(onSubmit)}>
                <CardHeader
                    title='Hero Section'
                    action={
                        <div className="flex items-center gap-4">
                            <Controller
                                name='isVisible'
                                control={control}
                                render={({ field }) => (
                                    <FormControlLabel
                                        control={<Switch checked={field.value} onChange={field.onChange} />}
                                        label={field.value ? "Visible" : "Hidden"}
                                    />
                                )}
                            />
                            <Button variant='contained' type='submit'>
                                Save
                            </Button>
                        </div>
                    }
                />
                <Divider />
                <CardContent>
                    <div className='flex flex-col gap-6'>
                        <Controller
                            name='title'
                            control={control}
                            render={({ field }) => (
                                <TextField
                                    {...field}
                                    fullWidth
                                    label='Title'
                                    placeholder='Hero Title'
                                />
                            )}
                        />
                        <Controller
                            name='subtitle'
                            control={control}
                            render={({ field }) => (
                                <TextField
                                    {...field}
                                    fullWidth
                                    label='Subtitle'
                                    placeholder='Hero Subtitle'
                                />
                            )}
                        />
                        <Controller
                            name='btnText'
                            control={control}
                            render={({ field }) => (
                                <TextField
                                    {...field}
                                    fullWidth
                                    label='CTA Button Text'
                                    placeholder='e.g. Learn More'
                                />
                            )}
                        />

                        <div>
                            <Typography variant='caption' className='mb-2 block'>Hero Image</Typography>
                            <Dropzone>
                                <div {...getRootProps({ className: 'dropzone' })}>
                                    <input {...getInputProps()} />
                                    <div className='flex items-center flex-col gap-2 text-center'>
                                        <CustomAvatar variant='rounded' skin='light' color='secondary'>
                                            <i className='ri-upload-2-line' />
                                        </CustomAvatar>
                                        <Typography variant='h6'>Drag and Drop Your Image Here</Typography>
                                        <Typography color='text.disabled'>or</Typography>
                                        <Button variant='outlined' size='small'>
                                            Browse Image
                                        </Button>
                                    </div>
                                </div>
                                {files.length > 0 && (
                                    <List>
                                        <ListItem className='pis-4 plb-3'>
                                            <div className='file-details'>
                                                <div className='file-preview'>{renderFilePreview(files[0])}</div>
                                                <div>
                                                    <Typography className='file-name font-medium' color='text.primary'>
                                                        {files[0].name}
                                                    </Typography>
                                                    <Typography className='file-size' variant='body2'>
                                                        {(files[0].size / 1024).toFixed(2)} kb
                                                    </Typography>
                                                </div>
                                            </div>
                                            <IconButton onClick={handleRemoveFile}>
                                                <i className='ri-close-line text-xl' />
                                            </IconButton>
                                        </ListItem>
                                    </List>
                                )}
                            </Dropzone>
                        </div>
                    </div>
                </CardContent>
            </form>
        </Card>
    )
}

export default HomeHeroSection

'use client'

// React Imports
import { useState, useEffect } from 'react'

// MUI Imports
import Button from '@mui/material/Button'
import Card from '@mui/material/Card'
import CardContent from '@mui/material/CardContent'
import CardHeader from '@mui/material/CardHeader'
import Grid from '@mui/material/Grid2'
import TextField from '@mui/material/TextField'
import InputAdornment from '@mui/material/InputAdornment'
import IconButton from '@mui/material/IconButton'
import Divider from '@mui/material/Divider'
import Typography from '@mui/material/Typography'

// Third-party Imports
import { useForm, Controller, useFieldArray } from 'react-hook-form'

// Local Imports
import type { RelatedContentData } from './IndustryRelated';
import IndustryRelated from './IndustryRelated'
import TextEditor from '@/components/TextEditor'

type DynamicSection = {
    heading: string
    content: string
    image: string
}

type FormValues = {
    title: string
    slug: string
    heroImage: string
    description: string
    dynamicSections: DynamicSection[]
    relatedContent: RelatedContentData
}

export type IndustryType = {
    id: string
    updatedAt: string
} & FormValues

type Props = {
    isDrawer?: boolean
    handleClose?: () => void
    dataToEdit?: IndustryType
    onSuccess?: () => void
}

const IndustryEditor = ({ isDrawer, handleClose, dataToEdit, onSuccess }: Props) => {
    const {
        control,
        handleSubmit,
        reset,
        watch,
        setValue,
        formState: { errors }
    } = useForm<FormValues>({
        defaultValues: {
            title: '',
            slug: '',
            heroImage: '',
            description: '',
            dynamicSections: [{ heading: '', content: '', image: '' }],
            relatedContent: {
                sectionTypes: [],
                relatedBlogs: [],
                relatedServices: [],
                relatedParts: [],
                relatedProjects: []
            }
        }
    })

    const { fields, append, remove } = useFieldArray({
        control,
        name: 'dynamicSections'
    })

    const relatedContentValue = watch('relatedContent')
    const titleValue = watch('title')

    // Auto-generate slug from title
    useEffect(() => {
        if (titleValue && !watch('slug')) {
            const generatedSlug = titleValue
                .toLowerCase()
                .replace(/[^a-z0-9]+/g, '-')
                .replace(/(^-|-$)+/g, '')

            setValue('slug', generatedSlug)
        }
    }, [titleValue, setValue, watch])

    useEffect(() => {
        if (dataToEdit) {
            reset({
                title: dataToEdit.title || '',
                slug: dataToEdit.slug || '',
                heroImage: dataToEdit.heroImage || '',
                description: dataToEdit.description || '',
                dynamicSections: dataToEdit.dynamicSections?.length ? dataToEdit.dynamicSections : [{ heading: '', content: '', image: '' }],
                relatedContent: dataToEdit.relatedContent || {
                    sectionTypes: [],
                    relatedBlogs: [],
                    relatedServices: [],
                    relatedParts: [],
                    relatedProjects: []
                }
            })
        }
    }, [dataToEdit, reset])

    const onSubmit = (data: FormValues) => {
        const savedIndustries = JSON.parse(localStorage.getItem('industry-list') || '[]')
        const timestamp = new Date().toISOString()

        let newIndustryList

        if (dataToEdit) {
            newIndustryList = savedIndustries.map((item: IndustryType) =>
                item.id === dataToEdit.id ? { ...item, ...data, updatedAt: timestamp } : item
            )
        } else {
            const newItem = {
                id: Date.now().toString(),
                ...data,
                updatedAt: timestamp
            }

            newIndustryList = [...savedIndustries, newItem]
        }

        localStorage.setItem('industry-list', JSON.stringify(newIndustryList))

        if (onSuccess) onSuccess()
        if (handleClose) handleClose()
    }

    const handleRelatedContentSave = (data: RelatedContentData) => {
        setValue('relatedContent', data)
    }

    return (
        <form onSubmit={handleSubmit(onSubmit)}>
            <Grid container spacing={6}>
                <Grid size={{ xs: 12 }}>
                    <Card className={isDrawer ? 'shadow-none border-none' : ''}>
                        {!isDrawer && <CardHeader title='Industry Editor' />}
                        <CardContent className={isDrawer ? 'p-0' : ''}>
                            <Grid container spacing={5}>
                                {/* Title */}
                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='title'
                                        control={control}
                                        rules={{ required: true }}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                label='Title'
                                                placeholder='Industry Title...'
                                                error={Boolean(errors.title)}
                                                helperText={errors.title && 'Title is required'}
                                            />
                                        )}
                                    />
                                </Grid>

                                {/* Slug */}
                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='slug'
                                        control={control}
                                        rules={{ required: true }}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                label='Page Slug'
                                                placeholder='industry-title'
                                                error={Boolean(errors.slug)}
                                                helperText={errors.slug && 'Slug is required'}
                                            />
                                        )}
                                    />
                                </Grid>

                                {/* Hero Image */}
                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='heroImage'
                                        control={control}
                                        render={({ field }) => (
                                            <div className='flex items-center gap-4'>
                                                <TextField
                                                    {...field}
                                                    size='small'
                                                    fullWidth
                                                    placeholder='No file chosen'
                                                    variant='outlined'
                                                    label='Hero Image'
                                                    slotProps={{
                                                        input: {
                                                            endAdornment: field.value ? (
                                                                <InputAdornment position='end'>
                                                                    <IconButton size='small' edge='end' onClick={() => field.onChange('')}>
                                                                        <i className='ri-close-line' />
                                                                    </IconButton>
                                                                </InputAdornment>
                                                            ) : null
                                                        }
                                                    }}
                                                />
                                                <Button component='label' variant='outlined' htmlFor='industry-hero-image' className='min-is-fit'>
                                                    Choose Image
                                                    <input
                                                        hidden
                                                        id='industry-hero-image'
                                                        type='file'
                                                        accept='image/*'
                                                        onChange={(event) => {
                                                            const { files } = event.target

                                                            if (files && files.length !== 0) {
                                                                field.onChange(files[0].name)
                                                            }
                                                        }}
                                                    />
                                                </Button>
                                            </div>
                                        )}
                                    />
                                </Grid>

                                {/* Description */}
                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='description'
                                        control={control}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                multiline
                                                rows={3}
                                                label='Description'
                                                placeholder='Brief description...'
                                            />
                                        )}
                                    />
                                </Grid>

                                {/* Dynamic Sections */}
                                <Grid size={{ xs: 12 }}>
                                    <Divider textAlign='left'>Dynamic Sections</Divider>
                                </Grid>

                                {fields.map((item, index) => (
                                    <Grid size={{ xs: 12 }} key={item.id} className='border rounded p-4 relative'>
                                        <div className='flex justify-between items-center mbe-4'>
                                            <Typography variant='h6'>Section {index + 1}</Typography>
                                            <IconButton size='small' color='error' onClick={() => remove(index)}>
                                                <i className='ri-delete-bin-line' />
                                            </IconButton>
                                        </div>
                                        <Grid container spacing={4}>
                                            <Grid size={{ xs: 12 }}>
                                                <Controller
                                                    name={`dynamicSections.${index}.heading`}
                                                    control={control}
                                                    rules={{ required: true }}
                                                    render={({ field }) => (
                                                        <TextField
                                                            {...field}
                                                            fullWidth
                                                            label='Heading'
                                                            placeholder='Section Heading...'
                                                            error={Boolean(errors.dynamicSections?.[index]?.heading)}
                                                        />
                                                    )}
                                                />
                                            </Grid>
                                            <Grid size={{ xs: 12 }}>
                                                <Controller
                                                    name={`dynamicSections.${index}.image`}
                                                    control={control}
                                                    render={({ field }) => (
                                                        <div className='flex items-center gap-4'>
                                                            <TextField
                                                                {...field}
                                                                size='small'
                                                                fullWidth
                                                                placeholder='No file chosen'
                                                                variant='outlined'
                                                                label='Section Image'
                                                            />
                                                            <Button component='label' variant='outlined' htmlFor={`industry-section-image-${index}`} className='min-is-fit'>
                                                                Choose
                                                                <input
                                                                    hidden
                                                                    id={`industry-section-image-${index}`}
                                                                    type='file'
                                                                    accept='image/*'
                                                                    onChange={(event) => {
                                                                        const { files } = event.target

                                                                        if (files && files.length !== 0) {
                                                                            field.onChange(files[0].name)
                                                                        }
                                                                    }}
                                                                />
                                                            </Button>
                                                        </div>
                                                    )}
                                                />
                                            </Grid>
                                            <Grid size={{ xs: 12 }}>
                                                <Typography className='mbe-2'>Content</Typography>
                                                <Controller
                                                    name={`dynamicSections.${index}.content`}
                                                    control={control}
                                                    render={({ field }) => (
                                                        <TextEditor
                                                            value={field.value}
                                                            onChange={field.onChange}
                                                        />
                                                    )}
                                                />
                                            </Grid>
                                        </Grid>
                                    </Grid>
                                ))}

                                <Grid size={{ xs: 12 }}>
                                    <Button variant='outlined' onClick={() => append({ heading: '', content: '', image: '' })}>
                                        Add Section
                                    </Button>
                                </Grid>

                                <Grid size={{ xs: 12 }}>
                                    <Divider textAlign='left'>Related Content</Divider>
                                </Grid>

                                {/* Related Content */}
                                <Grid size={{ xs: 12 }}>
                                    <IndustryRelated
                                        industryData={relatedContentValue}
                                        onSave={handleRelatedContentSave}
                                    />
                                </Grid>

                            </Grid>
                        </CardContent>
                    </Card>
                </Grid>

                <Grid size={{ xs: 12 }} className='flex justify-end pbe-10 gap-4'>
                    {isDrawer && handleClose && (
                        <Button variant='outlined' color='secondary' onClick={handleClose}>
                            Cancel
                        </Button>
                    )}
                    <Button variant='contained' size='large' type='submit'>
                        {dataToEdit ? 'Update Industry' : 'Save Industry'}
                    </Button>
                </Grid>
            </Grid>
        </form>
    )
}

export default IndustryEditor

'use client'

// React Imports
import { useState, useEffect } from 'react'

// MUI Imports
import Button from '@mui/material/Button'
import Card from '@mui/material/Card'
import CardContent from '@mui/material/CardContent'
import CardHeader from '@mui/material/CardHeader'
import Grid from '@mui/material/Grid2'
import TextField from '@mui/material/TextField'
import InputAdornment from '@mui/material/InputAdornment'
import IconButton from '@mui/material/IconButton'
import Divider from '@mui/material/Divider'

// Third-party Imports
import { useForm, Controller } from 'react-hook-form'

// Local Imports
import type { RelatedContentData } from './IndustryRelated';
import IndustryRelated from './IndustryRelated'

type LandingFormValues = {
    heroImage: string
    title: string
    shortLine: string
    relatedContent: RelatedContentData
}

type Props = {
    handleClose?: () => void
}

const IndustryLandingSettings = ({ handleClose }: Props) => {
    const {
        control,
        handleSubmit,
        reset,
        setValue,
        watch,
        formState: { errors }
    } = useForm<LandingFormValues>({
        defaultValues: {
            heroImage: '',
            title: '',
            shortLine: '',
            relatedContent: {
                sectionTypes: [],
                relatedBlogs: [],
                relatedServices: [],
                relatedParts: [],
                relatedProjects: []
            }
        }
    })

    const relatedContentValue = watch('relatedContent')

    useEffect(() => {
        const savedSettings = localStorage.getItem('industry-landing')

        if (savedSettings) {
            reset(JSON.parse(savedSettings))
        }
    }, [reset])

    const onSubmit = (data: LandingFormValues) => {
        localStorage.setItem('industry-landing', JSON.stringify(data))
        alert('Industry Landing Page Settings Saved!')
        if (handleClose) handleClose()
    }

    const handleRelatedContentSave = (data: RelatedContentData) => {
        setValue('relatedContent', data)
    }

    return (
        <form onSubmit={handleSubmit(onSubmit)}>
            <Grid container spacing={6}>
                <Grid size={{ xs: 12 }}>
                    <Card className='shadow-none border-none'>
                        <CardHeader title='Industry Page Configuration' />
                        <CardContent className='p-0'>
                            <Grid container spacing={5}>
                                {/* Title */}
                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='title'
                                        control={control}
                                        rules={{ required: true }}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                label='Page Title'
