'use client'

// React Imports
import { useState, useEffect } from 'react'

// Next Imports
import { useRouter } from 'next/navigation'

// MUI Imports
import Button from '@mui/material/Button'
import Card from '@mui/material/Card'
import CardContent from '@mui/material/CardContent'
import CardHeader from '@mui/material/CardHeader'
import Grid from '@mui/material/Grid2'
import TextField from '@mui/material/TextField'
import InputAdornment from '@mui/material/InputAdornment'
import IconButton from '@mui/material/IconButton'
import Divider from '@mui/material/Divider'
import Typography from '@mui/material/Typography'

// Third-party Imports
import { useForm, Controller, useFieldArray } from 'react-hook-form'

// Local Imports
import TextEditor from '@/components/TextEditor'

type DetailSection = {
    heading: string
    detail: string
}

type FormValues = {
    locationName: string
    slug: string
    heroTitle: string
    heroImage: string
    startingHeading: string
    startingText: string
    startingImage: string
    detailSections: DetailSection[]
    closingHeading: string
    closingDetail: string
    contactPhone: string
    contactEmail: string
    contactAddress: string
}

export type LocationType = {
    id: string
    updatedAt: string
} & FormValues

type Props = {
    isDrawer?: boolean
    handleClose?: () => void
    dataToEdit?: LocationType
    onSuccess?: () => void
}

const LocationEditor = ({ isDrawer, handleClose, dataToEdit, onSuccess }: Props) => {
    const {
        control,
        handleSubmit,
        reset,
        watch,
        setValue,
        formState: { errors }
    } = useForm<FormValues>({
        defaultValues: {
            locationName: '',
            slug: '',
            heroTitle: '',
            heroImage: '',
            startingHeading: '',
            startingText: '',
            startingImage: '',
            detailSections: [{ heading: '', detail: '' }],
            closingHeading: '',
            closingDetail: '',
            contactPhone: '',
            contactEmail: '',
            contactAddress: ''
        }
    })

    const { fields, append, remove } = useFieldArray({
        control,
        name: 'detailSections'
    })

    const locationNameValue = watch('locationName')

    // Auto-generate slug from locationName if slug is empty
    useEffect(() => {
        if (locationNameValue && !watch('slug')) {
            const generatedSlug = locationNameValue
                .toLowerCase()
                .replace(/[^a-z0-9]+/g, '-')
                .replace(/(^-|-$)+/g, '')

            setValue('slug', generatedSlug)
        }
    }, [locationNameValue, setValue, watch])

    useEffect(() => {
        if (dataToEdit) {
            reset({
                locationName: dataToEdit.locationName || '',
                slug: dataToEdit.slug || '',
                heroTitle: dataToEdit.heroTitle || '',
                heroImage: dataToEdit.heroImage || '',
                startingHeading: dataToEdit.startingHeading || '',
                startingText: dataToEdit.startingText || '',
                startingImage: dataToEdit.startingImage || '',
                detailSections: dataToEdit.detailSections?.length ? dataToEdit.detailSections : [{ heading: '', detail: '' }],
                closingHeading: dataToEdit.closingHeading || '',
                closingDetail: dataToEdit.closingDetail || '',
                contactPhone: dataToEdit.contactPhone || '',
                contactEmail: dataToEdit.contactEmail || '',
                contactAddress: dataToEdit.contactAddress || ''
            })
        } else {
            reset({
                locationName: '',
                slug: '',
                heroTitle: '',
                heroImage: '',
                startingHeading: '',
                startingText: '',
                startingImage: '',
                detailSections: [{ heading: '', detail: '' }],
                closingHeading: '',
                closingDetail: '',
                contactPhone: '',
                contactEmail: '',
                contactAddress: ''
            })
        }
    }, [dataToEdit, reset])

    const onSubmit = (data: FormValues) => {
        const savedLocations = JSON.parse(localStorage.getItem('location-list') || '[]')
        const timestamp = new Date().toISOString()

        let newLocationsList

        if (dataToEdit) {
            newLocationsList = savedLocations.map((loc: LocationType) =>
                loc.id === dataToEdit.id ? { ...loc, ...data, updatedAt: timestamp } : loc
            )
        } else {
            const newLocation = {
                id: Date.now().toString(),
                ...data,
                updatedAt: timestamp
            }

            newLocationsList = [...savedLocations, newLocation]
        }

        localStorage.setItem('location-list', JSON.stringify(newLocationsList))

        if (onSuccess) onSuccess()
        if (handleClose) handleClose()
    }

    return (
        <form onSubmit={handleSubmit(onSubmit)}>
            <Grid container spacing={6}>
                <Grid size={{ xs: 12 }}>
                    <Card className={isDrawer ? 'shadow-none border-none' : ''}>
                        {!isDrawer && <CardHeader title='Location Editor' />}
                        <CardContent className={isDrawer ? 'p-0' : ''}>
                            <Grid container spacing={5}>
                                {/* Location Info */}
                                <Grid size={{ xs: 12 }}>
                                    <Divider textAlign='left'>Location Info</Divider>
                                </Grid>

                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='locationName'
                                        control={control}
                                        rules={{ required: true }}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                label='Location Name'
                                                placeholder='e.g. New York'
                                                error={Boolean(errors.locationName)}
                                                helperText={errors.locationName && 'Location Name is required'}
                                            />
                                        )}
                                    />
                                </Grid>

                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='slug'
                                        control={control}
                                        rules={{ required: true }}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                label='Slug'
                                                placeholder='new-york'
                                                error={Boolean(errors.slug)}
                                                helperText={errors.slug && 'Slug is required'}
                                            />
                                        )}
                                    />
                                </Grid>

                                {/* Hero Section */}
                                <Grid size={{ xs: 12 }}>
                                    <Divider textAlign='left'>Hero Section</Divider>
                                </Grid>

                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='heroTitle'
                                        control={control}
                                        rules={{ required: true }}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                label='Hero Title'
                                                placeholder='Location Name or Title...'
                                                error={Boolean(errors.heroTitle)}
                                                helperText={errors.heroTitle && 'Hero Title is required'}
                                            />
                                        )}
                                    />
                                </Grid>

                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='heroImage'
                                        control={control}
                                        render={({ field }) => (
                                            <div className='flex items-center gap-4'>
                                                <TextField
                                                    {...field}
                                                    size='small'
                                                    fullWidth
                                                    placeholder='No file chosen'
                                                    variant='outlined'
                                                    label='Hero Image'
                                                    slotProps={{
                                                        input: {
                                                            endAdornment: field.value ? (
                                                                <InputAdornment position='end'>
                                                                    <IconButton size='small' edge='end' onClick={() => field.onChange('')}>
                                                                        <i className='ri-close-line' />
                                                                    </IconButton>
                                                                </InputAdornment>
                                                            ) : null
                                                        }
                                                    }}
                                                />
                                                <Button component='label' variant='outlined' htmlFor='loc-hero-image' className='min-is-fit'>
                                                    Choose Image
                                                    <input
                                                        hidden
                                                        id='loc-hero-image'
                                                        type='file'
                                                        accept='image/*'
                                                        onChange={(event) => {
                                                            const { files } = event.target

                                                            if (files && files.length !== 0) {
                                                                field.onChange(files[0].name)
                                                            }
                                                        }}
                                                    />
                                                </Button>
                                            </div>
                                        )}
                                    />
                                </Grid>

                                {/* Starting Section */}
                                <Grid size={{ xs: 12 }}>
                                    <Divider textAlign='left'>Starting Section</Divider>
                                </Grid>

                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='startingHeading'
                                        control={control}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                label='Starting Heading'
                                                placeholder='Introduction Title...'
                                            />
                                        )}
                                    />
                                </Grid>

                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='startingText'
                                        control={control}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                multiline
                                                rows={3}
                                                label='Starting Text'
                                                placeholder='Introduction text...'
                                            />
                                        )}
                                    />
                                </Grid>

                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='startingImage'
                                        control={control}
                                        render={({ field }) => (
                                            <div className='flex items-center gap-4'>
                                                <TextField
                                                    {...field}
                                                    size='small'
                                                    fullWidth
                                                    placeholder='No file chosen'
                                                    variant='outlined'
                                                    label='Starting Section Image'
                                                    slotProps={{
                                                        input: {
                                                            endAdornment: field.value ? (
                                                                <InputAdornment position='end'>
                                                                    <IconButton size='small' edge='end' onClick={() => field.onChange('')}>
                                                                        <i className='ri-close-line' />
                                                                    </IconButton>
                                                                </InputAdornment>
                                                            ) : null
                                                        }
                                                    }}
                                                />
                                                <Button component='label' variant='outlined' htmlFor='loc-start-image' className='min-is-fit'>
                                                    Choose Image
                                                    <input
                                                        hidden
                                                        id='loc-start-image'
                                                        type='file'
                                                        accept='image/*'
                                                        onChange={(event) => {
                                                            const { files } = event.target

                                                            if (files && files.length !== 0) {
                                                                field.onChange(files[0].name)
                                                            }
                                                        }}
                                                    />
                                                </Button>
                                            </div>
                                        )}
                                    />
                                </Grid>

                                {/* Details Section (Renamed from FAQs) */}
                                <Grid size={{ xs: 12 }}>
                                    <Divider textAlign='left'>Details Sections</Divider>
                                </Grid>

                                {fields.map((item, index) => (
                                    <Grid size={{ xs: 12 }} key={item.id} className='border rounded p-4 relative'>
                                        <div className='flex justify-between items-center mbe-4'>
                                            <Typography variant='h6'>Detail Section {index + 1}</Typography>
                                            <IconButton size='small' color='error' onClick={() => remove(index)}>
                                                <i className='ri-delete-bin-line' />
                                            </IconButton>
                                        </div>
                                        <Grid container spacing={4}>
                                            <Grid size={{ xs: 12 }}>
                                                <Controller
                                                    name={`detailSections.${index}.heading`}
                                                    control={control}
                                                    rules={{ required: true }}
                                                    render={({ field }) => (
                                                        <TextField
                                                            {...field}
                                                            fullWidth
                                                            label={`Heading`}
                                                            placeholder='Details Heading...'
                                                            error={Boolean(errors.detailSections?.[index]?.heading)}
                                                        />
                                                    )}
                                                />
                                            </Grid>
                                            <Grid size={{ xs: 12 }}>
                                                <Typography className='mbe-2'>Detail Content</Typography>
                                                <Controller
                                                    name={`detailSections.${index}.detail`}
                                                    control={control}
                                                    render={({ field }) => (
                                                        <TextEditor
                                                            value={field.value}
                                                            onChange={field.onChange}
                                                        />
                                                    )}
                                                />
                                            </Grid>
                                        </Grid>
                                    </Grid>
                                ))}

                                <Grid size={{ xs: 12 }}>
                                    <Button variant='outlined' onClick={() => append({ heading: '', detail: '' })}>
                                        Add Details Section
                                    </Button>
                                </Grid>

                                {/* Closing Section */}
                                <Grid size={{ xs: 12 }}>
                                    <Divider textAlign='left'>Closing Section</Divider>
                                </Grid>

                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='closingHeading'
                                        control={control}
                                        rules={{ required: true }}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                label='Closing Heading'
                                                placeholder='Conclusion Title...'
                                                error={Boolean(errors.closingHeading)}
                                                helperText={errors.closingHeading && 'Closing Heading is required'}
                                            />
                                        )}
                                    />
                                </Grid>

                                <Grid size={{ xs: 12 }}>
                                    <Typography className='mbe-2'>Closing Detail</Typography>
                                    <Controller
                                        name='closingDetail'
                                        control={control}
                                        render={({ field }) => (
                                            <TextEditor
                                                value={field.value}
                                                onChange={field.onChange}
                                            />
                                        )}
                                    />
                                </Grid>

                                {/* Contact Details */}
                                <Grid size={{ xs: 12 }}>
                                    <Divider textAlign='left'>Contact Details</Divider>
                                </Grid>

                                <Grid size={{ xs: 12, sm: 6 }}>
                                    <Controller
                                        name='contactPhone'
                                        control={control}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                label='Phone Number'
                                                placeholder='+1 234 567 8900'
                                            />
                                        )}
                                    />
                                </Grid>

                                <Grid size={{ xs: 12, sm: 6 }}>
                                    <Controller
                                        name='contactEmail'
                                        control={control}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                label='Email Address'
                                                placeholder='info@example.com'
                                            />
                                        )}
                                    />
                                </Grid>

                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='contactAddress'
                                        control={control}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                multiline
                                                rows={2}
                                                label='Address'
                                                placeholder='123 Main St, City, Country'
                                            />
                                        )}
                                    />
                                </Grid>

                            </Grid>
                        </CardContent>
                    </Card>
                </Grid>

                <Grid size={{ xs: 12 }} className='flex justify-end pbe-10 gap-4'>
                    {isDrawer && handleClose && (
                        <Button variant='outlined' color='secondary' onClick={handleClose}>
                            Cancel
                        </Button>
                    )}
                    <Button variant='contained' size='large' type='submit'>
                        {dataToEdit ? 'Update Location' : 'Save Location'}
                    </Button>
                </Grid>
            </Grid>
        </form>
    )
}

export default LocationEditor

'use client'

// React Imports
import { useState, useEffect, useMemo } from 'react'

// Next Imports
import Link from 'next/link'

// MUI Imports
import Card from '@mui/material/Card'
import CardHeader from '@mui/material/CardHeader'
import Button from '@mui/material/Button'
import Typography from '@mui/material/Typography'
import Chip from '@mui/material/Chip'
import IconButton from '@mui/material/IconButton'
import Drawer from '@mui/material/Drawer'
import Table from '@mui/material/Table'
import TableBody from '@mui/material/TableBody'
import TableCell from '@mui/material/TableCell'
import TableContainer from '@mui/material/TableContainer'
import TableHead from '@mui/material/TableHead'
import TableRow from '@mui/material/TableRow'
import TablePagination from '@mui/material/TablePagination'
import TextField from '@mui/material/TextField'
import InputAdornment from '@mui/material/InputAdornment'

// Third-party Imports
import classnames from 'classnames'

// Type Imports
import type { LocationType } from '../LocationEditor'

// Component Imports
import LocationEditor from '../LocationEditor'

const LocationList = () => {
    // States
    const [data, setData] = useState<LocationType[]>([])
    const [filteredData, setFilteredData] = useState<LocationType[]>([])
    const [searchTerm, setSearchTerm] = useState('')
    const [page, setPage] = useState(0)
    const [rowsPerPage, setRowsPerPage] = useState(10)
    const [editorOpen, setEditorOpen] = useState(false)
    const [editingNode, setEditingNode] = useState<LocationType | undefined>(undefined)

    // Load data from localStorage
    const loadData = () => {
        const savedData = localStorage.getItem('location-list')

        if (savedData) {
            const parsedData = JSON.parse(savedData)

            setData(parsedData)
            setFilteredData(parsedData)
        }
    }

    useEffect(() => {
        loadData()
    }, [])

    // Filter data based on search term
    useEffect(() => {
        const result = data.filter(item =>
            item.heroTitle.toLowerCase().includes(searchTerm.toLowerCase()) ||
            item.contactAddress.toLowerCase().includes(searchTerm.toLowerCase())
        )

        setFilteredData(result)
        setPage(0)
    }, [searchTerm, data])

    // Handle Pagination
    const handleChangePage = (event: unknown, newPage: number) => {
        setPage(newPage)
    }

    const handleChangeRowsPerPage = (event: React.ChangeEvent<HTMLInputElement>) => {
        setRowsPerPage(+event.target.value)
        setPage(0)
    }

    // Actions
    const handleAddNew = () => {
        setEditingNode(undefined)
        setEditorOpen(true)
    }

    const handleEdit = (item: LocationType) => {
        setEditingNode(item)
        setEditorOpen(true)
    }

    const handleDelete = (id: string) => {
        if (confirm('Are you sure you want to delete this location?')) {
            const newData = data.filter(item => item.id !== id)

            localStorage.setItem('location-list', JSON.stringify(newData))
            loadData()
        }
    }

    const handleEditorSuccess = () => {
        loadData()
        setEditorOpen(false)
    }

    return (
        <Card>
            <CardHeader
                title='Locations'
                action={
                    <div className='flex gap-4 items-center'>
                        <TextField
                            size='small'
                            placeholder='Search Location'
                            value={searchTerm}
                            onChange={e => setSearchTerm(e.target.value)}
                            InputProps={{
                                startAdornment: (
                                    <InputAdornment position='start'>
                                        <i className='ri-search-line' />
                                    </InputAdornment>
                                )
                            }}
                        />
                        <Button variant='contained' startIcon={<i className='ri-add-line' />} onClick={handleAddNew}>
                            Add Location
                        </Button>
                    </div>
                }
            />
            <TableContainer>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell>Title</TableCell>
                            <TableCell>Address</TableCell>
                            <TableCell>Phone</TableCell>
                            <TableCell>Actions</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {filteredData.length === 0 ? (
                            <TableRow>
                                <TableCell colSpan={4} align='center'>
                                    <Typography>No locations found</Typography>
                                </TableCell>
                            </TableRow>
                        ) : (
                            filteredData
                                .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                                .map((row) => (
                                    <TableRow key={row.id}>
                                        <TableCell>
                                            <div className='flex flex-col'>
                                                <Typography variant='body1' className='font-medium'>{row.heroTitle}</Typography>
                                                {row.heroImage && <Typography variant='caption' color='textSecondary'>{row.heroImage}</Typography>}
                                            </div>
                                        </TableCell>
                                        <TableCell>{row.contactAddress}</TableCell>
                                        <TableCell>{row.contactPhone}</TableCell>
                                        <TableCell>
                                            <div className='flex gap-2'>
                                                <IconButton size='small' onClick={() => handleEdit(row)}>
                                                    <i className='ri-pencil-line' />
                                                </IconButton>
                                                <IconButton size='small' color='error' onClick={() => handleDelete(row.id)}>
                                                    <i className='ri-delete-bin-line' />
                                                </IconButton>
                                            </div>
                                        </TableCell>
                                    </TableRow>
                                ))
                        )}
                    </TableBody>
                </Table>
            </TableContainer>
            <TablePagination
                rowsPerPageOptions={[10, 25, 50]}
                component='div'
                count={filteredData.length}
                rowsPerPage={rowsPerPage}
                page={page}
                onPageChange={handleChangePage}
                onRowsPerPageChange={handleChangeRowsPerPage}
            />

            <Drawer
                open={editorOpen}
                anchor='right'
                onClose={() => setEditorOpen(false)}
                sx={{ '& .MuiDrawer-paper': { width: { xs: '100%', md: '700px' } } }}
            >
                <div className='flex items-center justify-between pli-5 plb-4 border-b'>
                    <Typography variant='h5'>{editingNode ? 'Edit Location' : 'Add New Location'}</Typography>
                    <IconButton size='small' onClick={() => setEditorOpen(false)}>
                        <i className='ri-close-line text-2xl' />
                    </IconButton>
                </div>
                <div className='p-5 overflow-y-auto'>
                    <LocationEditor
                        isDrawer
                        handleClose={() => setEditorOpen(false)}
                        onSuccess={handleEditorSuccess}
                        dataToEdit={editingNode}
                    />
                </div>
            </Drawer>
        </Card>
    )
}

export default LocationList

'use client'

// React Imports
import { useState, useEffect } from 'react'

// Next Imports
import { useRouter, useSearchParams } from 'next/navigation'

// MUI Imports
import Button from '@mui/material/Button'
import Card from '@mui/material/Card'
import CardContent from '@mui/material/CardContent'
import CardHeader from '@mui/material/CardHeader'
import Grid from '@mui/material/Grid2'
import TextField from '@mui/material/TextField'
import MenuItem from '@mui/material/MenuItem'
import Select from '@mui/material/Select'
import InputLabel from '@mui/material/InputLabel'
import FormControl from '@mui/material/FormControl'
import Typography from '@mui/material/Typography'
import Divider from '@mui/material/Divider'
import IconButton from '@mui/material/IconButton'
import InputAdornment from '@mui/material/InputAdornment'
import Chip from '@mui/material/Chip'
import OutlinedInput from '@mui/material/OutlinedInput'
import Radio from '@mui/material/Radio'
import RadioGroup from '@mui/material/RadioGroup'
import FormControlLabel from '@mui/material/FormControlLabel'
import FormLabel from '@mui/material/FormLabel'

// Third-party Imports
import { useForm, useFieldArray, Controller } from 'react-hook-form'
import { useEditor, EditorContent } from '@tiptap/react'
import { StarterKit } from '@tiptap/starter-kit'
import { Underline } from '@tiptap/extension-underline'
import { Placeholder } from '@tiptap/extension-placeholder'
import { TextAlign } from '@tiptap/extension-text-align'
import DatePicker from 'react-datepicker'

// Style Imports
import 'react-datepicker/dist/react-datepicker.css'

// Local Imports
import EditorToolbar from './EditorToolbar'
import '@/libs/styles/tiptapEditor.css'

type Section = {
    title: string
    content: string
    imageUrl: string
}

type FormValues = {
    category: string
    newsHeadline: string
    mainImage: string
    relatedNews: string[]
    sections: Section[]
    status: 'published' | 'scheduled' | 'draft'
    publishedDate: Date | null
}

type Category = {
    id: string
    title: string
}

type NewsPost = {
    id: string
    newsHeadline: string
    status: string
    publishedDate: string | null

    /* ... other fields ... */
}

type Props = {
    isDrawer?: boolean
    handleClose?: () => void
    dataToEdit?: NewsPost
    onSuccess?: () => void
}

const TiptapEditor = ({ value, onChange }: { value: string; onChange: (content: string) => void }) => {
    const editor = useEditor({
        extensions: [
            StarterKit,
            Placeholder.configure({
                placeholder: 'Write section content here...'
            }),
            TextAlign.configure({
                types: ['heading', 'paragraph']
            }),
            Underline
        ],
        content: value,
        onUpdate: ({ editor }) => {
            onChange(editor.getHTML())
        },
        immediatelyRender: false
    })

    return (
        <Card className='p-0 border shadow-none'>
            <CardContent className='p-0'>
                <EditorToolbar editor={editor} />
                <Divider className='mli-5' />
                <EditorContent editor={editor} className='bs-[135px] overflow-y-auto flex ' />
            </CardContent>
        </Card>
    )
}

const NewsEditor = ({ isDrawer, handleClose, dataToEdit, onSuccess }: Props) => {
    const [categories, setCategories] = useState<Category[]>([])
    const [allNews, setAllNews] = useState<NewsPost[]>([])
    const router = useRouter()
    const searchParams = useSearchParams()

    // Use dataToEdit id if in drawer, else URL param
    const editId = isDrawer ? dataToEdit?.id : searchParams?.get('id')

    const {
        control,
        handleSubmit,
        reset,
        setValue,
        watch,
        formState: { errors }
    } = useForm<FormValues>({
        defaultValues: {
            category: '',
            newsHeadline: '',
            mainImage: '',
            relatedNews: [],
            sections: [{ title: '', content: '', imageUrl: '' }],
            status: 'draft',
            publishedDate: new Date()
        }
    })

    const statusValue = watch('status')

    const { fields, append, remove } = useFieldArray({
        control,
        name: 'sections'
    })

    useEffect(() => {
        // Load Categories
        const savedCategories = localStorage.getItem('news-categories')

        if (savedCategories) {
            setCategories(JSON.parse(savedCategories))
        } else {
            // Default categories if none exist
            const defaultCategories = [{ id: '1', title: 'Startups' }, { id: '2', title: 'Technology' }, { id: '3', title: 'Business' }]

            setCategories(defaultCategories)
            localStorage.setItem('news-categories', JSON.stringify(defaultCategories))
        }

        const savedNews = JSON.parse(localStorage.getItem('news-posts') || '[]')

        setAllNews(savedNews)

        if (editId && (dataToEdit || !isDrawer)) {
            const postToEdit = dataToEdit || savedNews.find((post: any) => post.id === editId)

            if (postToEdit) {
                reset({
                    category: postToEdit.category || '',
                    newsHeadline: postToEdit.newsHeadline || '',
                    mainImage: postToEdit.mainImage || '',
                    relatedNews: postToEdit.relatedNews || [],
                    sections: postToEdit.sections || [{ title: '', content: '', imageUrl: '' }],
                    status: postToEdit.status || 'draft',
                    publishedDate: postToEdit.publishedDate ? new Date(postToEdit.publishedDate) : new Date()
                })
            }
        }
    }, [editId, reset, dataToEdit, isDrawer])

    const onSubmit = (data: FormValues) => {
        const savedNews = JSON.parse(localStorage.getItem('news-posts') || '[]')
        const timestamp = new Date().toISOString()

        const finalStatus = data.status

        // Logic: if user clicks "Publish now" -> date is now. If "Schedule" -> check date.
        // But here we rely on form values.

        const finalData = {
            ...data,
            publishedDate: data.publishedDate ? data.publishedDate.toISOString() : null
        }

        let newNewsList

        if (editId) {
            newNewsList = savedNews.map((post: any) =>
                post.id === editId ? { ...post, ...finalData, updatedAt: timestamp } : post
            )
        } else {
            const newPost = {
                id: Date.now().toString(),
                ...finalData,
                publishedAt: timestamp,
                updatedAt: timestamp
            }

            newNewsList = [...savedNews, newPost]
        }

        localStorage.setItem('news-posts', JSON.stringify(newNewsList))

        if (isDrawer) {
            if (onSuccess) onSuccess()
            if (handleClose) handleClose()
        } else {
            alert(editId ? 'News Updated!' : 'News Published!')

            if (!editId) {
                reset()
            } else {
                router.push('/apps/news/list')
            }
        }
    }

    return (
        <form onSubmit={handleSubmit(onSubmit)}>
            <Grid container spacing={6}>
                <Grid size={{ xs: 12 }}>
                    <Card className={isDrawer ? 'shadow-none border-none' : ''}>
                        {!isDrawer && <CardHeader title='News Editing and Uploading' />}
                        <CardContent className={isDrawer ? 'p-0' : ''}>
                            <Grid container spacing={5}>
                                <Grid size={{ xs: 12, sm: 6 }}>
                                    <Controller
                                        name='category'
                                        control={control}
                                        rules={{ required: true }}
                                        render={({ field }) => (
                                            <FormControl fullWidth error={Boolean(errors.category)}>
                                                <InputLabel id='category-select'>Category</InputLabel>
                                                <Select
                                                    {...field}
                                                    labelId='category-select'
                                                    label='Category'
                                                >
                                                    <MenuItem value='' disabled>Select a Category</MenuItem>
                                                    {categories.map((cat) => (
                                                        <MenuItem key={cat.id} value={cat.title}>
                                                            {cat.title}
                                                        </MenuItem>
                                                    ))}
                                                </Select>
                                                {errors.category && <Typography color='error' variant='caption'>Category is required</Typography>}
                                            </FormControl>
                                        )}
                                    />
                                </Grid>
                                <Grid size={{ xs: 12, sm: 6 }}>
                                    <Controller
                                        name='newsHeadline'
                                        control={control}
                                        rules={{ required: true }}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                label='News Headline'
                                                placeholder='Breaking News...'
                                                error={Boolean(errors.newsHeadline)}
                                                helperText={errors.newsHeadline && 'Headline is required'}
                                            />
                                        )}
                                    />
                                </Grid>
                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='relatedNews'
                                        control={control}
                                        render={({ field }) => (
                                            <FormControl fullWidth>
                                                <InputLabel id='related-news-label'>Related News</InputLabel>
                                                <Select
                                                    {...field}
                                                    labelId='related-news-label'
                                                    multiple
                                                    input={<OutlinedInput label='Related News' />}
                                                    renderValue={(selected) => (
                                                        <div className='flex flex-wrap gap-2'>
                                                            {(selected as string[]).map((value) => {
                                                                const news = allNews.find((b: any) => b.id === value)


return <Chip key={value} label={news?.newsHeadline || value} size='small' />
                                                            })}
                                                        </div>
                                                    )}
                                                >
                                                    {allNews.map((news: any) => (
                                                        news.id !== editId && (
                                                            <MenuItem key={news.id} value={news.id}>
                                                                {news.newsHeadline}
                                                            </MenuItem>
                                                        )
                                                    ))}
                                                </Select>
                                            </FormControl>
                                        )}
                                    />
                                </Grid>
                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='mainImage'
                                        control={control}
                                        render={({ field }) => (
                                            <div className='flex items-center gap-4'>
                                                <TextField
                                                    {...field}
                                                    size='small'
                                                    fullWidth
                                                    placeholder='No file chosen'
                                                    variant='outlined'
                                                    label='Main Image'
                                                    slotProps={{
                                                        input: {
                                                            endAdornment: field.value ? (
                                                                <InputAdornment position='end'>
                                                                    <IconButton size='small' edge='end' onClick={() => field.onChange('')}>
                                                                        <i className='ri-close-line' />
                                                                    </IconButton>
                                                                </InputAdornment>
                                                            ) : null
                                                        }
                                                    }}
                                                />
                                                <Button component='label' variant='outlined' htmlFor='main-image-upload' className='min-is-fit'>
                                                    Choose Image
                                                    <input
                                                        hidden
                                                        id='main-image-upload'
                                                        type='file'
                                                        accept='image/*'
                                                        onChange={(event) => {
                                                            const { files } = event.target

                                                            if (files && files.length !== 0) {
                                                                field.onChange(files[0].name)
                                                            }
                                                        }}
                                                    />
                                                </Button>
                                            </div>
                                        )}
                                    />
                                </Grid>

                                <Grid size={{ xs: 12, sm: 12 }}>
                                    <FormControl>
                                        <FormLabel id="status-group-label" className='mbe-2'>Publishing Status</FormLabel>
                                        <Controller
                                            name="status"
                                            control={control}
                                            render={({ field }) => (
                                                <RadioGroup
                                                    row
                                                    aria-labelledby="status-group-label"
                                                    {...field}
                                                >
                                                    <FormControlLabel value="draft" control={<Radio />} label="Draft" />
                                                    <FormControlLabel value="published" control={<Radio />} label="Publish Now" />
                                                    <FormControlLabel value="scheduled" control={<Radio />} label="Schedule" />
                                                </RadioGroup>
                                            )}
                                        />
                                    </FormControl>
                                </Grid>

                                {statusValue === 'scheduled' && (
                                    <Grid size={{ xs: 12, sm: 6 }}>
                                        <Controller
                                            name="publishedDate"
                                            control={control}
                                            render={({ field }) => (
                                                <div className='flex flex-col gap-2'>
                                                    <InputLabel>Scheduled Date</InputLabel>
                                                    <DatePicker
                                                        selected={field.value}
                                                        onChange={(date) => field.onChange(date)}
                                                        showTimeSelect
                                                        dateFormat="Pp"
                                                        className='w-full border rounded p-2'
                                                        customInput={<TextField fullWidth size='small' />}
                                                    />
                                                </div>
                                            )}
                                        />
                                    </Grid>
                                )}
                            </Grid>
                        </CardContent>
                    </Card>
                </Grid>

                <Grid size={{ xs: 12 }}>
                    <div className='flex items-center justify-between'>
                        <Typography variant='h5'>News Content</Typography>
                        <Button variant='contained' onClick={() => append({ title: '', content: '', imageUrl: '' })}>
                            Add Section
                        </Button>
                    </div>
                </Grid>

                {fields.map((field, index) => (
                    <Grid size={{ xs: 12 }} key={field.id}>
                        <Card>
                            <CardHeader
                                title={`Section ${index + 1}`}
                                action={
                                    <IconButton onClick={() => remove(index)} color='error'>
                                        <i className='ri-delete-bin-line' />
                                    </IconButton>
                                }
                            />
                            <CardContent>
                                <Grid container spacing={5}>
                                    <Grid size={{ xs: 12 }}>
                                        <Controller
                                            name={`sections.${index}.title`}
                                            control={control}
                                            rules={{ required: true }}
                                            render={({ field }) => (
                                                <TextField
                                                    {...field}
                                                    fullWidth
                                                    label='Section Title'
                                                    placeholder='Subheading'
                                                    error={Boolean(errors.sections?.[index]?.title)}
                                                    helperText={errors.sections?.[index]?.title && 'Title is required'}
                                                />
                                            )}
                                        />
                                    </Grid>
                                    <Grid size={{ xs: 12 }}>
                                        <div className='flex items-center gap-4'>
                                            <Controller
                                                name={`sections.${index}.imageUrl`}
                                                control={control}
                                                render={({ field }) => (
                                                    <TextField
                                                        {...field}
                                                        size='small'
                                                        fullWidth
                                                        label='Section Image URL'
                                                        placeholder='No file chosen'
                                                        variant='outlined'
                                                        slotProps={{
                                                            input: {
                                                                endAdornment: field.value ? (
                                                                    <InputAdornment position='end'>
                                                                        <IconButton size='small' edge='end' onClick={() => field.onChange('')}>
                                                                            <i className='ri-close-line' />
                                                                        </IconButton>
                                                                    </InputAdornment>
                                                                ) : null
                                                            }
                                                        }}
                                                    />
                                                )}
                                            />
                                            <Button component='label' variant='outlined' htmlFor={`section-image-upload-${index}`} className='min-is-fit'>
                                                Choose Image
                                                <input
                                                    hidden
                                                    id={`section-image-upload-${index}`}
                                                    type='file'
                                                    accept='image/*'
                                                    onChange={(event) => {
                                                        const { files } = event.target

                                                        if (files && files.length !== 0) {
                                                            setValue(`sections.${index}.imageUrl`, files[0].name)
                                                        }
                                                    }}
                                                />
                                            </Button>
                                        </div>
                                    </Grid>
                                    <Grid size={{ xs: 12 }}>
                                        <Typography className='mbe-2'>Description</Typography>
                                        <Controller
                                            name={`sections.${index}.content`}
                                            control={control}
                                            render={({ field }) => (
                                                <TiptapEditor value={field.value} onChange={field.onChange} />
                                            )}
                                        />
                                    </Grid>
                                </Grid>
                            </CardContent>
                        </Card>
                    </Grid>
                ))}

                <Grid size={{ xs: 12 }} className='flex justify-end pbe-10 gap-4'>
                    {isDrawer && handleClose && (
                        <Button variant='outlined' color='secondary' onClick={handleClose}>
                            Cancel
                        </Button>
                    )}
                    <Button variant='contained' size='large' type='submit'>
                        {editId ? 'Update News' : 'Publish News'}
                    </Button>
                </Grid>
            </Grid>
        </form>
    )
}

export default NewsEditor

'use client'

import { useState, useEffect } from 'react'

import Link from 'next/link'

import Card from '@mui/material/Card'
import CardHeader from '@mui/material/CardHeader'
import CardContent from '@mui/material/CardContent'
import Button from '@mui/material/Button'
import Typography from '@mui/material/Typography'
import IconButton from '@mui/material/IconButton'
import Tooltip from '@mui/material/Tooltip'
import Chip from '@mui/material/Chip'
import Drawer from '@mui/material/Drawer'
import Table from '@mui/material/Table'
import TableBody from '@mui/material/TableBody'
import TableCell from '@mui/material/TableCell'
import TableContainer from '@mui/material/TableContainer'
import TableHead from '@mui/material/TableHead'
import TableRow from '@mui/material/TableRow'
import TablePagination from '@mui/material/TablePagination'
import TextField from '@mui/material/TextField'
import InputAdornment from '@mui/material/InputAdornment'

import NewsEditor from '../NewsEditor'
import AddCategoryDrawer from './AddCategoryDrawer'

type NewsPost = {
    id: string
    newsHeadline: string
    category: string
    status: string
    publishedDate: string | null
    updatedAt: string
}

const NewsList = () => {
    const [news, setNews] = useState<NewsPost[]>([])
    const [categoryDrawerOpen, setCategoryDrawerOpen] = useState(false)
    const [newsDrawerOpen, setNewsDrawerOpen] = useState(false)
    const [selectedNews, setSelectedNews] = useState<NewsPost | undefined>(undefined)

    // Pagination and Search State
    const [page, setPage] = useState(0)
    const [rowsPerPage, setRowsPerPage] = useState(10)
    const [searchTerm, setSearchTerm] = useState('')

    const fetchNews = () => {
        const savedNews = JSON.parse(localStorage.getItem('news-posts') || '[]')

        setNews(savedNews)
    }

    useEffect(() => {
        fetchNews()
    }, [])

    const handleEdit = (post: NewsPost) => {
        setSelectedNews(post)
        setNewsDrawerOpen(true)
    }

    const handleDelete = (id: string) => {
        if (confirm('Are you sure you want to delete this news?')) {
            const updatedNews = news.filter(item => item.id !== id)

            localStorage.setItem('news-posts', JSON.stringify(updatedNews))
            setNews(updatedNews)
        }
    }

    const handleNewsSuccess = () => {
        fetchNews()
        setNewsDrawerOpen(false)
        setSelectedNews(undefined)
    }

    const handleNewsDrawerClose = () => {
        setNewsDrawerOpen(false)
        setSelectedNews(undefined)
    }

    // Pagination Handlers
    const handleChangePage = (event: unknown, newPage: number) => {
        setPage(newPage)
    }

    const handleChangeRowsPerPage = (event: React.ChangeEvent<HTMLInputElement>) => {
        setRowsPerPage(parseInt(event.target.value, 10))
        setPage(0)
    }

    // Filter and Pagination Logic
    const filteredNews = news.filter((item) => {
        const term = searchTerm.toLowerCase()


return (
            item.newsHeadline.toLowerCase().includes(term) ||
            item.category.toLowerCase().includes(term)
        )
    })

    const displayedNews = filteredNews.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)

    return (
        <Card>
            <CardHeader
                title='News List'
                action={
                    <div className='flex gap-2 items-center'>
                        <TextField
                            size='small'
                            placeholder='Search News...'
                            variant='outlined'
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                            InputProps={{
                                startAdornment: (
                                    <InputAdornment position='start'>
                                        <i className='ri-search-line' />
                                    </InputAdornment>
                                )
                            }}
                        />
                        <Button variant='outlined' onClick={() => setCategoryDrawerOpen(true)}>
                            Manage Categories
                        </Button>
                        <Button variant='contained' onClick={() => { setSelectedNews(undefined); setNewsDrawerOpen(true); }}>
                            Add News
                        </Button>
                    </div>
                }
            />
            <CardContent>
                <TableContainer>
                    <Table>
                        <TableHead>
                            <TableRow>
                                <TableCell>Headline</TableCell>
                                <TableCell>Category</TableCell>
                                <TableCell>Status</TableCell>
                                <TableCell>Published Date</TableCell>
                                <TableCell align='right'>Actions</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {displayedNews.length > 0 ? (
                                displayedNews.map((item) => (
                                    <TableRow key={item.id}>
                                        <TableCell>
                                            <Typography variant='subtitle1' className='font-medium'>{item.newsHeadline}</Typography>
                                            <Typography variant='caption' color='text.secondary'>ID: {item.id}</Typography>
                                        </TableCell>
                                        <TableCell>
                                            <Chip label={item.category} size='small' color='primary' variant='outlined' />
                                        </TableCell>
                                        <TableCell>
                                            <Chip
                                                label={item.status || 'Draft'}
                                                size='small'
                                                color={item.status === 'published' ? 'success' : item.status === 'scheduled' ? 'warning' : 'default'}
                                            />
                                        </TableCell>
                                        <TableCell>
                                            {item.publishedDate ? new Date(item.publishedDate).toLocaleString() : '-'}
                                        </TableCell>
                                        <TableCell align='right'>
                                            <div className='flex justify-end gap-2'>
                                                <Tooltip title='Edit'>
                                                    <IconButton color='primary' size='small' onClick={() => handleEdit(item)}>
                                                        <i className='ri-pencil-line' />
                                                    </IconButton>
                                                </Tooltip>
                                                <Tooltip title='Delete'>
                                                    <IconButton color='error' size='small' onClick={() => handleDelete(item.id)}>
                                                        <i className='ri-delete-bin-line' />
                                                    </IconButton>
                                                </Tooltip>
                                            </div>
                                        </TableCell>
                                    </TableRow>
                                ))
                            ) : (
                                <TableRow>
                                    <TableCell colSpan={5} align='center'>
                                        <Typography className='p-5'>No news found.</Typography>
                                    </TableCell>
                                </TableRow>
                            )}
                        </TableBody>
                    </Table>
                </TableContainer>
                <TablePagination
                    rowsPerPageOptions={[5, 10, 25]}
                    component='div'
                    count={filteredNews.length}
                    rowsPerPage={rowsPerPage}
                    page={page}
                    onPageChange={handleChangePage}
                    onRowsPerPageChange={handleChangeRowsPerPage}
                />
            </CardContent>

            <AddCategoryDrawer
                open={categoryDrawerOpen}
                handleClose={() => setCategoryDrawerOpen(false)}
            />

            <Drawer
                open={newsDrawerOpen}
                anchor='right'
                onClose={handleNewsDrawerClose}
                ModalProps={{ keepMounted: false }} // Unmount to reset form state completely
                sx={{ '& .MuiDrawer-paper': { width: { xs: '100%', md: '800px', lg: '1000px' } } }}
            >
                <div className='flex items-center justify-between pli-5 plb-4 border-b'>
                    <Typography variant='h5'>{selectedNews ? 'Edit News' : 'Add News'}</Typography>
                    <IconButton size='small' onClick={handleNewsDrawerClose}>
                        <i className='ri-close-line text-2xl' />
                    </IconButton>
                </div>
                <div className='p-5 overflow-y-auto'>
                    <NewsEditor
                        isDrawer={true}
                        handleClose={handleNewsDrawerClose}
                        onSuccess={handleNewsSuccess}
                        dataToEdit={selectedNews}
                    />
                </div>
            </Drawer>
        </Card>
    )
}

export default NewsList

'use client'

// React Imports
import { useState, useEffect } from 'react'

// MUI Imports
import Button from '@mui/material/Button'
import Card from '@mui/material/Card'
import CardContent from '@mui/material/CardContent'
import CardHeader from '@mui/material/CardHeader'
import Grid from '@mui/material/Grid2'
import TextField from '@mui/material/TextField'
import InputAdornment from '@mui/material/InputAdornment'
import IconButton from '@mui/material/IconButton'
import Divider from '@mui/material/Divider'
import Typography from '@mui/material/Typography'

// Third-party Imports
import { useForm, Controller, useFieldArray } from 'react-hook-form'

// Local Imports
import type { RelatedContentData } from './PartsRelated';
import PartsRelated from './PartsRelated'

type DynamicSectionItem = {
    title: string
    description: string
    image: string
}

type TechSpecCardItem = {
    title: string
    description: string
    image: string
}

type DescriptionFormValues = {
    partName: string
    partBrand: string

    dynamicSections: DynamicSectionItem[]

    techSpecsTitle: string
    techSpecCards: TechSpecCardItem[]

    relatedContent: RelatedContentData
}

type Props = {
    handleClose?: () => void
}

const PartDescriptionSettings = ({ handleClose }: Props) => {
    const {
        control,
        handleSubmit,
        reset,
        setValue,
        watch,
        formState: { errors }
    } = useForm<DescriptionFormValues>({
        defaultValues: {
            partName: '',
            partBrand: '',
            dynamicSections: [],
            techSpecsTitle: 'Technical Specifications',
            techSpecCards: [],
            relatedContent: {
                sectionTypes: [],
                relatedBlogs: [],
                relatedServices: [],
                relatedParts: [],
                relatedProjects: []
            }
        }
    })

    const { fields: dynamicSectionFields, append: appendDynamicSection, remove: removeDynamicSection } = useFieldArray({
        control,
        name: 'dynamicSections'
    })

    const { fields: techSpecFields, append: appendTechSpec, remove: removeTechSpec } = useFieldArray({
        control,
        name: 'techSpecCards'
    })

    const relatedContentValue = watch('relatedContent')

    useEffect(() => {
        const savedSettings = localStorage.getItem('part-description')

        if (savedSettings) {
            reset(JSON.parse(savedSettings))
        }
    }, [reset])

    const onSubmit = (data: DescriptionFormValues) => {
        localStorage.setItem('part-description', JSON.stringify(data))
        alert('Part Description Page Settings Saved!')
        if (handleClose) handleClose()
    }

    const handleRelatedContentSave = (data: RelatedContentData) => {
        setValue('relatedContent', data)
    }

    const renderImageInput = (controlName: any, label: string) => (
        <Controller
            name={controlName}
            control={control}
            render={({ field }) => (
                <div className='flex items-center gap-4'>
                    <TextField
                        {...field}
                        fullWidth
                        placeholder='No file chosen'
                        variant='outlined'
                        label={label}
                        slotProps={{
                            input: {
                                endAdornment: field.value ? (
                                    <InputAdornment position='end'>
                                        <IconButton size='small' edge='end' onClick={() => field.onChange('')}>
                                            <i className='ri-close-line' />
                                        </IconButton>
                                    </InputAdornment>
                                ) : null
                            }
                        }}
                    />
                    <Button component='label' variant='outlined' htmlFor={`file-${controlName}`} className='min-is-fit'>
                        Choose
                        <input
                            hidden
                            id={`file-${controlName}`}
                            type='file'
                            accept='image/*'
                            onChange={(event) => {
                                const { files } = event.target

                                if (files && files.length !== 0) {
                                    field.onChange(files[0].name)
                                }
                            }}
                        />
                    </Button>
                </div>
            )}
        />
    )

    return (
        <form onSubmit={handleSubmit(onSubmit)}>
            <Grid container spacing={8}>
                <Grid size={{ xs: 12 }}>
                    <Card className='shadow-none border-none'>
                        <CardHeader title='Part Description Page Configuration' />
                        <CardContent>
                            <Grid container spacing={6}>

                                {/* General Info */}
                                <Grid size={{ xs: 12 }}>
                                    <Divider textAlign='left'>General Information</Divider>
                                </Grid>
                                <Grid size={{ xs: 12, sm: 6 }}>
                                    <Controller
                                        name='partName'
                                        control={control}
                                        rules={{ required: true }}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                label='Part Name'
                                                placeholder='e.g. Industrial Hydraulic Pump'
                                                error={Boolean(errors.partName)}
                                            />
                                        )}
                                    />
                                </Grid>
                                <Grid size={{ xs: 12, sm: 6 }}>
                                    <Controller
                                        name='partBrand'
                                        control={control}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                label='Brand'
                                                placeholder='e.g. Stark Industries'
                                            />
                                        )}
                                    />
                                </Grid>

                                {/* Dynamic Sections */}
                                <Grid size={{ xs: 12 }} className='mbe-4 mte-4'>
                                    <Divider textAlign='left'>Dynamic Content Sections</Divider>
                                </Grid>
                                <Grid size={{ xs: 12 }}>
                                    {dynamicSectionFields.map((item, index) => (
                                        <div key={item.id} className='flex gap-4 items-start mbe-6 border rounded p-6 relative'>
                                            <Grid container spacing={4} sx={{ width: '100%' }}>
                                                <Grid size={{ xs: 12 }}>
                                                    <Controller
                                                        name={`dynamicSections.${index}.title`}
                                                        control={control}
                                                        rules={{ required: true }}
                                                        render={({ field }) => (
                                                            <TextField
                                                                {...field}
                                                                fullWidth
                                                                label='Section Title'
                                                                placeholder='e.g. Advanced Performance'
                                                                error={Boolean(errors.dynamicSections?.[index]?.title)}
                                                            />
                                                        )}
                                                    />
                                                </Grid>
                                                <Grid size={{ xs: 12 }}>
                                                    {renderImageInput(`dynamicSections.${index}.image`, 'Section Image')}
                                                </Grid>
                                                <Grid size={{ xs: 12 }}>
                                                    <Controller
                                                        name={`dynamicSections.${index}.description`}
                                                        control={control}
                                                        render={({ field }) => (
                                                            <TextField
                                                                {...field}
                                                                fullWidth
                                                                multiline
                                                                rows={3}
                                                                label='Description'
                                                                placeholder='Detailed section content...'
                                                            />
                                                        )}
                                                    />
                                                </Grid>
                                            </Grid>
                                            <IconButton size='small' color='error' onClick={() => removeDynamicSection(index)} className='absolute top-2 right-2'>
                                                <i className='ri-delete-bin-line' />
                                            </IconButton>
                                        </div>
                                    ))}
                                    <Button variant='outlined' onClick={() => appendDynamicSection({ title: '', description: '', image: '' })}>
                                        Add Content Section
                                    </Button>
                                </Grid>

                                {/* Technical Specifications */}
                                <Grid size={{ xs: 12 }} className='mbe-4 mte-4'>
                                    <Divider textAlign='left'>Technical Specifications</Divider>
                                </Grid>
                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='techSpecsTitle'
                                        control={control}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                label='Section Title'
                                                placeholder='Technical Specifications'
                                                className='mbe-4'
                                            />
                                        )}
                                    />

                                    <Typography variant='subtitle1' className='mbe-4'>Specification Cards</Typography>

                                    {techSpecFields.map((item, index) => (
                                        <div key={item.id} className='flex gap-4 items-start mbe-6 border rounded p-6 relative'>
                                            <Grid container spacing={4} sx={{ width: '100%' }}>
                                                <Grid size={{ xs: 12 }}>
                                                    <Controller
                                                        name={`techSpecCards.${index}.title`}
                                                        control={control}
                                                        rules={{ required: true }}
                                                        render={({ field }) => (
                                                            <TextField
                                                                {...field}
                                                                fullWidth
                                                                label='Card Title'
                                                                placeholder='e.g. Dimensions'
                                                                error={Boolean(errors.techSpecCards?.[index]?.title)}
                                                            />
                                                        )}
                                                    />
                                                </Grid>
                                                <Grid size={{ xs: 12 }}>
                                                    {renderImageInput(`techSpecCards.${index}.image`, 'Card Image (Icon)')}
                                                </Grid>
                                                <Grid size={{ xs: 12 }}>
                                                    <Controller
                                                        name={`techSpecCards.${index}.description`}
                                                        control={control}
                                                        render={({ field }) => (
                                                            <TextField
                                                                {...field}
                                                                fullWidth
                                                                multiline
                                                                rows={2}
                                                                label='Description'
                                                                placeholder='Short spec description...'
                                                            />
                                                        )}
                                                    />
                                                </Grid>
                                            </Grid>
                                            <IconButton size='small' color='error' onClick={() => removeTechSpec(index)} className='absolute top-2 right-2'>
                                                <i className='ri-delete-bin-line' />
                                            </IconButton>
                                        </div>
                                    ))}
                                    <Button variant='outlined' onClick={() => appendTechSpec({ title: '', description: '', image: '' })}>
                                        Add Spec Card
                                    </Button>
                                </Grid>

                                {/* Related Content */}
                                <Grid size={{ xs: 12 }}>
                                    <Divider textAlign='left'>Related Content</Divider>
                                </Grid>
                                <Grid size={{ xs: 12 }}>
                                    <PartsRelated
                                        partsData={relatedContentValue}
                                        onSave={handleRelatedContentSave}
                                    />
                                </Grid>

                            </Grid>
                        </CardContent>
                    </Card>
                </Grid>

                <Grid size={{ xs: 12 }} className='flex justify-end pbe-10 gap-4'>
                    {handleClose && (
                        <Button variant='outlined' color='secondary' onClick={handleClose}>
                            Cancel
                        </Button>
                    )}
                    <Button variant='contained' size='large' type='submit'>
                        Save Description Settings
                    </Button>
                </Grid>
            </Grid>
        </form>
    )
}

export default PartDescriptionSettings

'use client'

// React Imports
import { useState, useEffect } from 'react'

// MUI Imports
import Button from '@mui/material/Button'
import Card from '@mui/material/Card'
import CardContent from '@mui/material/CardContent'
import CardHeader from '@mui/material/CardHeader'
import Grid from '@mui/material/Grid2'
import TextField from '@mui/material/TextField'
import InputAdornment from '@mui/material/InputAdornment'
import IconButton from '@mui/material/IconButton'
import Divider from '@mui/material/Divider'
import Typography from '@mui/material/Typography'

// Third-party Imports
import { useForm, Controller, useFieldArray } from 'react-hook-form'

// Local Imports
import type { RelatedContentData } from './PartsRelated';
import PartsRelated from './PartsRelated'

type DetailListItem = {
    heading: string
    description: string
}

type DetailFormValues = {

    // Hero Section
    heroImage: string
    heroTitle: string
    heroDescription: string
    ctaText: string
    ctaLink: string

    // Second Section
    section2Heading: string
    section2Description: string

    // Third Section (Details)
    detailsTitle: string
    detailsDescription: string
    detailsList: DetailListItem[]

    // Related
    relatedContent: RelatedContentData
}

type Props = {
    handleClose?: () => void
}

const PartsDetailSettings = ({ handleClose }: Props) => {
    const {
        control,
        handleSubmit,
        reset,
        setValue,
        watch,
        formState: { errors }
    } = useForm<DetailFormValues>({
        defaultValues: {
            heroImage: '',
            heroTitle: '',
            heroDescription: '',
            ctaText: '',
            ctaLink: '',
            section2Heading: '',
            section2Description: '',
            detailsTitle: '',
            detailsDescription: '',
            detailsList: [{ heading: '', description: '' }],
            relatedContent: {
                sectionTypes: [],
                relatedBlogs: [],
                relatedServices: [],
                relatedParts: [],
                relatedProjects: []
            }
        }
    })

    const { fields, append, remove } = useFieldArray({
        control,
        name: 'detailsList'
    })

    const relatedContentValue = watch('relatedContent')

    useEffect(() => {
        const savedSettings = localStorage.getItem('parts-detail')

        if (savedSettings) {
            try {
                const parsed = JSON.parse(savedSettings);


                // Migration check: if old format (label/value) exists, convert to heading/description
                if (parsed.detailsList && parsed.detailsList.length > 0 && 'label' in parsed.detailsList[0]) {
                    parsed.detailsList = parsed.detailsList.map((item: any) => ({
                        heading: item.label,
                        description: item.value
                    }))
                }

                reset(parsed)
            } catch (e) {
                console.error("Failed to parse parts-detail settings", e)
            }
        }
    }, [reset])

    const onSubmit = (data: DetailFormValues) => {
        localStorage.setItem('parts-detail', JSON.stringify(data))
        alert('Parts Detail Page Settings Saved!')
        if (handleClose) handleClose()
    }

    const handleRelatedContentSave = (data: RelatedContentData) => {
        setValue('relatedContent', data)
    }

    return (
        <form onSubmit={handleSubmit(onSubmit)}>
            <Grid container spacing={8}>
                <Grid size={{ xs: 12 }}>
                    <Card className='shadow-none border-none'>
                        <CardHeader title='Parts Detail Page Configuration' />
                        <CardContent className='p-2'>
                            <Grid container spacing={6}>

                                {/* Hero Section */}
                                <Grid size={{ xs: 12 }}>
                                    <Divider textAlign='left'>Hero Section</Divider>
                                </Grid>

                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='heroImage'
                                        control={control}
                                        render={({ field }) => (
                                            <div className='flex items-center gap-4'>
                                                <TextField
                                                    {...field}
                                                    size='small'
                                                    fullWidth
                                                    placeholder='No file chosen'
                                                    variant='outlined'
                                                    label='Hero Image'
                                                    slotProps={{
                                                        input: {
                                                            endAdornment: field.value ? (
                                                                <InputAdornment position='end'>
                                                                    <IconButton size='small' edge='end' onClick={() => field.onChange('')}>
                                                                        <i className='ri-close-line' />
                                                                    </IconButton>
                                                                </InputAdornment>
                                                            ) : null
                                                        }
                                                    }}
                                                />
                                                <Button component='label' variant='outlined' htmlFor='detail-hero-image' className='min-is-fit'>
                                                    Choose
                                                    <input
                                                        hidden
                                                        id='detail-hero-image'
                                                        type='file'
                                                        accept='image/*'
                                                        onChange={(event) => {
                                                            const { files } = event.target

                                                            if (files && files.length !== 0) {
                                                                field.onChange(files[0].name)
                                                            }
                                                        }}
                                                    />
                                                </Button>
                                            </div>
                                        )}
                                    />
                                </Grid>

                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='heroTitle'
                                        control={control}
                                        rules={{ required: true }}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                label='Hero Title'
                                                placeholder='Page Title...'
                                                error={Boolean(errors.heroTitle)}
                                            />
                                        )}
                                    />
                                </Grid>

                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='heroDescription'
                                        control={control}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                multiline
                                                rows={2}
                                                label='Hero Description'
                                                placeholder='Brief description...'
                                            />
                                        )}
                                    />
                                </Grid>

                                <Grid size={{ xs: 12, sm: 6 }}>
                                    <Controller
                                        name='ctaText'
                                        control={control}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                label='CTA Button Text'
                                                placeholder='e.g. Request Quote'
                                            />
                                        )}
                                    />
                                </Grid>
                                <Grid size={{ xs: 12, sm: 6 }}>
                                    <Controller
                                        name='ctaLink'
                                        control={control}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                label='CTA Link'
                                                placeholder='e.g. /contact'
                                            />
                                        )}
                                    />
                                </Grid>


                                {/* Second Section */}
                                <Grid size={{ xs: 12 }} className='mbe-4 mte-4'>
                                    <Divider textAlign='left'>Section 2 (Info)</Divider>
                                </Grid>

                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='section2Heading'
                                        control={control}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                label='Heading'
                                                placeholder='Section Heading...'
                                            />
                                        )}
                                    />
                                </Grid>
                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='section2Description'
                                        control={control}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                multiline
                                                rows={3}
                                                label='Description'
                                                placeholder='Section content...'
                                            />
                                        )}
                                    />
                                </Grid>

                                {/* Third Section (Details) */}
                                <Grid size={{ xs: 12 }} className='mbe-4 mte-4'>
                                    <Divider textAlign='left'>Section 3 (Details)</Divider>
                                </Grid>

                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='detailsTitle'
                                        control={control}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                label='Details Title'
                                                placeholder='e.g. Technical Specifications'
                                            />
                                        )}
                                    />
                                </Grid>
                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='detailsDescription'
                                        control={control}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                multiline
                                                rows={2}
                                                label='Details Description'
                                                placeholder='Extra info...'
                                            />
                                        )}
                                    />
                                </Grid>

                                <Grid size={{ xs: 12 }}>
                                    <Typography variant='subtitle1' className='mbe-4'>Detail Items</Typography>
                                    {fields.map((item, index) => (
                                        <div key={item.id} className='flex gap-4 items-start mbe-6 border rounded p-4 relative'>
                                            <Grid container spacing={4} sx={{ width: '100%' }}>
                                                <Grid size={{ xs: 12 }}>
                                                    <Controller
                                                        name={`detailsList.${index}.heading`}
                                                        control={control}
                                                        rules={{ required: true }}
                                                        render={({ field }) => (
                                                            <TextField
                                                                {...field}
                                                                fullWidth
                                                                size='small'
                                                                label='Detail Heading'
                                                                placeholder='e.g. Material'
                                                                error={Boolean(errors.detailsList?.[index]?.heading)}
                                                            />
                                                        )}
                                                    />
                                                </Grid>
                                                <Grid size={{ xs: 12 }}>
                                                    <Controller
                                                        name={`detailsList.${index}.description`}
                                                        control={control}
                                                        rules={{ required: true }}
                                                        render={({ field }) => (
                                                            <TextField
                                                                {...field}
                                                                fullWidth
                                                                multiline
                                                                rows={2}
                                                                size='small'
                                                                label='Detail Description'
                                                                placeholder='e.g. High-grade Aluminum Alloy...'
                                                                error={Boolean(errors.detailsList?.[index]?.description)}
                                                            />
                                                        )}
                                                    />
                                                </Grid>
                                            </Grid>
                                            <IconButton size='small' color='error' onClick={() => remove(index)} className='absolute top-2 right-2'>
                                                <i className='ri-delete-bin-line' />
                                            </IconButton>
                                        </div>
                                    ))}
                                    <Button variant='outlined' onClick={() => append({ heading: '', description: '' })}>
                                        Add Details Item
                                    </Button>
                                </Grid>

                                {/* Related Content */}
                                <Grid size={{ xs: 12 }}>
                                    <Divider textAlign='left'>Related Content</Divider>
                                </Grid>

                                <Grid size={{ xs: 12 }}>
                                    <PartsRelated
                                        partsData={relatedContentValue}
                                        onSave={handleRelatedContentSave}
                                    />
                                </Grid>

                            </Grid>
                        </CardContent>
                    </Card>
                </Grid>

                <Grid size={{ xs: 12 }} className='flex justify-end pbe-10 gap-4'>
                    {handleClose && (
                        <Button variant='outlined' color='secondary' onClick={handleClose}>
                            Cancel
                        </Button>
                    )}
                    <Button variant='contained' size='large' type='submit'>
                        Save Detail Settings
                    </Button>
                </Grid>
            </Grid>
        </form>
    )
}

export default PartsDetailSettings

'use client'

// React Imports
import { useState, useEffect } from 'react'

// MUI Imports
import Button from '@mui/material/Button'
import Card from '@mui/material/Card'
import CardContent from '@mui/material/CardContent'
import CardHeader from '@mui/material/CardHeader'
import Grid from '@mui/material/Grid2'
import TextField from '@mui/material/TextField'
import InputAdornment from '@mui/material/InputAdornment'
import IconButton from '@mui/material/IconButton'
import Divider from '@mui/material/Divider'
import Typography from '@mui/material/Typography'

// Third-party Imports
import { useForm, Controller, useFieldArray } from 'react-hook-form'

// Local Imports
import type { RelatedContentData } from './PartsRelated';
import PartsRelated from './PartsRelated'

type HeroSlide = {
    image: string
    title: string
    shortLine: string
    ctaText: string
    ctaLink: string
}

type LandingFormValues = {
    heroSlides: HeroSlide[]
    contentHeading: string
    contentDescription: string
    relatedContent: RelatedContentData
}

type Props = {
    handleClose?: () => void
}

const PartsLandingSettings = ({ handleClose }: Props) => {
    const {
        control,
        handleSubmit,
        reset,
        setValue,
        watch,
        formState: { errors }
    } = useForm<LandingFormValues>({
        defaultValues: {
            heroSlides: [{ image: '', title: '', shortLine: '', ctaText: '', ctaLink: '' }],
            contentHeading: '',
            contentDescription: '',
            relatedContent: {
                sectionTypes: [],
                relatedBlogs: [],
                relatedServices: [],
                relatedParts: [],
                relatedProjects: []
            }
        }
    })

    const { fields, append, remove } = useFieldArray({
        control,
        name: 'heroSlides'
    })

    const relatedContentValue = watch('relatedContent')

    useEffect(() => {
        const savedSettings = localStorage.getItem('parts-landing')

        if (savedSettings) {
            reset(JSON.parse(savedSettings))
        }
    }, [reset])

    const onSubmit = (data: LandingFormValues) => {
        localStorage.setItem('parts-landing', JSON.stringify(data))
        alert('Parts Landing Page Settings Saved!')
        if (handleClose) handleClose()
    }

    const handleRelatedContentSave = (data: RelatedContentData) => {
        setValue('relatedContent', data)
    }

    return (
        <form onSubmit={handleSubmit(onSubmit)}>
            <Grid container spacing={6}>
                <Grid size={{ xs: 12 }}>
                    <Card className='shadow-none border-none'>
                        <CardHeader title='Parts Page Configuration' />
                        <CardContent className='p-0'>
                            <Grid container spacing={5}>

                                {/* Dynamic Hero Section */}
                                <Grid size={{ xs: 12 }}>
                                    <Divider textAlign='left'>Dynamic Hero Slides</Divider>
                                </Grid>

                                {fields.map((item, index) => (
                                    <Grid size={{ xs: 12 }} key={item.id} className='border rounded p-4 relative'>
                                        <div className='flex justify-between items-center mbe-4'>
                                            <Typography variant='h6'>Slide {index + 1}</Typography>
                                            <IconButton size='small' color='error' onClick={() => remove(index)}>
                                                <i className='ri-delete-bin-line' />
                                            </IconButton>
                                        </div>
                                        <Grid container spacing={4}>
                                            <Grid size={{ xs: 12 }}>
                                                <Controller
                                                    name={`heroSlides.${index}.title`}
                                                    control={control}
                                                    rules={{ required: true }}
                                                    render={({ field }) => (
                                                        <TextField
                                                            {...field}
                                                            fullWidth
                                                            label='Title'
                                                            placeholder='Slide Title...'
                                                            error={Boolean(errors.heroSlides?.[index]?.title)}
                                                        />
                                                    )}
                                                />
                                            </Grid>
                                            <Grid size={{ xs: 12 }}>
                                                <Controller
                                                    name={`heroSlides.${index}.shortLine`}
                                                    control={control}
                                                    render={({ field }) => (
                                                        <TextField
                                                            {...field}
                                                            fullWidth
                                                            label='Short Line'
                                                            placeholder='Brief tag...'
                                                        />
                                                    )}
                                                />
                                            </Grid>
                                            <Grid size={{ xs: 12 }}>
                                                <Controller
                                                    name={`heroSlides.${index}.image`}
                                                    control={control}
                                                    render={({ field }) => (
                                                        <div className='flex items-center gap-4'>
                                                            <TextField
                                                                {...field}
                                                                size='small'
                                                                fullWidth
                                                                placeholder='No file chosen'
                                                                variant='outlined'
                                                                label='Slide Image'
                                                                slotProps={{
                                                                    input: {
                                                                        endAdornment: field.value ? (
                                                                            <InputAdornment position='end'>
                                                                                <IconButton size='small' edge='end' onClick={() => field.onChange('')}>
                                                                                    <i className='ri-close-line' />
                                                                                </IconButton>
                                                                            </InputAdornment>
                                                                        ) : null
                                                                    }
                                                                }}
                                                            />
                                                            <Button component='label' variant='outlined' htmlFor={`parts-hero-image-${index}`} className='min-is-fit'>
                                                                Choose
                                                                <input
                                                                    hidden
                                                                    id={`parts-hero-image-${index}`}
                                                                    type='file'
                                                                    accept='image/*'
                                                                    onChange={(event) => {
                                                                        const { files } = event.target

                                                                        if (files && files.length !== 0) {
                                                                            field.onChange(files[0].name)
                                                                        }
                                                                    }}
                                                                />
                                                            </Button>
                                                        </div>
                                                    )}
                                                />
                                            </Grid>
                                            <Grid size={{ xs: 12, sm: 6 }}>
                                                <Controller
                                                    name={`heroSlides.${index}.ctaText`}
                                                    control={control}
                                                    render={({ field }) => (
                                                        <TextField
                                                            {...field}
                                                            fullWidth
                                                            label='CTA Text'
                                                            placeholder='e.g. Learn More'
                                                        />
                                                    )}
                                                />
                                            </Grid>
                                            <Grid size={{ xs: 12, sm: 6 }}>
                                                <Controller
                                                    name={`heroSlides.${index}.ctaLink`}
                                                    control={control}
                                                    render={({ field }) => (
                                                        <TextField
                                                            {...field}
                                                            fullWidth
                                                            label='CTA Link'
                                                            placeholder='e.g. /contact'
                                                        />
                                                    )}
                                                />
                                            </Grid>
                                        </Grid>
                                    </Grid>
                                ))}

                                <Grid size={{ xs: 12 }}>
                                    <Button variant='outlined' onClick={() => append({ image: '', title: '', shortLine: '', ctaText: '', ctaLink: '' })}>
                                        Add Hero Slide
                                    </Button>
                                </Grid>

                                {/* Content Section */}
                                <Grid size={{ xs: 12 }}>
                                    <Divider textAlign='left'>Content Section</Divider>
                                </Grid>

                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='contentHeading'
                                        control={control}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                label='Heading'
                                                placeholder='Main Content Heading'
                                            />
                                        )}
                                    />
                                </Grid>

                                <Grid size={{ xs: 12 }}>
                                    <Controller
                                        name='contentDescription'
                                        control={control}
                                        render={({ field }) => (
                                            <TextField
                                                {...field}
                                                fullWidth
                                                multiline
                                                rows={4}
                                                label='Description'
                                                placeholder='Detailed description...'
                                            />
                                        )}
                                    />
                                </Grid>

                                {/* Related Content */}
                                <Grid size={{ xs: 12 }}>
                                    <Divider textAlign='left'>Related Content</Divider>
                                </Grid>

                                <Grid size={{ xs: 12 }}>
                                    <PartsRelated
                                        partsData={relatedContentValue}
                                        onSave={handleRelatedContentSave}
                                    />
                                </Grid>

                            </Grid>
                        </CardContent>
                    </Card>
                </Grid>

                <Grid size={{ xs: 12 }} className='flex justify-end pbe-10 gap-4'>
                    {handleClose && (
                        <Button variant='outlined' color='secondary' onClick={handleClose}>
                            Cancel
                        </Button>
                    )}
                    <Button variant='contained' size='large' type='submit'>
                        Save Settings
                    </Button>
                </Grid>
            </Grid>
        </form>
    )
}

export default PartsLandingSettings

'use client'

// React Imports
import { useState, useEffect } from 'react'

// MUI Imports
import Card from '@mui/material/Card'
import CardHeader from '@mui/material/CardHeader'
import CardContent from '@mui/material/CardContent'
import Grid from '@mui/material/Grid2'
import Select from '@mui/material/Select'
import MenuItem from '@mui/material/MenuItem'
import Chip from '@mui/material/Chip'
import OutlinedInput from '@mui/material/OutlinedInput'
import InputLabel from '@mui/material/InputLabel'
import FormControl from '@mui/material/FormControl'
import FormHelperText from '@mui/material/FormHelperText'
import Button from '@mui/material/Button'

// Third-party Imports
import { useForm, Controller, useWatch } from 'react-hook-form'

type RelatedSectionType = 'blogs' | 'services' | 'parts' | 'projects'

export type RelatedContentData = {
    sectionTypes: RelatedSectionType[]
    relatedBlogs: string[]
    relatedServices: string[]
    relatedParts: string[]
    relatedProjects: string[]
}

// Mock Data
const MOCK_BLOGS = [
    { id: '1', title: 'Top 10 Career Tips' },
    { id: '2', title: 'Resume Building Guide' },
    { id: '3', title: 'Interview Preparation' },
    { id: '4', title: 'Workplace Etiquette' }
]

const MOCK_SERVICES = [
    { id: 's1', title: 'Resume Review' },
    { id: 's2', title: 'Career Counseling' },
    { id: 's3', title: 'Mock Interview' },
    { id: 's4', title: 'LinkedIn Profile Optimization' }
]

const MOCK_PARTS = [
    { id: 'p1', title: 'Office Equipment' },
    { id: 'p2', title: 'Software Licenses' },
    { id: 'p3', title: 'Training Materials' },
    { id: 'p4', title: 'IT Support' }
]

const MOCK_PROJECTS = [
    { id: 'pr1', title: 'Internal Tool Development' },
    { id: 'pr2', title: 'Client Website Redesign' },
    { id: 'pr3', title: 'Mobile App Launch' },
    { id: 'pr4', title: 'Data Analytics Dashboard' }
]

// Type for Project
type ProjectType = {
    id: string
    title: string
}

const PartsRelated = ({ partsData, onSave }: { partsData?: any; onSave?: (data: RelatedContentData) => void }) => {
    const {
        control,
        handleSubmit,
        reset,
        watch
    } = useForm<RelatedContentData>({
        defaultValues: {
            sectionTypes: [],
            relatedBlogs: [],
            relatedServices: [],
            relatedParts: [],
            relatedProjects: []
        }
    })

    const [projects, setProjects] = useState<ProjectType[]>(MOCK_PROJECTS)

    useEffect(() => {
        const savedProjects = localStorage.getItem('category-products')

        if (savedProjects) {
            const parsedProjects = JSON.parse(savedProjects)

            if (Array.isArray(parsedProjects) && parsedProjects.length > 0) {
                setProjects(parsedProjects.map((p: any) => ({ id: p.id, title: p.title })))
            }
        }
    }, [])

    const selectedSectionTypes = watch('sectionTypes') || []

    useEffect(() => {
        if (partsData) {
            reset({
                sectionTypes: partsData.sectionTypes || [],
                relatedBlogs: partsData.relatedBlogs || [],
                relatedServices: partsData.relatedServices || [],
                relatedParts: partsData.relatedParts || [],
                relatedProjects: partsData.relatedProjects || []
            })
        }
    }, [partsData, reset])

    const onSubmit = (data: RelatedContentData) => {
        if (onSave) {
            onSave(data)
        }
    }

    const availableOptions = [
        { value: 'blogs', label: 'Related Blogs' },
        { value: 'services', label: 'Related Services' },
        { value: 'parts', label: 'Related Parts' },
        { value: 'projects', label: 'Related Projects' }
    ]

    return (
        <Card>
            <CardHeader title='Related Content' subheader='Choose up to 2 sections' />
            <CardContent>
                <Grid container spacing={5}>
                    <Grid size={{ xs: 12 }}>
                        <Controller
                            name='sectionTypes'
                            control={control}
                            render={({ field }) => (
                                <FormControl fullWidth error={selectedSectionTypes.length > 2}>
                                    <InputLabel id='parts-section-types-label'>Select Related Sections (Max 2)</InputLabel>
                                    <Select
                                        {...field}
                                        labelId='parts-section-types-label'
                                        multiple
                                        input={<OutlinedInput label='Select Related Sections (Max 2)' />}
                                        renderValue={(selected) => (
                                            <div className='flex flex-wrap gap-2'>
                                                {(selected as string[]).map((value) => (
                                                    <Chip
                                                        key={value}
                                                        label={availableOptions.find(opt => opt.value === value)?.label || value}
                                                        size='small'
                                                        onDelete={() => {
                                                            const newValue = (selected as string[]).filter((item) => item !== value)

                                                            field.onChange(newValue)
                                                        }}
                                                        onMouseDown={(event) => {
                                                            event.stopPropagation()
                                                        }}
                                                    />
                                                ))}
                                            </div>
                                        )}
                                        onChange={(e) => {
                                            const value = e.target.value as string[]

                                            if (value.length <= 2) {
                                                field.onChange(value)
                                                handleSubmit(onSubmit)()
                                            }
                                        }}
                                    >
                                        {availableOptions.map((option) => (
                                            <MenuItem
                                                key={option.value}
                                                value={option.value}
                                                disabled={selectedSectionTypes.length >= 2 && !selectedSectionTypes.includes(option.value as RelatedSectionType)}
                                            >
                                                {option.label}
                                            </MenuItem>
                                        ))}
                                    </Select>
                                    {selectedSectionTypes.length >= 2 && (
                                        <FormHelperText>Maximum 2 sections selected</FormHelperText>
                                    )}
                                </FormControl>
                            )}
                        />
                    </Grid>

                    {selectedSectionTypes.includes('blogs') && (
                        <Grid size={{ xs: 12 }}>
                            <Controller
                                name='relatedBlogs'
                                control={control}
                                render={({ field }) => (
                                    <FormControl fullWidth>
                                        <InputLabel>Select Related Blogs</InputLabel>
                                        <Select
                                            {...field}
                                            multiple
                                            input={<OutlinedInput label='Select Related Blogs' />}
                                            renderValue={(selected) => (
                                                <div className='flex flex-wrap gap-2'>
                                                    {(selected as string[]).map((value) => (
                                                        <Chip key={value} label={MOCK_BLOGS.find(b => b.id === value)?.title || value} size='small' onDelete={() => field.onChange(selected.filter(i => i !== value))} onMouseDown={(e) => e.stopPropagation()} />
                                                    ))}
                                                </div>
                                            )}
                                            onChange={(e) => {
                                                field.onChange(e.target.value)
                                                handleSubmit(onSubmit)()
                                            }}
                                        >
                                            {MOCK_BLOGS.map((item) => (
                                                <MenuItem key={item.id} value={item.id}>{item.title}</MenuItem>
                                            ))}
                                        </Select>
                                    </FormControl>
                                )}
                            />
                        </Grid>
                    )}
                    {selectedSectionTypes.includes('services') && (
                        <Grid size={{ xs: 12 }}>
                            <Controller
                                name='relatedServices'
                                control={control}
                                render={({ field }) => (
                                    <FormControl fullWidth>
                                        <InputLabel>Select Related Services</InputLabel>
                                        <Select
                                            {...field}
                                            multiple
                                            input={<OutlinedInput label='Select Related Services' />}
                                            renderValue={(selected) => (
                                                <div className='flex flex-wrap gap-2'>
                                                    {(selected as string[]).map((value) => (
                                                        <Chip key={value} label={MOCK_SERVICES.find(s => s.id === value)?.title || value} size='small' onDelete={() => field.onChange(selected.filter(i => i !== value))} onMouseDown={(e) => e.stopPropagation()} />
                                                    ))}
                                                </div>
                                            )}
                                            onChange={(e) => {
                                                field.onChange(e.target.value)
                                                handleSubmit(onSubmit)()
                                            }}
                                        >
                                            {MOCK_SERVICES.map((item) => (
                                                <MenuItem key={item.id} value={item.id}>{item.title}</MenuItem>
                                            ))}
                                        </Select>
                                    </FormControl>
                                )}
                            />
                        </Grid>
                    )}
                    {selectedSectionTypes.includes('parts') && (
                        <Grid size={{ xs: 12 }}>
                            <Controller
                                name='relatedParts'
                                control={control}
                                render={({ field }) => (
                                    <FormControl fullWidth>
                                        <InputLabel>Select Related Parts</InputLabel>
                                        <Select
                                            {...field}
                                            multiple
                                            input={<OutlinedInput label='Select Related Parts' />}
                                            renderValue={(selected) => (
                                                <div className='flex flex-wrap gap-2'>
                                                    {(selected as string[]).map((value) => (
                                                        <Chip key={value} label={MOCK_PARTS.find(p => p.id === value)?.title || value} size='small' onDelete={() => field.onChange(selected.filter(i => i !== value))} onMouseDown={(e) => e.stopPropagation()} />
                                                    ))}
                                                </div>
                                            )}
                                            onChange={(e) => {
                                                field.onChange(e.target.value)
                                                handleSubmit(onSubmit)()
                                            }}
                                        >
                                            {MOCK_PARTS.map((item) => (
                                                <MenuItem key={item.id} value={item.id}>{item.title}</MenuItem>
                                            ))}
                                        </Select>
                                    </FormControl>
                                )}
                            />
                        </Grid>
                    )}
                    {selectedSectionTypes.includes('projects') && (
                        <Grid size={{ xs: 12 }}>
                            <Controller
                                name='relatedProjects'
                                control={control}
                                render={({ field }) => (
                                    <FormControl fullWidth>
                                        <InputLabel>Select Related Projects</InputLabel>
                                        <Select
                                            {...field}
                                            multiple
                                            input={<OutlinedInput label='Select Related Projects' />}
                                            renderValue={(selected) => (
                                                <div className='flex flex-wrap gap-2'>
                                                    {(selected as string[]).map((value) => (
                                                        <Chip key={value} label={projects.find(p => p.id === value)?.title || value} size='small' onDelete={() => field.onChange(selected.filter(i => i !== value))} onMouseDown={(e) => e.stopPropagation()} />
                                                    ))}
                                                </div>
                                            )}
                                            onChange={(e) => {
                                                field.onChange(e.target.value)
                                                handleSubmit(onSubmit)()
                                            }}
                                        >
                                            {projects.map((item) => (
                                                <MenuItem key={item.id} value={item.id}>{item.title}</MenuItem>
                                            ))}
                                        </Select>
                                    </FormControl>
                                )}
                            />
                        </Grid>
                    )}
                </Grid>
            </CardContent>
        </Card>
    )
}

export default PartsRelated

'use client'

// React Imports
import { useState, useEffect } from 'react'

// MUI Imports
import Card from '@mui/material/Card'
import CardHeader from '@mui/material/CardHeader'
import Button from '@mui/material/Button'
import Typography from '@mui/material/Typography'
import IconButton from '@mui/material/IconButton'
import Drawer from '@mui/material/Drawer'
import Table from '@mui/material/Table'
import TableBody from '@mui/material/TableBody'
import TableCell from '@mui/material/TableCell'
import TableContainer from '@mui/material/TableContainer'
import TableHead from '@mui/material/TableHead'
import TableRow from '@mui/material/TableRow'
import TablePagination from '@mui/material/TablePagination'
import TextField from '@mui/material/TextField'
import InputAdornment from '@mui/material/InputAdornment'
import Collapse from '@mui/material/Collapse'
import Box from '@mui/material/Box'

// Type Imports

// Component Imports

import Menu from '@mui/material/Menu'
import MenuItem from '@mui/material/MenuItem'
import Dialog from '@mui/material/Dialog'
import DialogTitle from '@mui/material/DialogTitle'
import DialogContent from '@mui/material/DialogContent'
import DialogActions from '@mui/material/DialogActions'
import List from '@mui/material/List'
import ListItem from '@mui/material/ListItem'
import ListItemText from '@mui/material/ListItemText'
import ListItemIcon from '@mui/material/ListItemIcon'
import Checkbox from '@mui/material/Checkbox'
import ListItemButton from '@mui/material/ListItemButton'
import Chip from '@mui/material/Chip'

import PartsCategoryEditor from '../PartsCategoryEditor'
import type { PartsCategoryType } from '../PartsCategoryEditor'

const MOCK_PRODUCTS = [
    { id: 'p1', title: 'Office Equipment' },
    { id: 'p2', title: 'Software Licenses' },
    { id: 'p3', title: 'Training Materials' },
    { id: 'p4', title: 'IT Support' },
    { id: 'p5', title: 'Ergonomic Chairs' },
    { id: 'p6', title: 'Standing Desks' },
    { id: 'p7', title: 'Monitors' },
    { id: 'p8', title: 'Keyboards' }
]

// Row Component
const Row = ({
    row,
    level = 0,
    handleAddSubcategory,
    handleAssignProducts,
    handleRemoveProduct,
    handleEdit,
    handleDelete
}: {
    row: PartsCategoryType,
    level?: number,
    handleAddSubcategory: (parent: PartsCategoryType) => void,
    handleAssignProducts: (item: PartsCategoryType) => void,
    handleRemoveProduct: (item: PartsCategoryType, productId: string) => void,
    handleEdit: (item: PartsCategoryType) => void,
    handleDelete: (id: string, parentId?: string) => void
}) => {
    const [open, setOpen] = useState(false)
    const hasSubcategories = row.subcategories && row.subcategories.length > 0
    const assignedCount = row.assignedProducts?.length || 0

    return (
        <>
            <TableRow sx={{ '& > *': { borderBottom: 'unset' } }}>
                <TableCell>
                    <div style={{ paddingLeft: level * 20 }} className='flex items-center gap-2'>
                        {hasSubcategories && (
                            <IconButton aria-label="expand row" size="small" onClick={() => setOpen(!open)}>
                                {open ? <i className="ri-arrow-up-s-line" /> : <i className="ri-arrow-down-s-line" />}
                            </IconButton>
                        )}
                        {!hasSubcategories && level > 0 && <span className="w-8" />} {/* Spacer for alignment */}
                        <Typography variant='body1' className='font-medium'>{row.title}</Typography>
                    </div>
                </TableCell>
                <TableCell>
                    {row.image && <Typography variant='caption' color='textSecondary'>{row.image}</Typography>}
                </TableCell>
                <TableCell>
                    {assignedCount > 0 && (
                        <div className='flex gap-1 flex-wrap'>
                            <Chip
                                size='small'
                                label={`${assignedCount} Products`}
                                color='primary'
                                variant='outlined'
                                onClick={() => handleAssignProducts(row)}
                                onDelete={() => {
                                    // Delete all? No, usually chips are one by one, but here it is a summary chip.
                                    // The user asked "delete add a provsion for that also".
                                    // If I put onDelete on the summary chip, maybe clear all? Or open dialog?
                                    // Let's assume this chip is just a summary and user wants to edit via dialog.
                                    // BUT, I can show individual chips if count is small?
                                    // For now, let's keep the summary chip but make it clickable to edit.
                                    // Wait, "if someone wants to edit the Assign products or delete"
                                    // I'll assume opening the dialog allows deleting (unchecking).
                                    // But maybe they want to delete the CATEGORY? No "remove add Sub Category option... and if someone wants to edit the Assign products or delete"
                                    // Probably refers to deleting the assignment.
                                    // I will add a text tooltip or just rely on the dialog.
                                }}
                            />
                        </div>
                    )}
                </TableCell>
                <TableCell>
                    <div className='flex gap-2'>
                        <IconButton size='small' color='primary' onClick={() => handleAssignProducts(row)} title="Assign Parts">
                            <i className='ri-add-circle-line' />
                        </IconButton>
                        <IconButton size='small' color='secondary' onClick={() => handleAddSubcategory(row)} title="Add Subcategory">
                            <i className='ri-folder-add-line' />
                        </IconButton>
                        <IconButton size='small' onClick={() => handleEdit(row)} title="Edit">
                            <i className='ri-pencil-line' />
                        </IconButton>
                        <IconButton size='small' color='error' onClick={() => handleDelete(row.id, level > 0 ? 'SUB_DELETE_TODO' : undefined)} title="Delete">
                            <i className='ri-delete-bin-line' />
                        </IconButton>
                    </div>
                </TableCell>
            </TableRow>
            {/* Subcategories Row */}
            {hasSubcategories && (
                <TableRow>
                    <TableCell style={{ paddingBottom: 0, paddingTop: 0 }} colSpan={6}>
                        <Collapse in={open} timeout="auto" unmountOnExit>
                            <Box sx={{ margin: 1 }}>
                                <Table size="small" aria-label="purchases">
                                    <TableBody>
                                        {row.subcategories?.map((subRow) => (
                                            <Row
                                                key={subRow.id}
                                                row={subRow}
                                                level={level + 1}
                                                handleAddSubcategory={handleAddSubcategory}
                                                handleAssignProducts={handleAssignProducts}
                                                handleRemoveProduct={handleRemoveProduct}
                                                handleEdit={handleEdit}
                                                handleDelete={handleDelete}
                                            />
                                        ))}
                                    </TableBody>
                                </Table>
                            </Box>
                        </Collapse>
                    </TableCell>
                </TableRow>
            )}
        </>
    )
}

const CategoryList = () => {
    // States
    const [data, setData] = useState<PartsCategoryType[]>([])
    const [filteredData, setFilteredData] = useState<PartsCategoryType[]>([])
    const [searchTerm, setSearchTerm] = useState('')
    const [page, setPage] = useState(0)
    const [rowsPerPage, setRowsPerPage] = useState(10)

    // Available Parts State
    const [availableParts, setAvailableParts] = useState<any[]>([])

    // Drawer States
    const [editorOpen, setEditorOpen] = useState(false)
    const [editingNode, setEditingNode] = useState<PartsCategoryType | undefined>(undefined)
    const [parentCategory, setParentCategory] = useState<PartsCategoryType | undefined>(undefined)

    // Menu & Dialog States
    const [menuAnchorEl, setMenuAnchorEl] = useState<null | HTMLElement>(null)
    const [selectedCategoryForMenu, setSelectedCategoryForMenu] = useState<PartsCategoryType | null>(null)
    const [productDialogOpen, setProductDialogOpen] = useState(false)
    const [tempSelectedProducts, setTempSelectedProducts] = useState<string[]>([])

    // Load data
    const loadData = () => {
        const savedData = localStorage.getItem('parts-categories')

        if (savedData) {
            const parsedData = JSON.parse(savedData)

            setData(parsedData)
            setFilteredData(parsedData)
        }

        // Load Parts
        const savedParts = localStorage.getItem('part-descriptions')

        if (savedParts) {
            setAvailableParts(JSON.parse(savedParts))
        } else {
            // Seed Dummy Data if empty (per user request)
            const dummyParts = [
                { id: 'dp1', partName: 'Hydraulic Cylinder XZ-900', partBrand: 'Stark Industries', updatedAt: new Date().toISOString() },
                { id: 'dp2', partName: 'Ceramic Ball Bearings', partBrand: 'Acme Corp', updatedAt: new Date().toISOString() },
                { id: 'dp3', partName: 'Control Panel Interface', partBrand: 'CyberSystems', updatedAt: new Date().toISOString() },
                { id: 'dp4', partName: 'High-Pressure Valve', partBrand: 'Stark Industries', updatedAt: new Date().toISOString() }
            ]

            localStorage.setItem('part-descriptions', JSON.stringify(dummyParts))
            setAvailableParts(dummyParts)
        }
    }

    useEffect(() => {
        loadData()
    }, [])

    // Filter
    useEffect(() => {
        const result = data.filter(item =>
            item.title.toLowerCase().includes(searchTerm.toLowerCase())
        )

        setFilteredData(result)
        setPage(0)
    }, [searchTerm, data])

    // Pagination
    const handleChangePage = (event: unknown, newPage: number) => {
        setPage(newPage)
    }

    const handleChangeRowsPerPage = (event: React.ChangeEvent<HTMLInputElement>) => {
        setRowsPerPage(+event.target.value)
        setPage(0)
    }

    // Actions
    const handleAddNew = () => {
        setEditingNode(undefined)
        setParentCategory(undefined)
        setEditorOpen(true)
    }

    const handleAddSubcategory = (parent: PartsCategoryType) => {
        setEditingNode(undefined)
        setParentCategory(parent)
        setEditorOpen(true)
    }

    const handleAssignProducts = (item: PartsCategoryType) => {
        setSelectedCategoryForMenu(item)
        setTempSelectedProducts(item.assignedProducts || [])
        setProductDialogOpen(true)
    }

    const handleRemoveProduct = (item: PartsCategoryType, productId: string) => {
        const updateRecursive = (categories: PartsCategoryType[]): PartsCategoryType[] => {
            return categories.map(cat => {
                if (cat.id === item.id) {
                    return {
                        ...cat,
                        assignedProducts: (cat.assignedProducts || []).filter(p => p !== productId),
                        updatedAt: new Date().toISOString()
                    }
                }

                if (cat.subcategories && cat.subcategories.length > 0) {
                    return { ...cat, subcategories: updateRecursive(cat.subcategories) }
                }


return cat
            })
        }

        const newData = updateRecursive(data)

        localStorage.setItem('parts-categories', JSON.stringify(newData))
        loadData()
    }

    const handleSaveProducts = () => {
        if (selectedCategoryForMenu) {
            const updateRecursive = (categories: PartsCategoryType[]): PartsCategoryType[] => {
                return categories.map(cat => {
                    if (cat.id === selectedCategoryForMenu.id) {
                        return { ...cat, assignedProducts: tempSelectedProducts, updatedAt: new Date().toISOString() }
                    }

                    if (cat.subcategories && cat.subcategories.length > 0) {
                        return { ...cat, subcategories: updateRecursive(cat.subcategories) }
                    }


return cat
                })
            }

            const newData = updateRecursive(data)

            localStorage.setItem('parts-categories', JSON.stringify(newData))
            loadData()
        }

        setProductDialogOpen(false)
        setSelectedCategoryForMenu(null)
    }

    const handleToggleProduct = (productId: string) => {
        const currentIndex = tempSelectedProducts.indexOf(productId)
        const newChecked = [...tempSelectedProducts]

        if (currentIndex === -1) {
            newChecked.push(productId)
        } else {
            newChecked.splice(currentIndex, 1)
        }

        setTempSelectedProducts(newChecked)
    }

    const handleEdit = (item: PartsCategoryType) => {
        setEditingNode(item)
        setParentCategory(undefined)
        setEditorOpen(true)
    }

    const handleDelete = (id: string, parentId?: string) => {
        if (confirm('Are you sure you want to delete this category?')) {
            const deleteRecursive = (categories: PartsCategoryType[]): PartsCategoryType[] => {
                return categories.filter(cat => cat.id !== id).map(cat => ({
                    ...cat,
                    subcategories: cat.subcategories ? deleteRecursive(cat.subcategories) : undefined
                }))
            }

            const newData = deleteRecursive(data)

            localStorage.setItem('parts-categories', JSON.stringify(newData))
            loadData()
        }
    }

    const handleEditorSuccess = () => {
        loadData()
        setEditorOpen(false)
    }

    return (
        <Card>
            <CardHeader
                title='Parts Categories'
                action={
                    <div className='flex gap-4 items-center'>
                        <TextField
                            size='small'
                            placeholder='Search Categories'
                            value={searchTerm}
                            onChange={e => setSearchTerm(e.target.value)}
                            InputProps={{
                                startAdornment: (
                                    <InputAdornment position='start'>
                                        <i className='ri-search-line' />
                                    </InputAdornment>
                                )
                            }}
                        />
                        <Button variant='contained' startIcon={<i className='ri-add-line' />} onClick={handleAddNew}>
                            Add Category
                        </Button>
                    </div>
                }
            />
            <TableContainer>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell>Title</TableCell>
                            <TableCell>Image</TableCell>
                            <TableCell>Info</TableCell>
                            <TableCell>Actions</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {filteredData.length === 0 ? (
                            <TableRow>
                                <TableCell colSpan={4} align='center'>
                                    <Typography>No categories found</Typography>
                                </TableCell>
                            </TableRow>
                        ) : (
                            filteredData
                                .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                                .map((row) => (
                                    <Row
                                        key={row.id}
                                        row={row}
                                        handleAddSubcategory={handleAddSubcategory}
                                        handleAssignProducts={handleAssignProducts}
                                        handleRemoveProduct={handleRemoveProduct}
                                        handleEdit={handleEdit}
                                        handleDelete={handleDelete}
                                    />
                                ))
                        )}
                    </TableBody>
                </Table>
            </TableContainer>
            <TablePagination
                rowsPerPageOptions={[10, 25, 50]}
                component='div'
                count={filteredData.length}
                rowsPerPage={rowsPerPage}
                page={page}
                onPageChange={handleChangePage}
                onRowsPerPageChange={handleChangeRowsPerPage}
            />

            {/* Editor Drawer */}
            <Drawer
                open={editorOpen}
                anchor='right'
                onClose={() => setEditorOpen(false)}
                sx={{ '& .MuiDrawer-paper': { width: { xs: '100%', md: '500px' } } }}
            >
                <div className='flex items-center justify-between pli-5 plb-4 border-b'>
                    <Typography variant='h5'>
                        {editingNode
                            ? 'Edit Category'
                            : parentCategory
                                ? `Add Subcategory to ${parentCategory.title}`
                                : 'Add New Category'
                        }
                    </Typography>
                    <IconButton size='small' onClick={() => setEditorOpen(false)}>
                        <i className='ri-close-line text-2xl' />
                    </IconButton>
                </div>
                <div className='p-5 overflow-y-auto'>
                    <PartsCategoryEditor
                        isDrawer
                        handleClose={() => setEditorOpen(false)}
                        onSuccess={handleEditorSuccess}
                        dataToEdit={editingNode}
                        parentCategory={parentCategory}
                    />
                </div>
            </Drawer>

            {/* Product Selection Dialog */}
            <Dialog open={productDialogOpen} onClose={() => setProductDialogOpen(false)} maxWidth='sm' fullWidth>
                <DialogTitle>Assign Parts</DialogTitle>
                <DialogContent dividers>
                    <List sx={{ width: '100%', bgcolor: 'background.paper' }}>
                        {availableParts.length === 0 ? (
                            <Typography className='p-4 text-center'>No parts available. Create some in Description Page.</Typography>
                        ) : (
                            availableParts.map((product) => {
                                const labelId = `checkbox-list-label-${product.id}`;
                                const isChecked = tempSelectedProducts.indexOf(product.id) !== -1

                                return (
                                    <ListItem
                                        key={product.id}
                                        disablePadding
                                    >
                                        <ListItemButton role={undefined} onClick={() => handleToggleProduct(product.id)} dense>
                                            <ListItemIcon>
                                                <Checkbox
                                                    edge="start"
                                                    checked={isChecked}
                                                    tabIndex={-1}
                                                    disableRipple
                                                    inputProps={{ 'aria-labelledby': labelId }}
                                                />
                                            </ListItemIcon>
                                            <ListItemText id={labelId} primary={product.partName} secondary={product.partBrand} />
                                        </ListItemButton>
                                    </ListItem>
                                );
                            })
                        )}
                    </List>
                </DialogContent>
                <DialogActions>
                    <Button onClick={() => setProductDialogOpen(false)}>Cancel</Button>
                    <Button onClick={handleSaveProducts} variant='contained'>Save Assignments</Button>
                </DialogActions>
            </Dialog>
        </Card>
    )
}

export default CategoryList

'use client'

// React Imports
import { useState, useMemo, useEffect } from 'react'

// Next Imports
import Link from 'next/link'
import { useParams } from 'next/navigation'

// MUI Imports
import Card from '@mui/material/Card'
import CardContent from '@mui/material/CardContent'
import Button from '@mui/material/Button'
import TextField from '@mui/material/TextField'
import Select from '@mui/material/Select'
import MenuItem from '@mui/material/MenuItem'
import FormControl from '@mui/material/FormControl'
import InputLabel from '@mui/material/InputLabel'
import Chip from '@mui/material/Chip'
import Typography from '@mui/material/Typography'
import Checkbox from '@mui/material/Checkbox'
import IconButton from '@mui/material/IconButton'
import { styled } from '@mui/material/styles'
import TablePagination from '@mui/material/TablePagination'
import type { TextFieldProps } from '@mui/material/TextField'

// Third-party Imports
import classnames from 'classnames'
import { rankItem } from '@tanstack/match-sorter-utils'
import {
  createColumnHelper,
  flexRender,
  getCoreRowModel,
  useReactTable,
  getFilteredRowModel,
  getFacetedRowModel,
  getFacetedUniqueValues,
  getFacetedMinMaxValues,
  getPaginationRowModel,
  getSortedRowModel
} from '@tanstack/react-table'
import type { ColumnDef, FilterFn } from '@tanstack/react-table'
import type { RankingInfo } from '@tanstack/match-sorter-utils'

// Type Imports
import type { ThemeColor } from '@core/types'
import type { UsersType } from '@/types/apps/userTypes'
import type { Locale } from '@configs/i18n'

// Component Imports
import OptionMenu from '@core/components/option-menu'
import CustomAvatar from '@core/components/mui/Avatar'

// Util Imports
import { getInitials } from '@/utils/getInitials'
import { getLocalizedUrl } from '@/utils/i18n'

// Style Imports
import tableStyles from '@core/styles/table.module.css'

declare module '@tanstack/table-core' {
  interface FilterFns {
    fuzzy: FilterFn<unknown>
  }
  interface FilterMeta {
    itemRank: RankingInfo
  }
}

type UsersTypeWithAction = UsersType & {
  action?: string
}

type UserRoleType = {
  [key: string]: { icon: string; color: string }
}

type UserStatusType = {
  [key: string]: ThemeColor
}

// Styled Components
const Icon = styled('i')({})

const fuzzyFilter: FilterFn<any> = (row, columnId, value, addMeta) => {
  // Rank the item
  const itemRank = rankItem(row.getValue(columnId), value)

  // Store the itemRank info
  addMeta({
    itemRank
  })

  // Return if the item should be filtered in/out
  return itemRank.passed
}

const DebouncedInput = ({
  value: initialValue,
  onChange,
  debounce = 500,
  ...props
}: {
  value: string | number
  onChange: (value: string | number) => void
  debounce?: number
} & Omit<TextFieldProps, 'onChange'>) => {
  // States
  const [value, setValue] = useState(initialValue)

  useEffect(() => {
    setValue(initialValue)
  }, [initialValue])

  useEffect(() => {
    const timeout = setTimeout(() => {
      onChange(value)
    }, debounce)

    return () => clearTimeout(timeout)
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [value])

  return <TextField {...props} value={value} onChange={e => setValue(e.target.value)} size='small' />
}

// Vars
const userRoleObj: UserRoleType = {
  admin: { icon: 'ri-vip-crown-line', color: 'error' },
  author: { icon: 'ri-computer-line', color: 'warning' },
  editor: { icon: 'ri-edit-box-line', color: 'info' },
  maintainer: { icon: 'ri-pie-chart-2-line', color: 'success' },
  subscriber: { icon: 'ri-user-3-line', color: 'primary' }
}

const userStatusObj: UserStatusType = {
  active: 'success',
  pending: 'warning',
  inactive: 'secondary'
}

// Column Definitions
const columnHelper = createColumnHelper<UsersTypeWithAction>()

const RolesTable = ({ tableData }: { tableData?: UsersType[] }) => {
  // States
  const [role, setRole] = useState<UsersType['role']>('')
  const [rowSelection, setRowSelection] = useState({})
  const [data, setData] = useState(...[tableData])
  const [filteredData, setFilteredData] = useState(data)
  const [globalFilter, setGlobalFilter] = useState('')

  // Hooks
  const { lang: locale } = useParams()

  const columns = useMemo<ColumnDef<UsersTypeWithAction, any>[]>(
    () => [
      columnHelper.display({
              id: 'serialNumber',
              header: '#',
              cell: ({ row }) => <Typography>{row.index + 1}</Typography>
            }),
      columnHelper.accessor('fullName', {
        header: 'User',
        cell: ({ row }) => (
          <div className='flex items-center gap-3'>
            {getAvatar({ avatar: row.original.avatar, fullName: row.original.fullName })}
            <div className='flex flex-col'>
              <Typography color='text.primary' className='font-medium'>
                {row.original.fullName}
              </Typography>
              <Typography variant='body2'>{row.original.username}</Typography>
            </div>
          </div>
        )
      }),
      columnHelper.accessor('email', {
        header: 'Email',
        cell: ({ row }) => <Typography>{row.original.email}</Typography>
      }),
      columnHelper.accessor('role', {
        header: 'Role',
        cell: ({ row }) => (
          <div className='flex items-center gap-2'>
            <Icon
              className={userRoleObj[row.original.role].icon}
              sx={{ color: `var(--mui-palette-${userRoleObj[row.original.role].color}-main)`, fontSize: '1.375rem' }}
            />
            <Typography color='text.primary' className='capitalize'>
              {row.original.role}
            </Typography>
          </div>
        )
      }),

      columnHelper.accessor('status', {
        header: 'Status',
        cell: ({ row }) => (
          <div className='flex items-center gap-3'>
            <Chip
              variant='tonal'
              label={row.original.status}
              size='small'
              color={userStatusObj[row.original.status]}
              className='capitalize'
            />
          </div>
        )
      }),
      columnHelper.accessor('action', {
        header: 'Actions',
        cell: ({ row }) => (
          <div className='flex items-center gap-0.5'>
            <IconButton size='small' onClick={() => setData(data?.filter(product => product.id !== row.original.id))}>
              <i className='ri-delete-bin-7-line text-textSecondary' />
            </IconButton>
            <IconButton size='small'>
              <Link href={getLocalizedUrl('/apps/user/view', locale as Locale)} className='flex'>
                <i className='ri-eye-line text-textSecondary' />
              </Link>
            </IconButton>
            <OptionMenu
              iconClassName='text-textSecondary'
              options={[
                {
                  text: 'Download',
                  icon: 'ri-download-line',
                  menuItemProps: { className: 'flex items-center' }
                },
                {
                  text: 'Edit',
                  icon: 'ri-edit-box-line',
                  linkProps: { className: 'flex items-center' }
                }
              ]}
            />
          </div>
        ),
        enableSorting: false
      })
    ],
    // eslint-disable-next-line react-hooks/exhaustive-deps
    [data, filteredData]
  )

  const table = useReactTable({
    data: filteredData as UsersType[],
    columns,
    filterFns: {
      fuzzy: fuzzyFilter
    },
    state: {
      rowSelection,
      globalFilter
    },
    initialState: {
      pagination: {
        pageSize: 10
      }
    },
    enableRowSelection: true, //enable row selection for all rows
    // enableRowSelection: row => row.original.age > 18, // or enable row selection conditionally per row
    globalFilterFn: fuzzyFilter,
    onRowSelectionChange: setRowSelection,
    getCoreRowModel: getCoreRowModel(),
    onGlobalFilterChange: setGlobalFilter,
    getFilteredRowModel: getFilteredRowModel(),
    getSortedRowModel: getSortedRowModel(),
    getPaginationRowModel: getPaginationRowModel(),
    getFacetedRowModel: getFacetedRowModel(),
    getFacetedUniqueValues: getFacetedUniqueValues(),
    getFacetedMinMaxValues: getFacetedMinMaxValues()
  })

  const getAvatar = (params: Pick<UsersType, 'avatar' | 'fullName'>) => {
    const { avatar, fullName } = params

    if (avatar) {
      return <CustomAvatar src={avatar} size={34} />
    } else {
      return <CustomAvatar>{getInitials(fullName as string)}</CustomAvatar>
    }
  }

  useEffect(() => {
    const filteredData = data?.filter(user => {
      if (role && user.role !== role) return false

      return true
    })

    setFilteredData(filteredData)
  }, [role, data, setFilteredData])

  return (
    <Card>
      <CardContent className='flex justify-between flex-col items-start sm:flex-row sm:items-center max-sm:gap-4'>
        <Button
          variant='outlined'
          color='secondary'
          startIcon={<i className='ri-upload-2-line' />}
          className='max-sm:is-full'
        >
          Export
        </Button>
        <div className='flex flex-col !items-start max-sm:is-full sm:flex-row sm:items-center gap-4'>
          <DebouncedInput
            value={globalFilter ?? ''}
            className='max-sm:is-full min-is-[220px]'
            onChange={value => setGlobalFilter(String(value))}
            placeholder='Search User'
          />
          <FormControl size='small' className='max-sm:is-full'>
            <InputLabel id='roles-app-role-select-label'>Select Role</InputLabel>
            <Select
              value={role}
              onChange={e => setRole(e.target.value)}
              label='Select Role'
              id='roles-app-role-select'
              labelId='roles-app-role-select-label'
              className='min-is-[150px]'
            >
              <MenuItem value=''>Select Role</MenuItem>
              <MenuItem value='admin'>Admin</MenuItem>
              <MenuItem value='author'>Author</MenuItem>
              <MenuItem value='editor'>Editor</MenuItem>
              <MenuItem value='maintainer'>Maintainer</MenuItem>
              <MenuItem value='subscriber'>Subscriber</MenuItem>
            </Select>
          </FormControl>
        </div>
      </CardContent>
      <div className='overflow-x-auto'>
        <table className={tableStyles.table}>
          <thead>
            {table.getHeaderGroups().map(headerGroup => (
              <tr key={headerGroup.id}>
                {headerGroup.headers.map(header => (
                  <th key={header.id}>
                    {header.isPlaceholder ? null : (
                      <>
                        <div
                          className={classnames({
                            'flex items-center': header.column.getIsSorted(),
                            'cursor-pointer select-none': header.column.getCanSort()
                          })}
                          onClick={header.column.getToggleSortingHandler()}
                        >
                          {flexRender(header.column.columnDef.header, header.getContext())}
                          {{
                            asc: <i className='ri-arrow-up-s-line text-xl' />,
                            desc: <i className='ri-arrow-down-s-line text-xl' />
                          }[header.column.getIsSorted() as 'asc' | 'desc'] ?? null}
                        </div>
                      </>
                    )}
                  </th>
                ))}
              </tr>
            ))}
          </thead>
          {table.getFilteredRowModel().rows.length === 0 ? (
            <tbody>
              <tr>
                <td colSpan={table.getVisibleFlatColumns().length} className='text-center'>
                  No data available
                </td>
              </tr>
            </tbody>
          ) : (
            <tbody>
              {table
                .getRowModel()
                .rows.slice(0, table.getState().pagination.pageSize)
                .map(row => {
                  return (
                    <tr key={row.id} className={classnames({ selected: row.getIsSelected() })}>
                      {row.getVisibleCells().map(cell => (
                        <td key={cell.id}>{flexRender(cell.column.columnDef.cell, cell.getContext())}</td>
                      ))}
                    </tr>
                  )
                })}
            </tbody>
          )}
        </table>
      </div>
      <TablePagination
        rowsPerPageOptions={[10, 25, 50]}
        component='div'
        className='border-bs'
        count={table.getFilteredRowModel().rows.length}
        rowsPerPage={table.getState().pagination.pageSize}
        page={table.getState().pagination.pageIndex}
        SelectProps={{
          inputProps: { 'aria-label': 'rows per page' }
        }}
        onPageChange={(_, page) => {
          table.setPageIndex(page)
        }}
        onRowsPerPageChange={e => table.setPageSize(Number(e.target.value))}
      />
    </Card>
  )
}

