'use client'

// React Imports
import { useEffect, useState } from 'react'

// MUI Imports
import Card from '@mui/material/Card'
import CardContent from '@mui/material/CardContent'
import CardHeader from '@mui/material/CardHeader'
import Grid from '@mui/material/Grid2'
import TextField from '@mui/material/TextField'
import Button from '@mui/material/Button'
import IconButton from '@mui/material/IconButton'
import Typography from '@mui/material/Typography'
import { styled } from '@mui/material/styles'
import type { BoxProps } from '@mui/material/Box'

// Third-party Imports
import { useForm, Controller } from 'react-hook-form'
import { useDropzone } from 'react-dropzone'

// Component Imports
import type { RelatedContentData } from '../ServiceRelated';
import ServiceRelated from '../ServiceRelated'
import AppReactDropzone from '@/libs/styles/AppReactDropzone'


// Styled Dropzone
const Dropzone = styled(AppReactDropzone)<BoxProps>(({ theme }) => ({
    '& .dropzone': {
        minHeight: 'unset',
        padding: theme.spacing(4),
        border: `1px dashed ${theme.palette.divider}`,
        borderRadius: theme.shape.borderRadius,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        textAlign: 'center'
    }
}))

type FormValues = {
    title: string
    shortDescription: string
    image: string

    // Hero Section
    heroTitle: string
    cardSubheading: string
    heroSubheading: string

    // Intro Section
    introTitle: string
    introDescription: string

    // Related
    relatedContent: RelatedContentData
}

export type ServiceCategoryType = {
    id: string
    updatedAt: string
} & FormValues

type Props = {
    isDrawer?: boolean
    handleClose?: () => void
    dataToEdit?: ServiceCategoryType
    onSuccess?: () => void
}

const ServiceCategoryEditor = ({ isDrawer, handleClose, dataToEdit, onSuccess }: Props) => {
    const {
        control,
        handleSubmit,
        reset,
        setValue,
        watch,
        formState: { errors }
    } = useForm<FormValues>({
        defaultValues: {
            title: '',
            shortDescription: '',
            image: '',
            heroTitle: '',
            cardSubheading: '',
            heroSubheading: '',
            introTitle: '',
            introDescription: '',
            relatedContent: {
                sectionTypes: [],
                relatedBlogs: [],
                relatedServices: [],
                relatedParts: [],
                relatedProjects: []
            }
        }
    })

    const relatedContentValue = watch('relatedContent')
    const imageValue = watch('image')

    const [files, setFiles] = useState<File[]>([])

    const { getRootProps, getInputProps } = useDropzone({
        maxFiles: 1,
        accept: {
            'image/*': ['.png', '.jpg', '.jpeg', '.gif', '.webp']
        },
        onDrop: (acceptedFiles: File[]) => {
            const file = acceptedFiles[0]

            if (file) {
                setFiles([file])
                const reader = new FileReader()

                reader.onload = (e) => {
                    if (e.target?.result) {
                        setValue('image', e.target.result as string)
                    }
                }

                reader.readAsDataURL(file)
            }
        }
    })

    useEffect(() => {
        if (dataToEdit) {
            reset({
                title: dataToEdit.title || '',
                shortDescription: dataToEdit.shortDescription || '',
                image: dataToEdit.image || '',
                heroTitle: dataToEdit.heroTitle || '',
                cardSubheading: dataToEdit.cardSubheading || '',
                heroSubheading: dataToEdit.heroSubheading || '',
                introTitle: dataToEdit.introTitle || '',
                introDescription: dataToEdit.introDescription || '',
                relatedContent: dataToEdit.relatedContent || {
                    sectionTypes: [],
                    relatedBlogs: [],
                    relatedServices: [],
                    relatedParts: [],
                    relatedProjects: []
                }
            })
        }
    }, [dataToEdit, reset])

    const onSubmit = (data: FormValues) => {
        const savedCategories = JSON.parse(localStorage.getItem('service-categories') || '[]')
        const timestamp = new Date().toISOString()

        let newCategoryList

        if (dataToEdit) {
            // Editing existing
            newCategoryList = savedCategories.map((cat: ServiceCategoryType) =>
                cat.id === dataToEdit.id ? { ...cat, ...data, updatedAt: timestamp } : cat
            )
        } else {
            // New Category
            const newItem = {
                id: Date.now().toString(),
                ...data,
                updatedAt: timestamp
            }

            newCategoryList = [...savedCategories, newItem]
        }

        localStorage.setItem('service-categories', JSON.stringify(newCategoryList))

        if (onSuccess) onSuccess()
        if (handleClose) handleClose()
    }

    const handleRelatedContentSave = (data: RelatedContentData) => {
        setValue('relatedContent', data)
    }

    return (
        <>
            <form onSubmit={handleSubmit(onSubmit)}>
                <Grid container spacing={6}>
                    <Grid size={{ xs: 12 }}>
                        <Card className={isDrawer ? 'shadow-none border-none' : ''}>
                            {!isDrawer && <CardHeader title='Service Category Editor' />}
                            <CardContent className={isDrawer ? 'p-0' : ''}>
                                <Grid container spacing={5}>
                                    {/* Basic Info */}
                                    <Grid size={{ xs: 12 }}>
                                        <Typography variant='h6' className='mbe-2'>Basic Information</Typography>
                                        <Controller
                                            name='title'
                                            control={control}
                                            rules={{ required: true }}
                                            render={({ field }) => (
                                                <TextField
                                                    {...field}
                                                    fullWidth
                                                    label='Category Title'
                                                    placeholder='e.g. Consulting'
                                                    error={Boolean(errors.title)}
                                                    helperText={errors.title && 'Title is required'}
                                                    className='mbe-4'
                                                />
                                            )}
                                        />
                                        <Controller
                                            name='cardSubheading'
                                            control={control}
                                            render={({ field }) => (
                                                <TextField
                                                    {...field}
                                                    fullWidth
                                                    label='Card Subheading'
                                                    placeholder='Subheading for the card...'
                                                    className='mbe-4'
                                                />
                                            )}
                                        />
                                        <Controller
                                            name='shortDescription'
                                            control={control}
                                            render={({ field }) => (
                                                <TextField
                                                    {...field}
                                                    fullWidth
                                                    multiline
                                                    minRows={2}
                                                    label='Short Description'
                                                    placeholder='Brief summary for listing...'
                                                    className='mbe-4'
                                                />
                                            )}
                                        />

                                        <Typography variant='caption' className='mb-2 block'>Thumbnail Image</Typography>
                                        <Dropzone>
                                            <div {...getRootProps({ className: 'dropzone' })}>
                                                <input {...getInputProps()} />
                                                {imageValue ? (
                                                    <div className="flex flex-col items-center">
                                                        <img
                                                            src={imageValue}
                                                            alt="Preview"
                                                            style={{ maxHeight: 150, maxWidth: '100%', objectFit: 'contain', marginBottom: 8 }}
                                                        />
                                                        <Button variant='outlined' size='small' color='secondary' onClick={(e) => {
                                                            e.stopPropagation()
                                                            setValue('image', '')
                                                            setFiles([])
                                                        }}>
                                                            Remove Image
                                                        </Button>
                                                    </div>
                                                ) : (
                                                    <div className='flex flex-col items-center gap-2'>
                                                        <i className='ri-image-add-line text-2xl' />
                                                        <Typography variant='caption'>Upload Thumbnail Image</Typography>
                                                    </div>
                                                )}
                                            </div>
                                        </Dropzone>
                                    </Grid>

                                    <Grid size={{ xs: 12 }}>
                                        <Typography variant='h6' className='mbe-2'>Hero Section</Typography>
                                        <Controller
                                            name='heroTitle'
                                            control={control}
                                            render={({ field }) => (
                                                <TextField
                                                    {...field}
                                                    fullWidth
                                                    label='Hero Title'
                                                    placeholder='Title for the hero section...'
                                                    className='mbe-4'
                                                />
                                            )}
                                        />

                                        <Controller
                                            name='heroSubheading'
                                            control={control}
                                            render={({ field }) => (
                                                <TextField
                                                    {...field}
                                                    fullWidth
                                                    label='Hero Subheading'
                                                    placeholder='Subheading for the hero section...'
                                                />
                                            )}
                                        />
                                    </Grid>

                                    <Grid size={{ xs: 12 }}>
                                        <Typography variant='h6' className='mbe-2'>Introduction Section</Typography>
                                        <Controller
                                            name='introTitle'
                                            control={control}
                                            render={({ field }) => (
                                                <TextField
                                                    {...field}
                                                    fullWidth
                                                    label='Intro Title'
                                                    placeholder='Title for the introduction...'
                                                    className='mbe-4'
                                                />
                                            )}
                                        />
                                        <Controller
                                            name='introDescription'
                                            control={control}
                                            render={({ field }) => (
                                                <TextField
                                                    {...field}
                                                    fullWidth
                                                    multiline
                                                    minRows={3}
                                                    label='Intro Description'
                                                    placeholder='Detailed description...'
                                                />
                                            )}
                                        />
                                    </Grid>

                                    <Grid size={{ xs: 12 }}>
                                        <Typography variant='h6' className='mbe-2'>Related Content</Typography>
                                        <ServiceRelated serviceData={relatedContentValue} onSave={handleRelatedContentSave} />
                                    </Grid>
                                </Grid>
                            </CardContent>
                        </Card>
                    </Grid>

                    <Grid size={{ xs: 12 }} className='flex justify-end pt-5'>
                        <Button variant='contained' size='large' type='submit'>
                            Save Category
                        </Button>
                    </Grid>
                </Grid>
            </form>
        </>
    )
}

export default ServiceCategoryEditor

'use client'

// React Imports
import { useState, useEffect } from 'react'

// MUI Imports
import Card from '@mui/material/Card'
import CardHeader from '@mui/material/CardHeader'
import Button from '@mui/material/Button'
import Typography from '@mui/material/Typography'
import IconButton from '@mui/material/IconButton'
import Drawer from '@mui/material/Drawer'
import Table from '@mui/material/Table'
import TableBody from '@mui/material/TableBody'
import TableCell from '@mui/material/TableCell'
import TableContainer from '@mui/material/TableContainer'
import TableHead from '@mui/material/TableHead'
import TableRow from '@mui/material/TableRow'
import TablePagination from '@mui/material/TablePagination'
import TextField from '@mui/material/TextField'
import InputAdornment from '@mui/material/InputAdornment'
import Chip from '@mui/material/Chip'

// Type Imports
import { number } from 'valibot'

import type { ServiceCategoryType } from './ServiceCategoryEditor'

// Component Imports
import ServiceCategoryEditor from './ServiceCategoryEditor'
import type { ServiceItemType } from '../service/ServicePageEditor';
import ServicePageEditor from '../service/ServicePageEditor'
import ServiceMappingDialog from './ServiceMappingDialog'
import ServiceListDialog from './ServiceListDialog'

// Row Component
type RowProps = {
    row: ServiceCategoryType
    serviceCount: number
    handleEdit: (item: ServiceCategoryType) => void
    handleDelete: (id: string) => void
    handleMapServices: (category: ServiceCategoryType) => void
}

const Row = ({
    row,
    serviceCount,
    handleEdit,
    handleDelete,
    handleMapServices
}: RowProps) => {
    return (
        <TableRow sx={{ '& > *': { borderBottom: 'unset' } }}>
            <TableCell>
                <div className='flex items-center gap-2'>
                    <div>
                        <Typography variant='body1' className='font-medium'>{row.title}</Typography>
                        {row.shortDescription && (
                            <Typography variant='caption' color='text.secondary' className='truncate block max-w-xs'>
                                {row.shortDescription}
                            </Typography>
                        )}
                    </div>
                </div>
            </TableCell>
            <TableCell>
                {row.image && <img src={row.image} alt={row.title} className="h-8 w-8 object-contain" />}
            </TableCell>
            <TableCell>
                <Chip label={`${serviceCount} Services`} size='small' color='primary' variant='outlined' />
            </TableCell>
            <TableCell>
                <div className='flex gap-2'>
                    <IconButton size='small' onClick={() => handleEdit(row)} title="Edit Category">
                        <i className='ri-pencil-line' />
                    </IconButton>
                    <IconButton size='small' color='primary' onClick={() => handleMapServices(row)} title="Map Services">
                        <i className='ri-list-check' />
                    </IconButton>
                    <IconButton size='small' color='error' onClick={() => handleDelete(row.id)} title="Delete Category">
                        <i className='ri-delete-bin-line' />
                    </IconButton>
                </div>
            </TableCell>
        </TableRow>
    )
}

const ServiceCategoryList = () => {
    // States
    const [data, setData] = useState<ServiceCategoryType[]>([])
    const [filteredData, setFilteredData] = useState<ServiceCategoryType[]>([])
    const [searchTerm, setSearchTerm] = useState('')
    const [page, setPage] = useState(0)
    const [rowsPerPage, setRowsPerPage] = useState(10)

    // Service Stats
    const [categoryServiceCounts, setCategoryServiceCounts] = useState<Record<string, number>>({})

    // View State
    const [viewMode, setViewMode] = useState<'list' | 'service-editor'>('list')
    const [serviceToEdit, setServiceToEdit] = useState<ServiceItemType | undefined>(undefined)

    // Modals
    const [selectedCategory, setSelectedCategory] = useState<ServiceCategoryType | undefined>(undefined)

    // Service List Modal
    const [serviceListOpen, setServiceListOpen] = useState(false)

    // Mapping Dialog State
    const [mappingDialogOpen, setMappingDialogOpen] = useState(false)
    const [categoryToMap, setCategoryToMap] = useState<ServiceCategoryType | undefined>(undefined)

    // Drawer States
    const [editorOpen, setEditorOpen] = useState(false)
    const [editingNode, setEditingNode] = useState<ServiceCategoryType | undefined>(undefined)

    // Load data
    const loadData = () => {
        // Categories
        const savedData = localStorage.getItem('service-categories')

        if (savedData) {
            const parsedData = JSON.parse(savedData)

            setData(parsedData)
            setFilteredData(parsedData)
        }

        // Services & Counts
        const savedServices = JSON.parse(localStorage.getItem('category-services') || '[]') as ServiceItemType[]
        const counts: Record<string, number> = {}

        savedServices.forEach(s => {
            if (s.categoryId) {
                counts[s.categoryId] = (counts[s.categoryId] || 0) + 1
            }
        })
        setCategoryServiceCounts(counts)
    }

    useEffect(() => {
        loadData()
    }, [viewMode])

    // Filter
    useEffect(() => {
        const result = data.filter(item =>
            item.title.toLowerCase().includes(searchTerm.toLowerCase())
        )

        setFilteredData(result)
        setPage(0)
    }, [searchTerm, data])

    const handleChangePage = (event: unknown, newPage: number) => setPage(newPage)

    const handleChangeRowsPerPage = (event: React.ChangeEvent<HTMLInputElement>) => {
        setRowsPerPage(+event.target.value)
        setPage(0)
    }

    // Actions
    const handleAddNew = () => {
        setEditingNode(undefined)
        setEditorOpen(true)
    }

    const handleCreateService = () => {
        setServiceToEdit(undefined)
        setSelectedCategory(undefined) // Independent service initially
        setViewMode('service-editor')
    }

    const handleEditFromList = (service: ServiceItemType) => {
        setServiceListOpen(false)
        setServiceToEdit(service)
        setSelectedCategory(undefined) // We might want to find the category, but for editing it doesn't strictly matter for initial props, but let's just leave it undefined or null, the editor handles it via dataToEdit
        setViewMode('service-editor')
    }

    const handleEdit = (item: ServiceCategoryType) => {
        setEditingNode(item)
        setEditorOpen(true)
    }

    const handleMapServices = (category: ServiceCategoryType) => {
        setCategoryToMap(category)
        setMappingDialogOpen(true)
    }

    const handleMappingSave = () => {
        loadData()
        setMappingDialogOpen(false)
    }

    const handleServiceSave = (service: ServiceItemType) => {
        const allServices = JSON.parse(localStorage.getItem('category-services') || '[]') as ServiceItemType[]

        const otherServices = allServices.filter(s => s.id !== service.id)
        const updatedAllServices = [...otherServices, service]

        localStorage.setItem('category-services', JSON.stringify(updatedAllServices))

        setViewMode('list')
        setServiceToEdit(undefined)
    }

    const handleDelete = (id: string) => {
        if (confirm('Are you sure you want to delete this category?')) {
            const newData = data.filter(cat => cat.id !== id)

            localStorage.setItem('service-categories', JSON.stringify(newData))
            loadData()
        }
    }

    const handleEditorSuccess = () => {
        loadData()
        setEditorOpen(false)
    }

    if (viewMode === 'service-editor') {
        return (
            <ServicePageEditor
                categoryId={serviceToEdit?.categoryId || (selectedCategory?.id || '')}
                dataToEdit={serviceToEdit}
                onSave={handleServiceSave}
                onCancel={() => {
                    setViewMode('list')

                    // if (selectedCategory) setAssignmentModalOpen(true)
                }}
            />
        )
    }

    return (
        <Card>
            <CardHeader
                title='Service Categories'
                action={
                    <div className='flex gap-4 items-center'>
                        <TextField
                            size='small'
                            placeholder='Search Categories'
                            value={searchTerm}
                            onChange={e => setSearchTerm(e.target.value)}
                            InputProps={{
                                startAdornment: (
                                    <InputAdornment position='start'>
                                        <i className='ri-search-line' />
                                    </InputAdornment>
                                )
                            }}
                        />
                        <Button variant='outlined' startIcon={<i className='ri-list-check' />} onClick={() => setServiceListOpen(true)}>
                            List of Services
                        </Button>
                        <Button variant='outlined' startIcon={<i className='ri-add-line' />} onClick={handleCreateService}>
                            Create Service
                        </Button>
                        <Button variant='contained' startIcon={<i className='ri-add-line' />} onClick={handleAddNew}>
                            Add Category
                        </Button>
                    </div>
                }
            />
            <TableContainer>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell>Title & Description</TableCell>
                            <TableCell>Image</TableCell>
                            <TableCell>Info</TableCell>
                            <TableCell>Actions</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {filteredData.length === 0 ? (
                            <TableRow>
                                <TableCell colSpan={4} align='center'>
                                    <Typography>No categories found</Typography>
                                </TableCell>
                            </TableRow>
                        ) : (
                            filteredData
                                .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                                .map((row) => (
                                    <Row
                                        key={row.id}
                                        row={row}
                                        serviceCount={categoryServiceCounts[row.id] || 0}
                                        handleEdit={handleEdit}
                                        handleDelete={handleDelete}
                                        handleMapServices={handleMapServices}
                                    />
                                ))
                        )}
                    </TableBody>
                </Table>
            </TableContainer>
            <TablePagination
                rowsPerPageOptions={[10, 25, 50]}
                component='div'
                count={filteredData.length}
                rowsPerPage={rowsPerPage}
                page={page}
                onPageChange={handleChangePage}
                onRowsPerPageChange={handleChangeRowsPerPage}
            />

            <Drawer
                open={editorOpen}
                anchor='right'
                onClose={() => setEditorOpen(false)}
                sx={{ '& .MuiDrawer-paper': { width: { xs: '100%', md: '500px' } } }}
            >
                <div className='flex items-center justify-between pli-5 plb-4 border-b'>
                    <Typography variant='h5'>
                        {editingNode ? 'Edit Category' : 'Add New Category'}
                    </Typography>
                    <IconButton size='small' onClick={() => setEditorOpen(false)}>
                        <i className='ri-close-line text-2xl' />
                    </IconButton>
                </div>
                <div className='p-5 overflow-y-auto'>
                    <ServiceCategoryEditor
                        isDrawer
                        handleClose={() => setEditorOpen(false)}
                        onSuccess={handleEditorSuccess}
                        dataToEdit={editingNode}
                    />
                </div>
            </Drawer>

            {categoryToMap && (
                <ServiceMappingDialog
                    open={mappingDialogOpen}
                    onClose={() => setMappingDialogOpen(false)}
                    categoryId={categoryToMap.id}
                    onSave={handleMappingSave}
                />
            )}

            <ServiceListDialog
                open={serviceListOpen}
                onClose={() => {
                    setServiceListOpen(false)
                    loadData() // Refresh counts if deletions happened
                }}
                onEdit={handleEditFromList}
            />

        </Card>
    )
}

export default ServiceCategoryList

'use client'

// React Imports
import { useState } from 'react'
import type { SyntheticEvent, ReactElement } from 'react'

// MUI Imports

import TabContext from '@mui/lab/TabContext'
import TabPanel from '@mui/lab/TabPanel'
import Grid from '@mui/material/Grid2'

// Component Imports


const UserRight = ({ tabContentList }: { tabContentList: { [key: string]: ReactElement } }) => {
  // States
  const [activeTab, setActiveTab] = useState('overview')



  return (
    <>
      <TabContext value={activeTab}>
        <Grid container spacing={6}>

          <Grid size={{ xs: 12 }}>
            <TabPanel value={activeTab} className='p-0'>
              {tabContentList[activeTab]}
            </TabPanel>
          </Grid>
        </Grid>
      </TabContext>
    </>
  )
}

export default UserRight
